style.css:
@-moz-document domain("boulanger.com") {
/* Masquer l'image de fond qui est visuellement agressive */
body,
body.bg4xsf {
    background-image: none !important;
    background: white !important;
}

/* Masquage des éléments pénibles et distrayants */
#ems-or-not, /* bloc de suggestion publicitaire semble-t-il */
#tab-complementary-product, /* encore un bloc de suggestion */
#caracteristiques h3, /* Encart "POUR VOUS AIDER À MIEUX CHOISIR" */
#help-me, /* Les gros boutons de l'encart "POUR VOUS AIDER À MIEUX CHOISIR" */
div.bottom h4, /* Encart "VOUS AIMEREZ PEUT Être AUSSI..." */
#likely, /* bloc de suggestions pour l'encart "VOUS AIMEREZ PEUT Être AUSSI..." */
.more.opinion, /* Lien "que pensez-vous de la sélection ?" */
#newsletterFooter, /* Inscription à la newsletter */
.ftr__middle, /* Boutons rézosocios / appli smartphone */
.opcom, [data-opcom-id] /* Une foultitude de blocs inutiles  */
{
    display: none !important;
}

}
