style.css:
@-moz-document regexp('.*docs.oracle.com\/javase\/.*\/javafx\/api\/javafx\/scene\/doc-files\/.*') {
body {
  max-width: 1000px;
  font-size: 11pt;
  margin-left:350px;
  line-height: 1.5em;
  overflow-x: hidden;
  overflow-y: scroll;
}
body > .contents {
  position: fixed;
  top: 0;
  left: 0;
  bottom: 0;
  margin: 0;
  width: 300px;
  list-style:inside;
  padding:10px 15px;
  line-height: 1.2em;
  overflow-y: scroll;
  background: #f7f8f9;
}
}
