style.css:
@-moz-document domain("github.com") {
/*
 * GitHub Win95 - JuanPotato
 * https://github.com/JuanPotato/GitHub-Win95
 *
 * v0.0.5
 *
 */


/* Colors */
:root {
    --bg-color: #d4d0c8;
    --light-bg-color: #ebe9e2;
    --border-light-color: #fff;
    --border-dark-color: #606060;
}


/* Header Fade */
.Header {
    background: linear-gradient(to right, #0a246a, #a6caf0);
}

/* Not logged in header padding */
.HeaderMenu--logged-out .header-search-input,
.header-logged-out .py-3 {
    padding-top: 0px !important;
    padding-bottom: 0px !important;
}

.Header {
    padding-bottom: 12px !important;
    padding-top: 12px !important;
}

/* Issue Title Background */
.gh-header {
    background-color: var(--bg-color)
}

.gh-header-number {
    color: #666;
}

/* Body Backgrounds */
.repohead.experiment-repo-nav,
body,
.thread-subscription-status,
.discussion-timeline-actions,
.new-discussion-timeline .closed-banner,
.bg-white {
    background: var(--bg-color) !important;
}

/* Discussion background issues */
.new-discussion-timeline .closed-banner {
    border-bottom: 0;
    display: none;
}

.discussion-timeline-actions {
    border-top: 0;
}

/* Discussion icons Closed, Reopped, stop signs and checkmarks in circles */
.discussion-item-icon {
    border-color: var(--bg-color);
}

/* Rows for issues and PR */
.Box-row {
    background: #fff;
}

.Box-row--focus-gray.navigation-focus {
    background-color: #f6f8fa;
}

/* Lines */
.border-left {
    border-left: 1px solid var(--border-dark-color) !important;
}

.profile-rollup-wrapper+.profile-rollup-wrapper {
    border-top: 1px solid var(--border-dark-color) !important; 
}

.profile-timeline.discussion-timeline:before,
.profile-timeline.discussion-timeline .profile-timeline-month-heading:after {
    background-color: var(--border-dark-color) !important;
}

/* Timeline Cards */
.profile-timeline-card-wrapper {
    border-top: none !important;
    border-left: none !important;
    border-bottom: none !important;
    border-right: none !important;
}

.profile-timeline-card {
    border-radius: 0 !important;
    background: var(--light-bg-color) !important;
    border-top: 2px solid var(--border-dark-color) !important;
    border-left: 2px solid var(--border-dark-color) !important;
    border-bottom: 1px solid var(--border-light-color) !important;
    border-right: 1px solid var(--border-light-color) !important;
}

/* Pinned Repositories */
.pinned-repo-item,
/* Contribution Graph */
.js-contribution-graph > .rounded-1,
.graph-before-activity-overview,
.activity-overview-box {
    border-radius: 0 !important;
    background: var(--light-bg-color);
    border-top: 2px solid var(--border-dark-color) !important;
    border-left: 2px solid var(--border-dark-color) !important;
    border-bottom: 1px solid var(--border-light-color) !important;
    border-right: 1px solid var(--border-light-color) !important;
}

.graph-before-activity-overview {
    margin-bottom: 10px;
}

/* Contribution dots */
:root {
    --contrib-0: #d6d9df;
    --contrib-1: #93b7df;
    --contrib-2: #5683b6;
    --contrib-3: #3851a2;
    --contrib-4: #0a246a;
}


/* Contribution lvl 0 */
rect[fill="#ebedf0"] {
    fill: var(--contrib-0);
}
.contrib-footer .legend li:nth-child(1),
[style~="#ebedf0"] {
    background-color: var(--contrib-0) !important;
}


/* Contribution lvl 1 */
rect[fill="#c6e48b"] {
    fill: var(--contrib-1);
}
.contrib-footer .legend li:nth-child(2),
[style~="#c6e48b"] {
    background-color: var(--contrib-1) !important;
}
stop[stop-color="#c6e48b"] {
    stop-color: var(--contrib-1);
}


/* Contribution lvl 2 */
rect[fill="#7bc96f"] {
    fill: var(--contrib-2);
}
.contrib-footer .legend li:nth-child(3),
[style~="#7bc96f"] {
    background-color: var(--contrib-2) !important;
}
stop[stop-color="#7bc96f"] {
    stop-color: var(--contrib-2);
}

/* Contribution lvl 3 */
rect[fill="#239a3b"] {
    fill: var(--contrib-3);
}
.contrib-footer .legend li:nth-child(4),
[style~="#239a3b"] {
    background-color: var(--contrib-3) !important;
}
stop[stop-color="#239a3b"] {
    stop-color: var(--contrib-3);
}


/* Contribution lvl 4 */
rect[fill="#196127"] {
    fill: var(--contrib-4);
}
.contrib-footer .legend li:nth-child(5),
[style~="#196127"] {
    background-color: var(--contrib-4) !important;
}
stop[stop-color="#196127"] {
    stop-color: var(--contrib-4);
}



/* Basic button styling */
.btn,
.btn:hover,
ul.pagehead-actions > li,
ul.pagehead-actions > li:hover,
.subnav-item, /* Labels and Milestone buttons */
.subnav-item:hover,
.ajax-pagination-btn,
.ajax-pagination-btn:hover {
    color: #000 !important;
    box-shadow: none !important;
    background: var(--bg-color) !important;
    border-radius: 0 !important;
    border-top: 1px solid var(--border-light-color) !important;
    border-left: 1px solid var(--border-light-color) !important;
    border-bottom: 2px solid var(--border-dark-color) !important;
    border-right: 2px solid var(--border-dark-color) !important;
}

.btn:active,
.btn.selected,
[open]>.btn,
ul.pagehead-actions > li:active,
.subnav-item:active,
.ajax-pagination-btn:active {
    color: #000 !important;
    box-shadow: none !important;
    background: var(--bg-color) !important;
    border-radius: 0 !important;
    border-top: 2px solid var(--border-dark-color) !important;
    border-left: 2px solid var(--border-dark-color) !important;
    border-bottom: 1px solid var(--border-light-color) !important;
    border-right: 1px solid var(--border-light-color) !important;
}

.btn:focus,
.btn:focus-within,
ul.pagehead-actions li:focus,
ul.pagehead-actions li:focus-within,
.branch-select-menu:focus,
.branch-select-menu:focus-within,
.subnav-item:focus,
.subnav-item:focus-within,
.ajax-pagination-btn:focus,
.ajax-pagination-btn:focus-within {
    box-shadow: none !important;
    outline: 1px solid black !important;
}


/* buttons within buttons in the Fork Star Issues buttons */
ul.pagehead-actions > li .btn,
ul.pagehead-actions > li .btn:hover,
ul.pagehead-actions > li .btn:active,
ul.pagehead-actions > li .btn:focus,
ul.pagehead-actions > li .btn:focus-within {
    background: none !important;
    border-top: none !important;
    border-left: none !important;
    border-bottom: none !important;
    border-right: none !important;
    outline: none !important;
}

ul.pagehead-actions .btn:focus,
ul.pagehead-actions .social-count:focus {
    box-shadow: none;
	outline: none;
}

.subnav-item {
    margin-right: 5px;
}

/* Danger buttons Aaaaaaaaaaaaaaaaaaaaaaaaah */
.btn-danger,
.btn-danger:hover,
.btn-danger:active,
.btn-danger:focus,
.btn-danger:focus-within {
    color: #f00 !important;
}

/* Tags */
.topic-tag, .topic-tag:hover {
    background: var(--light-bg-color);
    color: #000;
    border-radius: 0;
    border-top: 2px solid var(--border-dark-color);
    border-left: 2px solid var(--border-dark-color);
    border-bottom: 1px solid var(--border-light-color);
    border-right: 1px solid var(--border-light-color);
}

/* Separate the New file, Upload file, and Find file buttons */
.BtnGroup-item,
.file-navigation .get-repo-select-menu {
    margin-left: 5px;
}


/* Watch, Star, and Fork buttons */
ul.pagehead-actions .btn,
ul.pagehead-actions .social-count {
    border-radius: 0;
    border: none;
    background: none;
}

ul.pagehead-actions .social-count,
ul.pagehead-actions .social-count:hover {
    color: #000;
}



/* Getting rid of lines above and below the comments in issues */
.timeline-comment-wrapper {
    border-top: none;
    border-bottom: none;
}


/* Summary and code lang graph */
.overall-summary {
    background: #fff;
    border-radius: 0;
    border-bottom: 1px solid var(--border-light-color);
    border-right: 1px solid var(--border-light-color);
    border-top: 2px solid var(--border-dark-color);
    border-left: 2px solid var(--border-dark-color);
}

.overall-summary-bottomless {
    border-bottom: none;
}

.repository-lang-stats-graph {
    border-radius: 0;
    border-bottom: 1px solid var(--border-light-color);
    border-right: 1px solid var(--border-light-color);
    border-left: 2px solid var(--border-dark-color);
}

.repository-lang-stats-graph > .language-color {
    border-radius: 0 !important;
}


/* Project tabs */
.repohead.experiment-repo-nav {
    -webkit-box-shadow: inset 0px -1px 0px 0px #fff;
    -moz-box-shadow: inset 0px -1px 0px 0px #fff;
    box-shadow: inset 0px -1px 0px 0px #fff;
    border-bottom: 1px solid #fff;
}

.reponav-item.selected {
    padding-bottom: 8px;
    transform: none;
    background: var(--bg-color);
    border-top: 2px solid var(--border-light-color);
    border-left: 2px solid var(--border-light-color);
    border-right: 2px solid var(--border-dark-color);
}

.reponav-item {
    border-radius: 5px 5px 0 0;
    padding-bottom: 0px;
    transform: translateY(7px);
    border-top: 2px solid var(--border-light-color);
    border-left: 1px solid var(--border-light-color);
    border-right: 2px solid var(--border-dark-color);
    padding-top: 2px;
}


/* Editor */
.file {
    border: none;
    border-radius: 0;
}

.file .file-header {
    margin-bottom: 10px;
    background: var(--bg-color);
    border-top: 1px solid var(--border-light-color);
    border-left: 1px solid var(--border-light-color);
    border-right: 2px solid var(--border-dark-color);
    border-bottom: 2px solid var(--border-dark-color);
}

/* Any white thing */
.file .data,
.blankslate {
    border-radius: 0 !important;
    background: #fff !important;
    border-top: 2px solid var(--border-dark-color) !important;
    border-left: 2px solid var(--border-dark-color) !important;
    border-bottom: 1px solid var(--border-light-color) !important;
    border-right: 1px solid var(--border-light-color) !important;
}

div.watch_started > .body > div > div > div.Box,
div.public        > .body > div > div > div:nth-child(2),
div.follow        > .body > div > div > div.Box,
div.fork          > .body > div > div > div:nth-child(2),
div.repo          > .body > div > div > div.Box,
div.push          > .body > div > div > div > div.Box,
div.push          > .body > div > div > div > div > div.Box {
    border-radius: 0 !important;
    background: var(--light-bg-color) !important;
    border-top: 2px solid var(--border-dark-color) !important;
    border-left: 2px solid var(--border-dark-color) !important;
    border-bottom: 1px solid var(--border-light-color) !important;
    border-right: 1px solid var(--border-light-color) !important;
}

.subnav-search-input {
    border-radius: 0;
    background: #fff;
    border-top: 2px solid var(--border-dark-color);
    border-left: 1px solid var(--border-dark-color);
    border-bottom: 1px solid var(--border-light-color);
    border-right: 1px solid var(--border-light-color);
}


