}
.qr-alternative {
  color: #d9e1e8;
}
.simple_form .warning,
.table-form .warning {
  background: rgba(223, 64, 90, 0.5);
  color: #ffffff;
  text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.3);
