}
.simple_form .warning a,
  .table-form .warning a {
    color: #ffffff;
}
.post-follow-actions {
  color: #9baec8;
}
.alternative-login h4 {
    color: #ffffff;
}
.scope-danger {
  color: #ff5050;
}







.pagination a,
  .pagination .current,
  .pagination .newer,
  .pagination .older,
  .pagination .page,
  .pagination .gap {
    color: #ffffff;
}
.pagination .current {
    background: #ffffff;
    color: #705480;
}
.pagination .older,
  .pagination .newer {
    color: #AE8CD1;
}
.pagination .disabled {
    color: #705480;
