style.css:
@-moz-document 
url-prefix('http://www5.9anime.is/watch'),
url-prefix('https://www5.9anime.is/watch'),
url-prefix('http://www5.9anime.is/watch'),
url-prefix('https://www5.9anime.is/watch'),
url-prefix('https://9anime.is/watch'),
url-prefix('http://9anime.is/watch'),
url-prefix('http://www5.9anime.to/watch'),
url-prefix('https://www5.9anime.to/watch'),
url-prefix('http://www5.9anime.to/watch'),
url-prefix('https://www5.9anime.to/watch'),
url-prefix('https://9anime.to/watch'),
url-prefix('http://9anime.to/watch'),
url-prefix('http://www6.9anime.is/watch'),
url-prefix('https://www6.9anime.is/watch'),
url-prefix('http://www8.9anime.is/watch'),
url-prefix('https://www8.9anime.is/watch')
{
body > div:not(#wrapper) {
    display: none;
}
#body-container{
	width: 100%;
    max-width: 100%;
}
#sidebar{
    display: none;
}
#main{
    padding-right: 0 !important;
}
#player{
	height: 102vh !important;
}
#main > .content:first-child{
	/*padding-top: 107vh;*/
}
#main > .content:first-child > div:first-child{
	display: none;
}
.widget-player{
	position: absolute !important; 
    top: 0px !important; 
    overflow: hidden !important; 
    width: 100% !important;
}
div.resize.control{
	display: none;
}
.film-list .item{
    width: auto !important;
}
.film-list .item img{
    width: auto !important;
}
}
