style.css:
@-moz-document url-prefix("https://klasa.js.org/") {
@font-face {
    font-family: Fira Code;
    src: url(https://cdn.rawgit.com/tonsky/FiraCode/1.205/distr/fira_code.css);
}
* {
    transition-duration: 0.2s;
}
body {
    background-color: #212121;
    color: #E0E0E0;
}
li {
    color: #E0E0E0;
    background-color: transparent;
}
.is-pulled-right {
    background-color: #212121;
    border-radius: 100px;
    padding: 5px 9px;
    cursor: pointer;
}
.is-pulled-right a {
    color: #E0E0E0;
}
.nav {
    background-color: #01579B;
}
.footer {
    background-color: #263238;
}
.footer p {
    color: #CFD8DC;
}
.footer strong {
    color: #E0E0E0;
}
.card {
    background-color: #2d2d2d;
    border-radius: 5px;
}
.menu-label {
    color: #E0E0E0;
}
.animated-list-item fa {
    color: #CFD8DC;
}
.tabs a {
    color: #78909C;
}
.tabs a:hover {
    color: #B0BEC5;
}
.card-header {
    border-radius: 10px;
    color: #E0E0E0;
    ;
    background-color: #209cee;
}
.card-header-title {
    color: #E0E0E0;
    ;
}
.card-header-title a {
    color: #B3E5FC;
}
.is-pulled-right {
    background-color: #209cee;
}
.is-pulled-right fa {
    color: #E0E0E0;
    ;
}
.card-content p {
    color: #CFD8DC;
}
.card-content strong {
    color: #CFD8DC;
}
.card-footer-item strong {
    color: #CFD8DC;
}
.title {
    color: #E0E0E0;
    ;
}
.animated-list-item a {
    color: #CFD8DC;
}
th {
    background-color: #0288D1;
}
.th-wrap {
    color: #E0E0E0;
    ;
}
pre {
    background-color: #2d2d2d;
}
.table {
    margin-top: 10px;
}
.box {
    border-radius: 5px;
    color: #E0E0E0;
    ;
    background-color: #2d2d2d;
}
.box strong {
    color: #E0E0E0;
    ;
}
.notification.is-light {
    color: #E0E0E0;
    ;
    background-color: #455A64;
}
.content h2,
.content h3,
.content h1,
.content:not(blockquote) strong {
    color: #E0E0E0;
}
.content blockquote {
    background-color: #CFD8DC;
    border-radius: 5px;
    color: #2d2d2d;
}
.content blockquote strong,
blockquote li {
    color: #2d2d2d;
}
td strong,
.card {
    color: #E0E0E0;
}
.select select,
.input.is-light {
    background-color: #CFD8DC;
}
code {
    border-radius: 5px;
    font-family: Fira Code;
    font-variant-ligatures: contextual;
    background-color: #B0BEC5;
    color: #b71c1c;
}
.content table thead td,
.content table thead th {
    color: #E0E0E0;
    ;
}
}
