style.css:
@-moz-document url-prefix("https://www.ebay-kleinanzeigen.de/"), url-prefix("https://www.ebay-kleinanzeigen.de/")
{
/*  =====================================
    Whatsapp Dark-Green Monster by Nico
    by Nico
    https://userstyles.org/styles/161984/
    Last updated: v2.0: 09/04/2020
    =====================================   */



body {
	color: #535353;
	background-color: #000;
}

#site-header {
	background: black;
}
.a-horizontal-padded.l-container, .and-horizontal-padded.l-container {
	background-color: #000;
}
body {
	color: green;
}
.listitem-container > header {
	border-bottom: 1px solid #000;
}
.l-col {
	background-color: #000;
}
.mainnav-item.on, .mainnav-item:hover {
	background-color: rgba(107, 107, 107, 0);
	color: GREEN;
}
.mainnav-my.on > .mainnav-item-inner, .mainnav-my:hover > .mainnav-item-inner {
	background-image: url(data:image/png;base64,);
	color: green;
}
.mainnav-my > .mainnav-item-inner {
	background-image: url(data:image/png;base64,);
	color: green;
}
.mainnav-item-inner {
	border-left: 1px solid #000;
}
.mainnav-postad.on > .mainnav-item-inner, .mainnav-postad:hover > .mainnav-item-inner {
	background-image: url(data:image/png;base64,);
	color: red;
}
.mainnav-postad > .mainnav-item-inner {
	background-size: 33px 23px;
	-moz-background-size: 33px 23px;
	-webkit-background-size: 33px 23px;
	-o-background-size: 33px 23px;
	background-image: url(data:image/png;base64,);
	padding-left: 50px;
	color: green;
}
.a-green.bar {
	background-color: #000;
}

.a-vertical-padded.l-container, .and-vertical-padded.l-container {
	background-color: #000;
}

.contentbox, .contentbox-unpadded, .contentbox-info, .contentbox-landingpage, .info-contentbox {
	background: #000;
}

.headline, .headline-huge, .headline-big, .headline-large, .headline-mediumlarge, .headline-medium, .headline-small, .sectionheadline, .sectionheadline-underlined, .boxheader > h1, .boxheader-nomarginbottom > h1, .boxheader > h2, .boxheader-nomarginbottom > h2, .boxheader > h3, .boxheader-nomarginbottom > h3, .boxheader > h4, .boxheader-nomarginbottom > h4, .boxheader > h5, .boxheader-nomarginbottom > h5, .boxheader > h6, .boxheader-nomarginbottom > h6, .icon-headline, .icon-headline-small {
	font-weight: 500;
	color: green;
}
.formcontrol, textarea, select, .splitfield-dropdown, .recaptchatable input[type="text"] {
	height: 32px;
	box-shadow: none;
	outline: none;
	font-size: 12px;
	border: 1px solid #cbcbcb;
	background-color: #000;
	color: green;
	width: 100%;
	display: block;
	padding: 6px 12px;
	border-radius: 3px;
	box-sizing: border-box;
}
Element {
	background-color: #000;
}
.replybox {
	padding: 12px;
	border-top: 1px solid #000;
	background-color: #000;
}
.height-full {
	height: 100%;
	background-color: #000;
}
.conversationitem {
	display: table;
	width: 100%;
	background-color: #000;
	padding: 12px 12px 12px 0;
	border-left: 5px solid transparent;
	border-right: 1px solid transparent;
	box-sizing: border-box;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}

.conversationitem:hover, .conversationitem.is-active {
	background-color: #000;
	border-left-color: green;

/* Update 01.07.2018 */

	border-right: 1px solid green; 

/* Update End */

	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}

#msgbox-conversation-overview .itemlist > li + li, #msgbox-conversation-overview .itemlist-separatedbefore > li + li, #msgbox-conversation-overview .itemlist-padded-separatedbefore > li + li, #msgbox-conversation-overview .itemlist-separatedafter > li + li, #msgbox-conversation-overview .itemlist-padded-separatedafter > li + li, #msgbox-conversation-overview .itemlist-padded > li + li {
	border-top: 1px solid #000;
}
.msgbox-to {
	width: 90%;
	background-color: green;
	color: #fff;
	margin-right: 0px;
	border-radius: 15px;
	margin-left: auto;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
.msgbox-from {
	width: 90%;
	background-color: #242424;
	border-radius: 15px;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
input.splitfield-input {
	background-color: #000;
	text-overflow: ellipsis;
	padding: 7px 0px;
	height: auto;
	margin: 0;
	border: none;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	width: 100%;
	min-height: 32px;
	display: inline-block;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
	border-radius: 100px;
	text-align: center;
}
.splitfield-left {
	display: table-cell;
	vertical-align: middle;
	width: 55%;
	border-radius: 0px;
	background-color: #000;
}
.splitfield-right {
	background-color: #000;
	border-radius: 0px;
	border: solid #000;
	border-width: 0 0 0 1px;
	display: table-cell;
	vertical-align: middle;
	width: 45%;
}
.attributelist-striped .attributelist--key:nth-of-type(2n+1), .attributelist-striped .attributelist--value:nth-of-type(2n+1) {
	background-color: #242424;
}
.ad-gallery .ad-image-wrapper > .ad-image > img, .ad-gallery-horizontal .ad-image-wrapper > .ad-image > img {
	border-radius: 15px;
}
.imagebox, .imagebox-small, .imagebox-storelogo-mini, .imagebox-thumbnail, .imagebox-storelogo {
	border-radius: 15px;
}
fieldset, img {
	border-radius: 15px;
}
fieldset {
	border: 1px solid #000;
}
.imagebox-thumbnail {
	width: 70px;
	height: 70px;
	line-height: 65px;
}
.ad-gallery-horizontal--imagecontainer {
	border-radius: 15px;
}

.mainnav-sub {
	background-color: #000;
	white-space: nowrap;
	text-align: left;
	border: solid #87B919;
	border-width: 0 1px 1px;
	border-radius: 0 0 3px 3px;
	padding: 10px 10px 0px 10px;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
.on .mainnav-item-inner, .mainnav-item:hover .mainnav-item-inner, .mainnav-sub-link {
	color: green;
	text-decoration: none;
}
hr, .separator {
	margin: 0;
	border: solid #000;
	border-width: 1px 0 0 0;
}
.dropdown-list > li {
	padding: 5px 5px 5px 20px;
	color: green;
	cursor: pointer;
	white-space: nowrap;
}
.dropdown-list {
	border: 1px solid #000;
	background-color: #000;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
.dropdown-list::after {
	border-color: green;
	background-color: transparent;
}
.dropdown-list > li.is-active, .dropdown-list > li:hover {
	background-color: transparent;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
.category-selection-list {
box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
.category-selection-list-item {
	font-size: 13px;
	border-bottom: 1px solid green;
	cursor: pointer;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
	color: green;
}
.category-selection-list-item:hover, .category-selection-list-item.is-active {
	background-color: transparent;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
.category-selection-list-item:first-of-type {
	border-top: 1px solid transparent;
}
.category-selection-list-item:hover a, .category-selection-list-item.is-active a {
	text-decoration: none;
	padding-right: 30px;
	color: green !important;
}
.itemtile-title > a {
	color: green;
}
.itemtile-body {
	color: green;
}
::-moz-placeholder {
	color: lightgreen;
}
.itemtile, .itemtile-condensed, .itemtile-fullpic {
	background-color: transparent;
	border-radius: 15px;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
.tilesbox, .tilesbox-featured {
	background-color: transparent;
}
.button-secondary {
	color: green;
	background: none;
	background-color: #000;
	border: 1px solid #000;
	box-shadow: none;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
#home-feed > .button-back2top {
	position: fixed;
	margin-left: 720px;
	bottom: 20px;
	z-index: 4;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
button, .button, .button-secondary, .button-square, .button-feature, .button-browse-next, .button-browse-previous, .button-back2top, .button-toggle, .is-toggled {
	font-weight: 400;
	white-space: nowrap;
	color: white;
	line-height: 1.4em;
}
.button-browse-next, .button-browse-previous, .button-back2top {
	background-color: #000;
}
.imagebox, .imagebox-small, .imagebox-storelogo-mini, .imagebox-thumbnail, .imagebox-storelogo {
	background-color: transparent;
}

/* Update 09.04.2020 */

.above-header-info {
	padding: 10px 0;
	width: 100%;
	color: green;
	background-color: #000;
}
.bubblebox, .login-bubblebox {
	background-color: #000;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}
.uploadbox, .uploadbox-logo {
	background-color: #000;
	border: 2px dashed transparent;
	box-shadow: 0 2px 6px rgba(0,255,0,0.8), inset 0 2px rgba(0,255,0,0.8), inset 0 10px rgba(255, 255, 255, 0.05), inset 0 10px 20px rgba(255, 255, 255, 0.08), inset 0 -15px 30px rgba(0,0,0,0.8) !important;
}

.galleryimage-element {
	background-color: #000;
}

.splitlinebox {
	padding: 16px;
	background-color: #fff0;
}

.actionlist-body {
	background-color: #fff0;
	padding: 0 0 10px 0;
}

.manageaditem > footer, .savedsearchitem-footer, .followuseritem-footer, .aditem-footer {
	background-color: #f4f4f400;
}

Element {
	background-color: rgb(0, 0, 0);
}
.itemtile-ribbon {
	color: #000;
}}
