style.css:
@-moz-document regexp('https?:\/\/news\.ycombinator\.com\/item(.*)')
{
  body {
      background-color: #2e3436 !important;
      font-family: Verdana;
  }

  table.comment-tree {
      background-color: #2e3436;
      width: 100%;
      padding-bottom: 20px;
  }

  a.storylink {
      font-size: 200%;
  }

  div.comment {
      background-color: #fff;
      font-size: 16px;
      border: 1px #f60 solid;
      border-radius: 5px;
      padding: 15px;
  }

  div.reply > p > font > u > a {
      background-color: #f60;
      padding: 5px;
      display: inline-block;
  }
}
