style.css:
@-moz-document domain("next.weekplan.net") {
body {
     color: #fff;  
     background-color: #25292d;
}

.vertical-tabs {
    background: #22252a;
}

.vertical-tabs-group {
    background-color: #1d1f23;
    color: #95a1af;
}

.vertical-tabs section>article {
    background-color: #22252a;
}

.font, .theme {
    border: 0;
    background-color: #1d1f23;
}

.vertical-tabs section>label {
    background: #2b3035;
    border: 1px solid #22252a;
    color: #c1cdd0;
}

.vertical-tabs input[name=sections]:checked+label {
    background: #1d1f23;
    color: #459c7f;
}

.info {
    color: #c1cdd0;
}

g {
    stroke: white;
}

.search-panel .group {
    background-color: #22252a;
}

.quadrant-body .quadrant-container .quadrant {
    border-color: #373d44;
}

.centered-form {
    background-color: #22252a;
    color: #ffffff;
    border: 1px solid #2b3035;
}

.call-out.tip, .call-out.warning {
    background-color: #1d1f23;
}

hr {
    border-top: 1px solid #373d44;
}

.gcal-settings-table td, .gcal-settings-table th, table.gcal-settings-table {
    border-bottom: 1px solid #373d44;
    border-top: 1px solid #373d44;
}

/* Forced text colors */

.journal-entry .inner-journal-entry, .journal-log .inner-journal-entry, .t-forest .journal-entry .inner-journal-entry, .t-forest .journal-log .inner-journal-entry, .t-nebula .journal-entry .inner-journal-entry, .t-nebula .journal-log .inner-journal-entry, .t-sunset .journal-entry .inner-journal-entry, .t-sunset .journal-log .inner-journal-entry, .t-water .journal-entry .inner-journal-entry, .t-water .journal-log .inner-journal-entry, .t-zengarden .journal-entry .inner-journal-entry, .t-zengarden .journal-log .inner-journal-entry, .entry-type-0 .inner-journal-entry, .entry-type-2 .inner-journal-entry, .table-container tr.goal-row .description  { 
    color: #fff !important;
}

/* Links */

a, button, .button, .link-button {
    border-bottom: 0;
    color: #459c7f;
}

a:hover, .period a:hover {
    color: #c1cdd0 !important;
}

.action:hover {
	color: #fff !important;
}

.board-navigation a, .picker a, .picker .menu-item, .nav-button, .popover-menu a {
    color: #95a1af;
}

.right-navigation a i {
    color: #fff;
}

.right-navigation a {
    color: #c1cdd0;
}

.right-navigation a.selected, .right-navigation a:hover {
    background-color: #22252a;
    color: #fff;
}

.board-navigation a:hover, .board-navigation a.selected, .picker a:hover, .picker a.selected, .picker .menu-item:hover, .picker .menu-item.selected, .nav-button:hover, .nav-button.selected, .popover-menu a:hover, .popover-menu a.selected {
    background-color: #2b3035;
    color: #c1cdd0;
}



/* Generic buttons */

.done-editing, .save-close, button.primary, .button.primary, .link-button.primary, .inner-inline-add-form button, .dropzone button, .button, .guider_button, input[type=button], input[type=submit], .gcal-settings-table .button {
    background-color: #373d44;
    color: #c1cdd0;
    border: 1px solid #525558;
    margin-top: 5px;
}


.done-editing:hover, .save-close:hover, button.primary:hover, .button.primary:hover, .link-button.primary:hover, .inner-inline-add-form button:hover, .dropzone button:hover, .button:hover, .guider_button:hover, input[type=button]:hover, input[type=submit]:hover, .gcal-settings-table .button:hover {
    color: #fff;
    background-color: #373d44;
    border-color: #5c6b7b;
}

.link-button, no-print link-button {
    color: #999999;
}

.link-button:hover, no-print link-button:hover {
    background-color: #2b3035;
    color: #fff;
} 

.collapsible-board-header .link-button:hover, .board-nav .link-button:hover {
    color: #ffffff;
    background-color: transparent;
}


.active .link-button {
    color: #eaeaea;
}


.quick-action-list-item .link-button:not(.urgent-selected), .menu-trigger .link-button {
    color: #95a1af;
    
    
}

/* Secondary buttons */

.modal-content button:not(.primary), .sidebar-content button[type="button"] {
    background-color: #1a1d21;
    color: #95a1af;
    border: 1px solid #2b3035;
    margin-top: 5px;
}

.modal-content button:not(.primary):hover, .sidebar-content button[type="button"]:hover {
    color: #c1cdd0;
    border: 1px solid #373d44;
}

/* negative buttons */

.button.negative, .guider_button.negative, input[type=button].negative, input[type=submit].negative {
    background-color: #c74545;
    color: #ffffff;
    border-color: #d86969;
}

.button.negative:hover, .guider_button.negative:hover, input[type=button].negative:hover, input[type=submit].negative:hover {
    background-color: #ea4040;
    color: #ffffff;
    border-color: #fb7c7c;
}

/* inputs and textareas */


input, textarea, select, .notes textarea, .notes .markdown-editor {
    padding: 3px;
    background: #373d44 !important;
    color: #fff !important;
	border-color: rgb(67, 71, 74) !important;
}

.edit-task-container textarea, .vision-page textarea, .react-date-picker__button input {
    background: transparent !important;
}



textarea::placeholder {
	color: #fff;
    opacity: 0.7;
}

/*this allows longer titles to fit in the task sidebar*/
.task-text {
    box-sizing: content-box;
}


/* this fixes subtask checkboy sliding */
.sidebar-content .subtask-area i.task-checkbox.fa.fa-check-empty {
    position: relative !important;
    padding: 0;
    margin: 0;
    float: right;
    top: 0;
    line-height: 1.15;
    padding-top: 11px;
    padding-right: 5px;
    font-size: 1.15em;
}

input:hover:not[type="range"], input:focus, select:hover, select:focus {
    border: 1px solid #525558;
}

.task-checkbox:hover {
    color: #25c782;
}

/* nav-bar / header */



.collapsible-board-header .link-button, .board-nav .link-button  {
    color: #c3c7dd;
}

nav.top {
    background-color: #459c7f;
}

.toolbar-buttons i.active {
    color: #ffffff;
}


.collapsible-board-title a, .collapsible-board-title-label {
    color: #c3c7dd;
}

.breadcrumb-parent {
    background-color: #303337;

}

.account-bar {
    color: #e6e6e6;
    background-color: #459c7f;
 }

.team-bar-nav li a:hover, .team-bar-nav li a.active {
    color: #4abf98;
 }


/* Layout */

.big-rock-advisor {
    display: none;
}

/*Droppable */
.dragging-task .droppable > .time-bar, .dragging-task .list-wrapper.droppable, .dragging-action-user.droppable > .task-container > .list-item, .droppable.over > .inner-quadrant > .quadrant-list {
    border-color: #459c7f !important; 
}

/* Headers */

h1 {
    color: #ffffff;
}

h2, .centered-form h2, .fold .left .login-form h2, .vertical-tabs h2 {
    color: #c1cdd0;
}


.today .list header h3 {
    color: #44a37c;
}


/*jquery ui*/
/* only used for the calendar in the journal page. the other calendars are React UI */

.ui-widget-content {
    background: #22252a;
    color: #c1cdd0;
    border: 1px solid rgba(0, 0, 0, 0.15);
    border-radius: 2px;
    box-shadow: 0 0 1px 1px rgba(0, 0, 0, 0.15), 0 2px 12px 0 rgba(0, 0, 0, 0.12);
}
  

.ui-datepicker-month, .ui-datepicker-year {
    font-family: 'Fjalla One', sans-serif;
    text-transform: uppercase;
    margin-top: 0;
    margin-bottom: 1em;
    color: #fff;
}

.ui-datepicker-calendar th, .ui-datepicker-week-col {
	text-align: center;
    text-transform: uppercase;
    font-weight: bold;
    font-size: .75em;
    color: #fff;
}

.ui-datepicker-calendar td, .ui-datepicker-calendar td a {
	color: #c1cdd0 !important;
    background: #22252a !important;
    border: 0 !important;
}


.ui-datepicker-calendar td:hover, .ui-datepicker-calendar td a:hover {
     color: #c1cdd0 !important;
    background-color: #373d44 !important;
}

/*  React UI */

.popover {
    background-color: #22252a;
}


/* React calendar */

.react-calendar {
       background-color: #22252a;
}

.react-calendar__navigation__label, .react-calendar button {
       color: #c1cdd0 !important;
}

.react-calendar .react-calendar__tile:not([disabled]):hover, .react-calendar .react-calendar__navigation__arrow:not([disabled]):hover, .react-calendar .react-calendar__navigation__label:not([disabled]):hover {
    background-color: #373d44;
}

.react-calendar__navigation button:enabled:hover, .react-calendar__navigation button:enabled:focus {
    background-color: #2b3035;
}

.react-calendar__tile--active {
    background: #459c7f;
}

.react-calendar__tile--hasActive {
    background: #459c7f;
}

.time-check-box {
    margin-right: 7px;
    width: 16px;
    height: 16px;
}

button.react-calendar__tile--active:enabled:hover, button.react-calendar__tile--active:enabled:focus {
    background: #6d9fd2;
}

/* React tabs */

.react-tabs [role=tab][aria-selected=true] {
    color: rgba(255, 255, 255, 0.8);
     border-color: #43474a;
}


.react-tabs__tab-panel.react-tabs__tab-panel--selected {
    border: 1px solid #43474a;
}

.react-tabs [role=tab]:hover {
    border-radius: 0;
    color: #c1cdd0;
}

.react-tabs [role=tab] {
    margin-left: 1px;
    color: #95a1af;
}

/* React modal */

.modal-body {
    background-color: #1a1d21;
}

.ReactModal__Content .modal-body > header {
    background-color: #22252a;
    color: #ffffff;
}


/* Hint orb */
.joyride-beacon__inner:before {
    content: "?";
