    background: #00000012;
    box-shadow: 2px 1px 3px #22252a;
}



.dropzone {
    background-color: #373d44;
}

.attachments ul li a {
    background-color: #2b3035;
}


/* Tasks */


.subtask-area .sub-task, .subtask-stack, .subtask-stack:after {
    border-color: #373d44;
}

.collapsed-task-list, .list-wrapper, .big-rock-advisor {
