}


.collapsed-task-list, .list-wrapper {
    /* border: 1px solid #c3070700; */
    border: 1px solid #1a1d21;
}

.today.list-wrapper {
    border: 1px solid #005dff00;
    background-color: #2b3035;
}

.task-container div:not(.progress) {
    background-color: #00000000;
}

.progress {
    background-color: #999999;
	
}

.inner-list-item {
    background-color: #1a1d21ed;
}

.list-item {
    border: 1px solid #8888886e;
}

.time-bar {
