}


.time-window, .slider {
	background-image: -webkit-linear-gradient(left top, transparent 0%, transparent 25%, #2b3035 25%, #2b3035 50%, transparent 50%, transparent 75%, #2b3035 75%);
}

.list-item.null {
    background-color: #2b3035 !important;
}

.today .task-container div {
    background-color: #373d44;
}

.list-item .time {
    color: #63a4ac;
