}

.notification-item > div:hover {
    background-color: #373d44;
}


.modal-area {
    background-color: #30363c;
}

.markdown-editor {
    cursor: text;
    color: #c1cdd0;
}

.subtask-area {
    border: 1px solid transparent;
}

.today .list-wrapper {
    border: 1px solid transparent;
    background-color: #2b3035;
}





/* range slider */

input[type=range] {
  -webkit-appearance: none;
  background: transparent;
}

input[type=range]:focus {
  outline: none; 

}

/* range slider WebKit */

input[type=range]::-webkit-slider-runnable-track {
  background: #373d44;
}

input[type=range]:focus::-webkit-slider-runnable-track {
  background: #5c6b7b;
}

input[type=range]::-webkit-slider-thumb {
  -webkit-appearance: none;
  background: #fff;
    
}


/*  range slider Firefox */

input[type=range]::-moz-range-track {
  background: #373d44;
}

input[type=range]::-moz-range-thumb {
  background: #fff;
}

/* IE */
input[type=range]::-ms-track {
  background: #373d44;
}

input[type=range]::-ms-thumb {
  background: #fff;
}



/* range slider Timer */

.focus-timer {
    background-color: #459c7f;
}


.focus-timer button {
    color: #ffffff;
    border-color: #1f674f;
    background: #459c7f;
}

.focus-timer .right-buttons button:hover {
    background-color: #408e75;
    color: #ffffff;
    border-color: #1f674f;
}

/* Color picker */

.github-picker {
    background-color: transparent !important;
    border: 0;
    width: 100% !important;
    box-shadow: 0 !important;
}

.github-picker span {
    padding: 3px;
}

/* Logo transparency fixed version */

.blue.weekplan-symbol.desktop-only {
    background-image: url("https://i.imgur.com/sYZhlYn.png");
}

/* Journal page */

.journal .primary-action {
    background-color: #22252a !important;
}


.journal textarea {
	margin-top: 10px;
}

.journal .moreWrite {
    color: #5c6b7b;
}

.journal .moreWrite:hover {
    color: #459c7f;
}

.tm-icon {
    background: #459c7f;
    box-shadow: 0 0 0 3px #72dcb9;
}

.timeline:before {
    background: #373d44;
}

.tab-item.selected {
    color: #ffffff;
    border-bottom-color: #ffffff;
}


.inner-journal-entry .positive {
    color: #27b561;
}

.inner-journal-entry .negative {
    color: #ff2e2e;
}

/* Goals page */

.table-container td.schedule, .table-container td.progress, .table-container td.description {
    border-right: 1px solid #373d44;
}


.table-container td.description, .table-container td.progress, .table-container td.schedule {
    border-bottom: 1px solid #373d44;
}

.table-container .goal {
    border: 1px solid #373d44;
    
}
.table-container td.progress {
    border-left: 1px solid #373d44;
}

.table-container tr.goal-row {
    background: #373d44;
}


.table-container .late .yes {
    background-color: #c74545;
}

.table-container .yes {
     background-color: #555b61;
}

.table-container td.schedule:hover {
    color: #fff;
    background-color: dontchange !important; /* invalid CSS on purpose, this breaks the hover background change */
    outline: 2px solid #ffffff40;
}

.legend-scheduled {
    border-left: 10px solid #555b61;
}

.legend-late {
    border-left: 10px solid #ed4337;
}

.circle-progress-bar:after {
    background-color: #25292d;
}

.circle-progress-bar {
    background-color: #373d44;
}
    



/* Scrollbars */

::-webkit-scrollbar {
    background-color: #00000024;
}

::-webkit-scrollbar-thumb {
    background-color: #0000005c;
}


::-webkit-scrollbar {
    background-color: #939ba966;
}


