style.css:
@-moz-document domain("twitter.com") {
:root {
    --blueLight: #d5e6f4;
    --blue: #80a8cc;
    --blueHover: #7093b3;
}

*, *:active, *:focus {
    font-family: 'Arial' !important;
    box-shadow: none !important;
    font-weight: 400 !important;
}



.global-nav .container {
    max-width: 100%;
    padding-right: 15px;
}




.global-nav-inner, .global-nav {
    height: 70px;
}
.global-nav-inner .container {
    display: flex;
    align-items: center;
}
.topbar, .global-nav {
    border: none;
    z-index: 10000;
}
.global-nav {
    box-shadow: 0 0 5px rgba(0,0,0,0.2) !important;
}
.global-nav-inner .pull-right.nav-extras, .global-nav-inner [role="navigation"] {
    flex: 1;
}
.global-nav-inner .pull-right.nav-extras {
    justify-content: flex-end;
    display: flex;
}
.global-nav-inner .pull-right.nav-extras [role="search"] {
    display: flex;
    align-items: center;
}
.global-nav-inner .pull-right.nav-extras [role="search"] form {
    margin: 0 15px 0 0;
    height: 36px;
}
.global-nav .search-input {
    background: #fff;
    height: 36px;
    margin-top: 0px
}
.global-nav-inner [role="navigation"] {
    padding-left: 100px;
}
.nav li, .global-nav .pull-right .nav {
    position: initial;
}
.nav.right-actions > li + li {
    margin: 0;
}
#global-actions .new::before {
    left: -1px;
    top: -8px
}
#global-actions > li {
    height: 42px;
}
.nav#global-actions > li .Icon {
    margin: 0;
}
#global-actions a {
    border: 1px solid #eee !important;
    border-radius: 50%;
    padding: 0;
    height: 40px !important;
    width: 40px !important;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 15px;
}
#global-actions a .text {
    display: none;
}
#global-new-tweet-button {
    position: absolute;
    left: 15px;
    top: 15px;
    border-radius: 50%;
    height: 42px;
    width: 42px;
    background: var(--blue);
}
#global-new-tweet-button:hover {
    border: none;
    background: var(--blueHover);
}
#global-new-tweet-button:after {
    content: "";
    height: 70px;
    width: 1px;
    background: #fafafa;
    display: block;
    position: absolute;
    top: -16px;
    right: -20px;
}
#global-new-tweet-button span {
    font-size: 0px;
}
#global-new-tweet-button span:after {
    content: "";
    position: absolute;
    background: url('http://cdn.onlinewebfonts.com/svg/img_557136.png') center;
    background-size: cover;
    display: block;
    width: 60%;
    height: 60%;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    filter: invert(1);
}
#global-actions > li {
    position: relative;
}
.bird-topbar-etched {
    margin-top: -10px;
    font-size: 32px;
}
.u-textUserColor, .u-textUserColorHover:hover, .u-textUserColorHover:hover .ProfileTweet-actionCount, .u-textUserColorHover:focus, #global-actions > li:hover > a, #global-actions > li > a:focus, #global-actions > li.active .text, .DashUserDropdown.dropdown-menu .nightmode-toggle .Icon, .nav.right-actions > li > a:hover, .nav.right-actions > li > a:focus, a, s, .btn-link, .btn-link:focus, .icon-btn, .pretty-link b, .pretty-link:hover s, .pretty-link:hover b, .pretty-link:focus s, .pretty-link:focus b, .metadata a:hover, .metadata a:focus, a.account-group:hover .fullname, a.account-group:focus .fullname, .account-summary:focus .fullname, .message .message-text a, .message .message-text button, .stats a strong, .plain-btn:hover, .plain-btn:focus, .dropdown.open .user-dropdown.plain-btn, .open > .plain-btn, #global-actions .new::before, .module .list-link:hover, .module .list-link:focus, .stats a:hover, .stats a:hover strong, .stats a:focus, .stats a:focus strong, .find-friends-sources li:hover .source, .stream-item a:hover .fullname, .stream-item a:focus .fullname, .stream-item .view-all-supplements:hover, .stream-item .view-all-supplements:focus, .tweet .time a:hover, .tweet .time a:focus, .tweet .details.with-icn b, .tweet .details.with-icn .Icon, .stream-item:hover .original-tweet .details b, .stream-item .original-tweet.focus .details b, .stream-item.open .original-tweet .details b, .client-and-actions a:hover, .client-and-actions a:focus, .dismiss-btn:hover b, .tweet .context .pretty-link:hover s, .tweet .context .pretty-link:hover b, .tweet .context .pretty-link:focus s, .tweet .context .pretty-link:focus b, .list .username a:hover, .list .username a:focus, .list-membership-container .create-a-list, .list-membership-container .create-a-list:hover, .new-tweets-bar, .card .list-details a:hover, .card .list-details a:focus, .card .card-body:hover .attribution, .card .card-body .attribution:focus {
    color: var(--blue) !important;
}
.dropdown-menu li > a:hover, .dropdown-menu li > a:focus, .dropdown-menu .dropdown-link:hover, .dropdown-menu .dropdown-link:focus, .dropdown-menu .dropdown-link.is-focused, .dropdown-menu li:hover .dropdown-link, .dropdown-menu li:focus .dropdown-link, .dropdown-menu .selected a, .dropdown-menu .dropdown-link.selected {
    background: var(--blue) !important;
}
.DashUserDropdown {
    right: 0 !important;
    height: 100vh;
    top: 70px !important;
    box-shadow: 0 5px 5px rgba(0,0,0,0.3) !important;
    width: 290px;
    margin: 0 !important;
    border-radius: 0;
    border-top: 1px solid #eee;
}
.DashUserDropdown .dropdown-caret {
    display: none;
}
.SendTweetsButton {
    background: var(--blue);
}
.SendTweetsButton:hover {
    background: var(--blueHover) !important;
    border-color: var(--blueHover);
}
.dropdown-menu:not(.DashUserDropdown) {
    box-shadow: 0 2px 5px rgba(0,0,0,0.3) !important;
}









.AppContent {
    padding: 70px 0 15px
}

