#timeline {
    width: 690px;
}
.stream-container {
    box-shadow: 0 0 10px rgba(0,0,0,0.1), 0 0 3px rgba(0,0,0,0.1) !important;
}
.ThreadedDescendants .stream-container {
    box-shadow: none !important;
}
.stream-item:not(.no-header-background-module) {
    border-right: none;
    border-left: none;
}
.tweet {
    padding: 15px 25px;
    box-sizing: border-box;
    position: relative;
}
.top-timeline-tweetbox .timeline-tweet-box {
    margin: 25px 0 75px;
}
.new-tweets-bar {
    margin-top: -75px;
    margin-left: 1px;
    position: relative;
    border: none;
    background: #fff;
    border-top: 2px solid var(--blue);
    width: 688px !important;
    border-radius: 0;
    overflow: hidden;
    transition: 0.2s ease;
}
.new-tweets-bar:before {
    content: "";
    position: absolute;
    top: -100%;
    left: 0;
    width: 100%;
    height: 100%;
    background: var(--blue);
    transition: 0.2s ease;
    z-index: -1;
}
.new-tweets-bar:hover {
    background: #fff;
    border: none;
    border-top: 2px solid var(--blue);
    color: #fff !important;
}
.new-tweets-bar:hover:before {
    top: 0;
}
.AdaptiveMediaOuterContainer .AdaptiveMedia, .AdaptiveMedia img, .AdaptiveMedia-video {
    min-width: 100%;
}
.home-tweet-box, .LiveVideo-tweetBox, .RetweetDialog-commentBox {
    background: #fff;
    box-shadow: 0 0 10px rgba(0,0,0,0.1), 0 0 3px rgba(0,0,0,0.1) !important;
}
.top-timeline-tweet-box-user-image {
    display: none;
}
.top-timeline-tweetbox .timeline-tweet-box .t1-form:before {
    content: "+";
    position: absolute;
    left: 0;
    background: var(--blue);
    top: 0;
    width: 58px;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #fff;
    font-size: 52px;
}
.RichEditor, .tweet-box textarea:focus, .tweet-box input[type="text"], .currently-dragging .tweet-form.is-droppable .tweet-drag-help, .tweet-box[contenteditable="true"]:focus, .RichEditor.is-fakeFocus, .RichEditor.is-fakeFocus ~ .TweetBoxAttachments {
    border-color: transparent;
    box-shadow: none;
}
.top-timeline-tweetbox .timeline-tweet-box .tweet-form.condensed .tweet-box {
    color: var(--blue);
}
.photo-selector:hover .btn, .icon-btn:hover {
    background: #eee;
    border-color: #eee;
}
.timeline-tweet-box .EdgeButton--primary, .timeline-tweet-box .EdgeButton--primary:focus, .timeline-tweet-box .EdgeButton--secondary, .timeline-tweet-box .EdgeButton--secondary:hover, .timeline-tweet-box .EdgeButton--secondary:focus, .timeline-tweet-box .EdgeButton--secondary:active, .EdgeButton--secondary:hover, .EdgeButton--secondary:active, .following button {
    background: var(--blue);
    border-color: var(--blue);
    color: #fff !important;
}
.EdgeButton--secondary, .EdgeButton--secondary:hover, .EdgeButton--secondary:focus, .EdgeButton--secondary:active, .following button:hover {
    border-color: var(--blue) !important;
    color: var(--blue) !important;
    background: transparent;
}
.TweetBoxAttachments {
    border-color: transparent;
}
.tweet:hover {
    background: #fafafa;
}
.js-stream-tweet .self-thread-tweet-cta .mini-avatar-with-thread::before {
    bottom: 50px;
    left: 46px;
}
.js-stream-tweet.tweet-has-context .self-thread-tweet-cta .mini-avatar-with-thread::before {
    top: 95px;
}
.AdaptiveMedia-doublePhoto, .AdaptiveMedia-triplePhoto, .AdaptiveMedia-quadPhoto {
    width: 582px;
}
.hovercard {
    box-shadow: 0 0 10px rgba(0,0,0,0.1), 0 0 3px rgba(0,0,0,0.1) !important;
}
#global-tweet-dialog .modal-tweet-form-container, #Tweetstorm-dialog .modal-body {
    background: var(--blueLight);
}
#global-tweet-dialog .modal-header, #Tweetstorm-dialog .modal-header {
    border-color: var(--blueLight);
}
.PlayableMedia-attribution.js-user-profile-link.js-nav img {
    min-width: 20px !important;
}
.AdaptiveMedia.is-square:not(.is-video) {
    max-width: 506px !important;
}






