style.css:
@-moz-document url-prefix("https://minerender.org/") {
body {
    background: #1b2121;
    color: #dee8e8;
}

.divider {
    color: #3f51b5;
    background: #3f51b5;
}

.card-panel {
    background: #212828;
}

pre[class*=language-] {
    background: #101414;
}

code[class*=language-], pre[class*=language-] {
    text-shadow: none;
    color: #dee8e8;
}

.modal {
    background: #1b2121;
    color: #dee8e8;
}
}
