  .tg_head_peer_dropdown .dropdown-menu {
    box-shadow: none;
  }
  .im_message_focus_active .im_message_outer_wrap {
    animation-duration: 2s;
  }
  @keyframes im_message_focus_fade {
    from {
      background-color: var(--gray-light);
    }
  }
  /* Selection */
  ::-moz-selection {
    background-color: var(--main);
    color: var(--white);
  }
  ::selection {
    background-color: var(--main);
    color: var(--white);
  }
  /* Margin / Padding */
  .im_service_message {
    margin: 0;
    padding: 9px 7px;
  }
  .im_dialogs_panel {
    padding: 8px;
  }
  /* Progress arc */
  .progress-arc-intermediate .progress-arc .progress-arc-bar {
    stroke: var(--main);
  }
  .stop0,
  .stop60,
  .stop100 {
    stop-color: var(--main);
  }
  .composer_emoji_insert_btn:hover .icon-emoji,
  .inactive_image {
    opacity: 0.8;
  }
  .composer_emoji_insert_btn_on:hover .icon-emoji {
    opacity: 1;
  }
  /* Hue rotation */
  .composer_emoji_tooltip_category i {
    filter: invert(100%);
  }
  .active .im_dialog_pinned,
  .tgme_widget_message_views:after {
    filter: brightness(250%);
  }
  .icon-filter-audio,
  .icon-filter-documents,
  .icon-filter-photos,
  .icon-filter-video,
  .icon-message-views {
    filter: brightness(75%);
  }
  .im_history_message_wrap.im_message_selected .icon-message-views {
    filter: saturate(0%);
  }
  /* Code blocks */
  code, pre {
    background-color: #272822;
    border: none;
    color: #EFEBDA;
  }
}

@-moz-document domain("t.me"), domain("telegram.me") {
  a.tgme_widget_message_date {
    color: inherit;
  }
  .tgme_page_web_action a.tgme_action_button:active,
  .tgme_page_web_action a.tgme_action_button:hover {
    background-color: #5DC390;
  }
  .tgme_page_widget_actions a.tgme_action_web_button:hover {
    text-decoration: none;
  }
  .tgme_widget_message_bubble_tail .background,
  .tgme_widget_message_bubble_tail .border_1x {
    fill: var(--gray-light);
  }
  .tgme_widget_message_bubble_tail .border {
    stroke: var(--gray-light);
  }
  a.tgme_action_button.tgme_action_web_button {
    padding-top: 13px;
    padding-bottom: 11px;
    border: 2px solid #5DC390;
  }
  a.tgme_action_button_new:before {
    filter: saturate(0%);
  }
  a.tgme_action_button_new:hover:before {
    filter: brightness(0%);
  }
}

