	}
	/*Video*/
	#watch > .ui.grid > .anime-video .options
	{
		background: var(--main-background) !important;
	}
	/*next and prev buttons*/
	.ui.basic.button.small.svg.uppercase:hover, .ui.basic.button.small.svg.uppercase:hover svg {
		color: var(--main-color) !important;
		fill: var(--main-color) !important;
		transition: none !important;
	}
	/*episode number*/
	#watch > .ui.grid > .anime-info > .sixteen.wide.column > .info > .details > .info h2
	{
		color: var(--main-color) !important;
	}
	/*people wacthing*/
	#watch > .ui.grid > .anime-info > .sixteen.wide.column > .info > .details > .stats .whos-watching .users .item .ui.avatar.image, 
	#watch > .ui.grid > .anime-info > .sixteen.wide.column > .info > .details > .stats .whos-watching .users .item .ui.circular.label
	{
		border-color: var(--hover-background) !important;
	}
