	/*scrubber, audio*/
	.vjs-masterani-skin .vjs-control-bar .vjs-progress-control .vjs-play-progress, .vjs-masterani-skin .vjs-control-bar .vjs-volume-menu-button .vjs-volume-bar .vjs-volume-level
	{
