style.css:
@-moz-document url-prefix(http://localhost/phpmyadmin), url-prefix(http://127.0.0.1/phpmyadmin){
/* FILE: codemirror.css */
/******************************************************************************/
/* BASICS */

img#imgpmalogo {
    -webkit-filter: invert(1);
    filter: invert(1);
}
.CodeMirror {
    /* Set height, width, borders, and global font properties here */
    
    font-family: monospace;
    height: 300px;
    color: #ffffff;
    border: 1px solid #ddd;
}
/* PADDING */

.CodeMirror-scrollbar-filler,
.CodeMirror-gutter-filler {
    background-color: #111;
    /* the little square between h and v scrollbars */
}
/* GUTTER */

.CodeMirror-gutters {
    border-right: 1px solid #222222;
    background-color: #080808;
    white-space: nowrap;
}
.CodeMirror-linenumber {
    padding: 0 3px 0 5px;
    min-width: 20px;
    text-align: right;
    color: #666666;
    white-space: nowrap;
}
.CodeMirror-guttermarker {
    color: #ffffff;
}
.CodeMirror-guttermarker-subtle {
    color: #666666;
}
/* CURSOR */

.CodeMirror div.CodeMirror-cursor {
    border-left: 1px solid #fff;
}
/* Shown when moving in bi-directional text */

.CodeMirror div.CodeMirror-secondarycursor {
    border-left: 1px solid #000;
}
.CodeMirror.cm-fat-cursor div.CodeMirror-cursor {
    width: auto;
    border: 0;
    background: #881188;
}
@-moz-keyframes blink {
    0% {
        background: #881188;
    }
    50% {
        background: none;
    }
    100% {
        background: #881188;
    }
}
@-webkit-keyframes blink {
    0% {
        background: #881188;
    }
    50% {
        background: none;
    }
    100% {
        background: #881188;
    }
}
@keyframes blink {
    0% {
        background: #881188;
    }
    50% {
        background: none;
    }
    100% {
        background: #881188;
    }
}
.CodeMirror-ruler {
    border-left: 1px solid #333333;
    position: absolute;
}
/* DEFAULT THEME */

.cm-s-default .cm-header {
    color: #ffff00;
}
.cm-s-default .cm-quote {
    color: #ff66ff;
}
.cm-negative {
    color: #22bbbb;
}
.cm-positive {
    color: #dd66dd;
}
.cm-header,
.cm-strong {
    font-weight: bold;
}
.cm-em {
    font-style: italic;
}
.cm-link {
    text-decoration: underline;
}
.cm-strikethrough {
    text-decoration: line-through;
}
.cm-s-default .cm-keyword {
    color: #88ff77;
}
.cm-s-default .cm-atom {
    color: #ddee66;
}
.cm-s-default .cm-number {
    color: #ee99bb;
}
.cm-s-default .cm-def {
    color: #ffff00;
}
.cm-s-default .cm-variable-2 {
    color: #ffaa55;
}
.cm-s-default .cm-variable-3 {
    color: #ff77aa;
}
.cm-s-default .cm-comment {
    color: #55aaff;
}
.cm-s-default .cm-string {
    color: #55eeee;
}
.cm-s-default .cm-string-2 {
    color: #00aaff;
}
.cm-s-default .cm-meta {
    color: #aaaaaa;
}
.cm-s-default .cm-qualifier {
    color: #aaaaaa;
}
.cm-s-default .cm-builtin {
    color: #ccff55;
}
.cm-s-default .cm-bracket {
    color: #666688;
}
.cm-s-default .cm-tag {
    color: #ee88ff;
}
.cm-s-default .cm-attribute {
    color: #ffff33;
}
.cm-s-default .cm-hr {
    color: #666666;
}
.cm-s-default .cm-link {
    color: #ffff33;
}
.cm-s-default .cm-error {
    color: #00ffff;
}
.cm-invalidchar {
    color: #00ffff;
}
.CodeMirror-composing {
    border-bottom: 2px solid;
}
/* Default styles for common addons */

div.CodeMirror span.CodeMirror-matchingbracket {
    color: #ff00ff;
}
div.CodeMirror span.CodeMirror-nonmatchingbracket {
    color: #00dddd;
}
.CodeMirror-matchingtag {
    background: rgba(0, 105, 255, .3);
}
.CodeMirror-activeline-background {
    background: #170d00;
}
/* STOP */
/* The rest of this file contains styles related to the mechanics of
   the editor. You probably shouldn't touch them. */

.CodeMirror {
    position: relative;
    overflow: hidden;
    background: #111;
}
.CodeMirror-scroll {
    overflow: scroll !important;
    /* Things will break if this is overridden */
    /* 30px is the magic margin used to hide the element's real scrollbars */
    /* See overflow: hidden in .CodeMirror */
    
    margin-bottom: -30px;
    margin-right: -30px;
    padding-bottom: 30px;
    height: 100%;
    outline: none;
    /* Prevent dragging from highlighting the element */
    
    position: relative;
}
.CodeMirror-sizer {
    position: relative;
    border-right: 30px solid transparent;
}
/* The fake, visible scrollbars. Used to force redraw during scrolling
   before actuall scrolling happens, thus preventing shaking and
   flickering artifacts. */

.CodeMirror-vscrollbar,
.CodeMirror-hscrollbar,
.CodeMirror-scrollbar-filler,
.CodeMirror-gutter-filler {
    position: absolute;
    z-index: 6;
    display: none;
}
.CodeMirror-vscrollbar {
    right: 0;
    top: 0;
    overflow-x: hidden;
    overflow-y: scroll;
}
.CodeMirror-hscrollbar {
    bottom: 0;
    left: 0;
    overflow-y: hidden;
    overflow-x: scroll;
}
.CodeMirror-scrollbar-filler {
    right: 0;
    bottom: 0;
}
.CodeMirror-gutter-filler {
    left: 0;
    bottom: 0;
}
.CodeMirror-gutters {
    position: absolute;
    left: 0;
    top: 0;
    z-index: 3;
}
.CodeMirror-gutter-wrapper {
    position: absolute;
    z-index: 4;
    height: 100%;
}
.CodeMirror-gutter-elt {
    position: absolute;
    cursor: default;
    z-index: 4;
}
.CodeMirror-gutter-wrapper {
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
}
.CodeMirror-lines {
    cursor: text;
    min-height: 1px;
    /* prevents collapsing before first draw */
}
.CodeMirror pre {
    /* Reset some styles that the rest of the page might have set */
    
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
    border-width: 0;
    background: transparent;
    font-family: inherit;
    font-size: inherit;
    margin: 0;
    white-space: pre;
    word-wrap: normal;
    line-height: inherit;
    color: inherit;
    z-index: 2;
    position: relative;
    overflow: visible;
    -webkit-tap-highlight-color: transparent;
}
.CodeMirror-wrap pre {
    word-wrap: break-word;
    white-space: pre-wrap;
    word-break: normal;
}
.CodeMirror-linebackground {
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    z-index: 0;
}
.CodeMirror-linewidget {
    position: relative;
    z-index: 2;
    overflow: auto;
}
.CodeMirror-code {
    outline: none;
}
/* Force content-box sizing for the elements where we expect it */

.CodeMirror-scroll,
.CodeMirror-sizer,
.CodeMirror-gutter,
.CodeMirror-gutters,
.CodeMirror-linenumber {
    -moz-box-sizing: content-box;
    box-sizing: content-box;
}
.CodeMirror-measure {
    position: absolute;
    width: 100%;
    height: 0;
    overflow: hidden;
    visibility: hidden;
}
.CodeMirror-measure pre {
    position: static;
}
.CodeMirror div.CodeMirror-cursor {
    position: absolute;
    border-right: none;
    width: 0;
}
div.CodeMirror-cursors {
    visibility: hidden;
    position: relative;
    z-index: 3;
}
.CodeMirror-focused div.CodeMirror-cursors {
    visibility: visible;
}
.CodeMirror-selected {
    background: #262626;
}
.CodeMirror-focused .CodeMirror-selected {
    background: #282b0f;
}
.CodeMirror-crosshair {
    cursor: crosshair;
}
.CodeMirror::selection {
    background: #282b0f;
}
.CodeMirror::-moz-selection {
    background: #282b0f;
}
.cm-searching {
    background: #000055;
    background: rgba(0, 0, 255, .4);
}
/* IE7 hack to prevent it from returning funny offsetTops on the spans */

.CodeMirror span {
    *vertical-align: text-bottom;
}
/* Used to force a border model for a node */

.cm-force-border {
    padding-right: .1px;
}
@media print {
    /* Hide the cursor when printing */
    
    .CodeMirror div.CodeMirror-cursors {
        visibility: hidden;
    }
}
/* See issue #dd66ff1 */

.cm-tab-wrap-hack:after {
    content: '';
}
/* Help users use markselection to safely style text background */

span.CodeMirror-selectedtext {
    background: none;
}
/* FILE: common.css.php */
/******************************************************************************/
/* general tags */

body {
    font-family: sans-serif;
    padding: 0;
    margin: 0;
    margin-left: 240px;
    color: #bbbbbb;
    background: #333333;
}
select,
textarea,
input {
    color: #bbbbbb;
    background: #000000;
}
h2 {
    font-size: 2em;
    font-weight: normal;
    text-shadow: 0 1px 0 #000000;
    padding: 10px 0 10px;
    padding-left: 3px;
    color: #888888;
}
/* Hiding icons in the page titles */

a,
a:link,
a:visited,
a:active,
button.mult_submit,
.checkall_box+label {
    text-decoration: none;
    color: #dca57e;
    cursor: pointer;
    outline: none;
}
a:hover,
button.mult_submit:hover,
button.mult_submit:focus,
.checkall_box+label:hover {
    text-decoration: underline;
    color: #dca57e;
}
#initials_table {
    background: #0c0c0c;
    border: 1px solid #555555;
    margin-bottom: 10px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
}
#initials_table a {
    border: 1px solid #555555;
    background: #000000;
    padding: 4px 8px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#000000), to(#1f1f1f));
    background: -webkit-linear-gradient(top, #000000, #1f1f1f);
    background: -moz-linear-gradient(top, #000000, #1f1f1f);
    background: -ms-linear-gradient(top, #000000, #1f1f1f);
    background: -o-linear-gradient(top, #000000, #1f1f1f);
}
#initials_table a.active {
    border: 1px solid #999999;
    box-shadow: 0 0 2px #666666;
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#444444), to(#000000));
    background: -webkit-linear-gradient(top, #444444, #000000);
    background: -moz-linear-gradient(top, #444444, #000000);
    background: -ms-linear-gradient(top, #444444, #000000);
    background: -o-linear-gradient(top, #444444, #000000);
}
th {
    font-weight: bold;
    color: #ffffff;
    background: #0c0c0c;
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#000000), to(#333333));
    background: -webkit-linear-gradient(top, #000000, #333333);
    background: -moz-linear-gradient(top, #000000, #333333);
    background: -ms-linear-gradient(top, #000000, #333333);
    background: -o-linear-gradient(top, #000000, #333333);
}
hr {
    color: #bbbbbb;
    background-color: #bbbbbb;
    border: 0;
    height: 1px;
}
input[type=text],
input[type=password],
input[type=number],
input[type=date] {
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    background: #111;
    border: 1px solid #555555;
    color: #fff;
    padding: 4px;
}
input[type=text][disabled],
input[type=text][disabled]:hover,
input[type=password][disabled],
input[type=password][disabled]:hover,
input[type=number][disabled],
input[type=number][disabled]:hover,
input[type=date][disabled],
input[type=date][disabled]:hover,
select[disabled],
select[disabled]:hover {
    background: #171717;
    box-shadow: none;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
}
input[type=text]:hover,
input[type=text]:focus,
input[type=password]:hover,
input[type=password]:focus,
input[type=number]:hover,
input[type=number]:focus,
input[type=date]:hover,
input[type=date]:focus,
select:focus {
    border: 1px solid #838383;
    background: #000000;
}
input[type=text]:hover,
input[type=password]:hover,
input[type=number]:hover,
input[type=date]:hover {
    box-shadow: 0 1px 3px #555555;
    -webkit-box-shadow: 0 1px 3px #555555;
    -moz-box-shadow: 0 1px 3px #555555;
}
input[type=submit],
input[type=button],
button[type=submit]:not(.mult_submit),
input[type=reset],
input[name=submit_reset],
input.button {
    margin: 6px 14px;
    border: 1px solid #555555;
    padding: 3px 7px;
    color: #eeeeee;
    text-decoration: none;
    background: #222222;
    border-radius: 12px;
    -webkit-border-radius: 12px;
    -moz-border-radius: 12px;
    text-shadow: 0 1px 0 #000000;
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#070707), to(#272727));
    background: -webkit-linear-gradient(top, #070707, #272727);
    background: -moz-linear-gradient(top, #070707, #272727);
    background: -ms-linear-gradient(top, #070707, #272727);
    background: -o-linear-gradient(top, #070707, #272727);
}
input[type=submit]:hover,
input[type=button]:hover,
button[type=submit]:not(.mult_submit):hover,
input[type=reset]:hover,
input[name=submit_reset]:hover,
input.button:hover {
    position: relative;
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#000000), to(#222222));
    background: -webkit-linear-gradient(top, #000000, #222222);
    background: -moz-linear-gradient(top, #000000, #222222);
    background: -ms-linear-gradient(top, #000000, #222222);
    background: -o-linear-gradient(top, #000000, #222222);
    cursor: pointer;
}
input[type=submit]:active,
input[type=button]:active,
button[type=submit]:not(.mult_submit):active,
input[type=reset]:active,
input[name=submit_reset]:active,
input.button:active {
    position: relative;
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#111111), to(#222222));
    background: -webkit-linear-gradient(top, #111111, #222222);
    background: -moz-linear-gradient(top, #111111, #222222);
    background: -ms-linear-gradient(top, #111111, #222222);
    background: -o-linear-gradient(top, #111111, #222222);
    box-shadow: 0 1px 6px -2px #cccccc inset;
    text-shadow: none;
}
input[type=submit]:disabled,
input[type=button]:disabled,
button[type=submit]:not(.mult_submit):disabled,
input[type=reset]:disabled,
input[name=submit_reset]:disabled,
input.button:disabled {
    background: #333333;
    color: #999999;
    text-shadow: none;
}
fieldset,
.preview_sql {
    margin-top: 1em;
    border-radius: 4px 4px 0 0;
    -moz-border-radius: 4px 4px 0 0;
    -webkit-border-radius: 4px 4px 0 0;
    border: #555555 solid 1px;
    padding: 1.5em;
    background: #222;
    text-shadow: 1px 1px 2px #000000 inset;
    -moz-box-shadow: 1px 1px 2px #000000 inset;
    -webkit-box-shadow: 1px 1px 2px #000000 inset;
    box-shadow: 1px 1px 2px #000000 inset;
}
fieldset fieldset {
    margin: .8em;
    border: 1px solid #555555;
    background: #171717;
}
fieldset legend {
    font-weight: bold;
    color: #bbbbbb;
    padding: 5px 10px;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border: 1px solid #555555;
    background-color: #000000;
    -moz-box-shadow: 3px 3px 15px #444444;
    -webkit-box-shadow: 3px 3px 15px #444444;
    box-shadow: 3px 3px 15px #444444;
    max-width: 100%;
}
table caption,
table th,
table td {
    padding: .3em;
    margin: .1em;
    vertical-align: top;
    text-shadow: 0 1px 0 #000000;
}
/* 3.4 */

thead th {
    border-right: 1px solid #000000;
}
select {
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    border: 1px solid #444444;
    color: #fff;
    padding: 3px;
    background: #000;
    margin: 6px;
}
select[multiple] {
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#000000), to(#0d0d0d));
    background: -webkit-linear-gradient(top, #000000, #0d0d0d);
    background: -moz-linear-gradient(top, #000000, #0d0d0d);
    background: -ms-linear-gradient(top, #000000, #0d0d0d);
    background: -o-linear-gradient(top, #000000, #0d0d0d);
}
/******************************************************************************/
/* classes */

.separator {
    color: #000000;
    text-shadow: 0 1px 0 #ffffff;
}
div.tools a {
    color: #c58152 !important;
}
/* odd items 1,3,5,7,... */

table tr.odd th,
.odd {
    background: #000000;
}
/* even items 2,4,6,8,... */
/* (tested on CRTs and ACLs) */

table tr.even th,
.even {
    background: #202020;
}
/* marked table rows */

td.marked:not(.nomarker),
table tr.marked:not(.nomarker) td,
table tr.marked:not(.nomarker) th,
table tr.marked:not(.nomarker) {
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#312920), to(#493928));
    background: -webkit-linear-gradient(top, #312920, #493928);
    background: -moz-linear-gradient(top, #312920, #493928);
    background: -ms-linear-gradient(top, #312920, #493928);
    background: -o-linear-gradient(top, #312920, #493928);
    color: #ffffff;
}
/* hovered items */

.odd:not(.nopointer):hover,
.even:not(.nopointer):hover,
.hover:not(.nopointer) {
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#312920), to(#493928));
    background: -webkit-linear-gradient(top, #312920, #493928);
    background: -moz-linear-gradient(top, #312920, #493928);
    background: -ms-linear-gradient(top, #312920, #493928);
    background: -o-linear-gradient(top, #312920, #493928);
    color: #ffffff;
}
/* hovered table rows */

table tr.odd:not(.nopointer):hover th,
table tr.even:not(.nopointer):hover th,
table tr.hover:not(.nopointer) th {
    background-size: 100% 100%;
    background: -webkit-gradient(linear, left top, left bottom, from(#312920), to(#493928));
    background: -webkit-linear-gradient(top, #312920, #493928);
    background: -moz-linear-gradient(top, #312920, #493928);
    background: -ms-linear-gradient(top, #312920, #493928);
    background: -o-linear-gradient(top, #312920, #493928);
    color: #ffffff;
}
/**
 * marks table rows/cells if the db field is in a where condition
 */

.condition {
    border-color: #003366 !important;
}
/* for first th which must have right border set (ltr only) */

.before-condition {
    border-right: 1px solid #003366;
}
/**
 * cells with the value NULL
 */

.attention {
    color: #ff0000;
    font-weight: bold;
}
.allfine {
    color: #008000;
}
.pdflayout {
    overflow: hidden;
    clip: inherit;
    background-color: #000000;
    display: none;
    border: 1px solid #ffffff;
    position: relative;
}
.pdflayout_table {
    background: #2c231c;
    color: #ffffff;
    overflow: hidden;
    clip: inherit;
    z-index: 2;
    display: inline;
    visibility: inherit;
    cursor: move;
    position: absolute;
    font-size: 80%;
    border: 1px dashed #ffffff;
}
/* Doc links in SQL */

.cm-sql-doc {
    text-decoration: none;
    border-bottom: 1px dotted #ffffff;
    color: inherit !important;
}
/* message boxes: error, confirmation */

div.success,
div.notice,
div.error {
    margin: .5em 0 0.5em;
    border: 1px solid;
    background-repeat: no-repeat;
    background-position: 10px 50%;
    padding: 10px 10px 10px 10px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    -moz-box-shadow: 0 1px 1px #000000 inset;
    -webkit-box-shadow: 0 1px 1px #000000 inset;
    box-shadow: 0 1px 1px #000000 inset;
}
.success {
    color: #ffffff;
    background-color: #14075b;
}
h1.success,
div.success {
    border-color: #5d2db9;
}
.success h1 {
    border-color: #ff00ff;
}
.notice {
    color: #ffffff;
    background-color: #17110e;
}
h1.notice,
div.notice {
    border-color: #c59381;
}
.notice h1 {
    border-color: #004ef5;
}
.error {
    border: 1px solid #800000 !important;
    color: #ffffff;
    background: #ffc0cb;
}
h1.error,
div.error {
    border-color: #cccccc;
    background: #954545;
}
div.error h1 {
    border-color: #00ffff;
    background-color: #954545;
}
.confirmation {
    color: #ffffff;
    background-color: #ffc0cb;
}
/* end messageboxes */

.tblcomment {
    font-size: 70%;
    font-weight: normal;
    color: #ffff66;
}
.tblHeaders {
    font-weight: bold;
    color: #ffffff;
    background: #2c231c;
}
div.tools,
.tblFooters {
    font-weight: normal;
    color: #ffffff;
    background: #2c231c;
}
.tblHeaders a:link,
.tblHeaders a:active,
.tblHeaders a:visited,
div.tools a:link,
div.tools a:visited,
div.tools a:active,
.tblFooters a:link,
.tblFooters a:active,
.tblFooters a:visited {
    color: #ffff00;
}
.tblHeaders a:hover,
div.tools a:hover,
.tblFooters a:hover {
    color: #00ffff;
}
/* forbidden, no privileges */

.noPrivileges {
    color: #00ffff;
    font-weight: bold;
}
/* disabled text */

.disabled,
.disabled a:link,
.disabled a:active,
.disabled a:visited {
    color: #999999;
}
.disabled a:hover {
    color: #999999;
    text-decoration: none;
}
