/* FILE: navigation.css.php */
/******************************************************************************/
/* Navigation */

#pma_navigation {
    width: 240px;
    position: fixed;
    top: 0;
    left: 0;
    height: 100%;
    color: #ffffff;
    z-index: 800;
}
/******************************************************************************/
/* specific elements */

#pma_navigation div.pageselector {
    text-align: center;
    margin: 0;
    margin-left: 0.75em;
    border-left: 1px solid #999999;
}
/* Navigation tree*/

#pma_navigation_content,
#pma_navigation_header {
    background-color: #333;
}
#pma_navigation_tree {
    margin: 0;
    margin-left: 5px;
    overflow: hidden;
    color: #bbbbbb;
    background-color: #333;
    height: 74%;
    position: relative;
}
#pma_navigation_db_select select {
    -webkit-border-radius: 2px;
    border-radius: 2px;
    border: 1px solid #444444;
    border-top: 1px solid #444444;
    color: #cccccc;
    padding: 4px 6px;
    margin: 0 0 0;
    width: 92%;
    font-size: 1.11em;
}
#pma_navigation_tree a {
    color: #ffffff;
}
#pma_navigation_tree li.activePointer {
    color: #ffffff;
    background-color: #222222;
}
#pma_navigation_tree li.selected {
    color: #ffffff;
    background-color: #222222;
}
#pma_navigation_tree div.block i {
    /* Top and right segments for the tree element connections */
    
    display: block;
    border-left: 1px solid #999999;
    border-bottom: 1px solid #999999;
    position: relative;
    z-index: 0;
}
#pma_navigation_tree div.block b {
    /* Bottom segment for the tree element connections */
    
    display: block;
    height: 0.75em;
    bottom: 0;
    left: 0.75em;
    border-left: 1px solid #999999;
}
#pma_navigation_tree .list_container {
    border-left: 1px solid #999999;
    margin-left: 0.75em;
    padding-left: 0.75em;
}
#pma_navigation_tree .last > .list_container {
    border-left: 0 solid #999999;
}
/* Fast filter */

li.fast_filter {
    padding-left: 0.75em;
    margin-left: 0.75em;
    padding-right: 35px;
    border-left: 1px solid #999999;
    list-style: none;
}
li.fast_filter span {
    position: relative;
    right: 1.5em;
    padding: 0.2em;
    cursor: pointer;
    font-weight: bold;
    color: #77ffff;
    font-size: 0.7em;
}
/* Resize handler */

#pma_navigation_resizer {
    width: 3px;
    height: 100%;
    background-color: #555555;
    cursor: col-resize;
    position: fixed;
    top: 0;
    left: 240px;
    z-index: 801;
}
#pma_navigation_collapser {
    width: 20px;
    height: 22px;
    line-height: 22px;
    background: #111111;
    color: #aaaaaa;
    font-weight: bold;
    position: fixed;
    top: 0;
    left: 240px;
    text-align: center;
    cursor: pointer;
    z-index: 800;
    text-shadow: 0 1px 0 #000000;
    filter: dropshadow(color=#000000, offx=0, offy=1);
    border: 1px solid #777777;
}
.pma_quick_warp .drop_button {
    padding: 0 .3em;
    border: 1px solid #222222;
    border-radius: .3em;
    background: #0d0d0d;
    cursor: pointer;
}
.pma_quick_warp .drop_list:hover .drop_button {
    background: #000000;
}
.pma_quick_warp .drop_list ul {
    position: absolute;
    margin: 0;
    padding: 0;
    overflow: hidden;
    overflow-y: auto;
    list-style: none;
    background: #000000;
    border: 1px solid #222222;
    border-radius: .3em;
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
    box-shadow: 0 0 5px #333333;
    top: 100%;
    left: 3px;
    right: 0;
    display: none;
    z-index: 802;
}
.pma_quick_warp .drop_list li:hover {
    background: #0d0d0d;
}
