/* ==UserStyle==
@name           Electronics Tutorials Dark
@namespace      github.com/SicariusNoctis
@version        0.1.0
@description    Dark Style for Electronics Tutorials
@author         Mateen Ulhaq
==/UserStyle== */

@-moz-document domain("electronics-tutorials.ws") {
    img {
        background: #fff;
    }
    
    .top-bar-1-wrap {
        background: #070707;
        border-bottom: 1px solid #070707;
    }
    
    .body-cont {
        background: #070707;
        color: #777;
    }
    
    .single-page .single-wrap .content-cont {
        background: #171717;
        color: #aaa;
    }
    
    ul.pointslist li.sqr {
        color: #aaa;
    }
    
    .ntxt {
        color: #eee;
    }
}
