style.css:
@-moz-document domain("travis-ci.org"), domain("travis-ci.com") {
  
  code,
  pre {
    background-color: #404040;
    color: #F0F0F0;
  }

  .index-intro,
  .index-main h2,
  .sidebar h2,
  .sidebar h3 {
    border-bottom-color: #303030;
  }
  
  p a {
    border-bottom-color: #808080;
    color: #808080;
  }
  
  p a:hover,
  p a:active {
    color: #A0A0A0;
  }

  .sidebar {
    background-color: #202020;
  }

  .sidebar-notice {
    border-top-color: #303030;
  }
}
