style.css:
@-moz-document domain("app.liquidplanner.com") {
html,
body {
    font-family: "Segoe UI";
}

@font-face {
    font-family: 'FontAwesome';
    src: url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?v=4.7.0');
    src: url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?#iefix&v=4.7.0') format('embedded-opentype'), url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.woff2?v=4.7.0') format('woff2'), url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.woff?v=4.7.0') format('woff'), url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.ttf?v=4.7.0') format('truetype'), url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/fonts/fontawesome-webfont.svg?v=4.7.0#fontawesomeregular') format('svg');
    font-weight: normal;
    font-style: normal;
}

/* Buttons */
.ui-button-text {
    border: 0px !important;
    border-radius: 0px !important;
    background: transparent !important;
    color: white !important;
}

/* Action Button */
button.ui-button,
label.ui-button,
button.ui-button.ui-widget.ui-state-default.ui-corner-all {
    border-radius: 0px;
    border: 0px;
    padding: 8px 20px;
    background: rgb(66, 159, 133);
    color: white;
    margin: 0px;
}

.ui-button-action {
    animation: action 2s infinite ease-in-out; /* animation-name followed by duration in seconds*/
         /* you could also use milliseconds (ms) or something like 2.5s */
	-webkit-animation: action 2s infinite ease-in-out; /* Chrome and Safari */
}

@keyframes action {
	0%   {background: #337a66;}
	50%  {background: rgb(79, 187, 157);}
	100% {background: #337a66;}
}

@-webkit-keyframes action { /* Safari and Chrome - necessary duplicate */ 
	0%   {background: #337a66;}
	50%  {background: rgb(79, 187, 157);}
	100% {background: #337a66;}
}


button.eblue.ui-button-action.ui-button-default.ui-button.ui-widget.ui-state-default.ui-corner-all.ui-button-text-only.midnight:hover,
label.ui-button,
button.ui-button.ui-widget.ui-state-default.ui-corner-all:hover {
    background: rgb(79, 187, 157);
}


.ui-widget.lp-dialog button.ui-state-default.ui-widget.ui-button.eblue span,
button.eblue,
a.button.eblue,
a.plain_button_round.eblue {
    color: white;
    border: 0px solid #1690c6;
    background: transparent;
}


button.eblue.disabled, a.button.disabled {
    opacity:1;
    background:#3a3c45 !important;
}

button.eblue.disabled span.ui-button-text, a.button.disabled span.ui-button-text {
    color:hsla(229, 9%, 45%, 1) !important;
}

/* Cancel Button */
button.cancel.ui-button-action.ui-button.ui-widget.ui-state-default.ui-corner-all.ui-button-text-only.midnight,
button.ui-button-dismiss.ui-button.ui-widget.ui-state-default.ui-corner-all.ui-button-text-only.midnight {
    background: hsl(230, 8%, 45%);
    float:left;
    margin-right: 1px;
}

button.cancel.ui-button-action.ui-button.ui-widget.ui-state-default.ui-corner-all.ui-button-text-only.midnight:hover,
button.ui-button-dismiss.ui-button.ui-widget.ui-state-default.ui-corner-all.ui-button-text-only.midnight:hover {
    background: hsl(230, 8%, 35%);
}

.segmented_control .segment {
    border: 0px solid transparent;
    border-radius: 0px;
    display: inline-block;
    margin: 0px;
    padding: 15px 20px;
    background: #3a3c45;
    color: white;
}

.segmented_control .segment.selected {
    background: rgba(66, 130, 183, 1);
    border-color: transparent;
    color: white;
}

.segmented_control .segment:hover {
    background: #f4f4f4;
    color: black;
}

a.help_guide {
    padding: 15px 20px;
    position: relative;
    top: 14px;
    background: #3a3c45;
    color: white;
}

a._picker_control.picker_control.text,
a._picker_control.picker_control,
div._options.options.link a {
    color: white;
    background: #4282b7;
    padding: 15px !important;
    position: relative;
    right: -1px;
}

div._options.options.link a {
    margin-left: -4px;
    margin-right: -3px;
}

a._picker_control.picker_control.text:hover,
a._picker_control.picker_control:hover,
div._options.options.link a:hover {
    background: rgba(79, 155, 216, 1);
}




/* Main Header */
#main_header {
    background: rgba(32, 33, 38, 1) !important;
    padding: 0px !important;
    vertical-align: top !important;
}

#main_header td.logo {
    width: 50px;
    padding-right: 0px;
}

#main_header td.logo img {
    filter: sepia(1) saturate(0.75) hue-rotate(155deg);
    height: 49px;
}

#main_header td.logo img:hover {
    filter: sepia(1) saturate(0.75) hue-rotate(155deg) brightness(0.5);
}

.avatar_wrapper.medium-small {
    width: 50px;
    height: 50px;
    margin-top: 0px !important;
}

#main_ribbon .rounded_button,
#main_header .tabs li {
    border-radius: 0px;
    height: 22px;
}

.lp_ribbon .rounded_button.selected,
#main_header .tabs li.selected a,
#search_ribbon .rounded_button.selected,
#main_ribbon .rounded_button.selected {
    background: rgba(65, 130, 183, 1);
}

#main_header .navigation_links li:hover {
    background:transparent;
}
#main_header .navigation_links li:hover > a {
    background: rgba(57, 60, 69, 1);
}

#main_header .navigation_links li.selected:hover > a {
    background: #4f9bd8;
}

#main_header .navigation_links li a {
    padding: 15px;
    display: inline-block;
}

#main_header .navigation_links li + li {
    margin-left: 0px;
}

#main_header .for_space td.nav,
#main_header .for_portal td.nav {
    padding: 0px;
    vertical-align: top;
}

#main_header .avatar span {
    display: none;
}

label.input_group_submit.l_inline {
    margin-right: 15px;
}

.appcues-widget {
    margin-right: 10px !important;
}



/* Search Workspace */

#main_header form.header_search {
    margin-right: 2px;
}

.input_group_submit {
    margin:0px !important;
    padding:0px !important;
    vertical-align:top;
}

#main_header .header_search input.expanding_field {
    padding-right: 25px !important;
}

#main_header .header_search input.expanding_field:focus {
    padding: 15px 16px !important;
    position: relative;
    top: -1px;
}

#main_header .header_search img.icon_search {
    position: relative;
    right: 40px !important;
}

/* Schedule Status Button */

#main_header .header_capsule {
    border-radius: 0px;
    padding:0px;
    text-align: left;
    color: white;
    position: relative;
    top: 0px;
}

#lp_scheduler_message {
    background:transparent;
}

#lp_scheduler_message a {
    padding: 17px 19px 17px;
}

#lp_scheduler_message.running {
    padding: 14px 20px 12px;
    position:relative;
    top:-2px;
}

#lp_scheduler_message.done a {
    background-color: #4dac64;
}

#lp_scheduler_message.running {
    background-color: #b47e30;
}

#lp_scheduler_message.done img {
    filter:brightness(1.1);
}





/* Avatar Menu */
.avatar_menu {
    color: #ffffff;
    margin: 0px !important;
    border-radius: 0px;
}

.avatar_menu .header {
    padding: 0px;
}

.avatar_menu .user_name {
    font-size: 18px;
    padding: 15px !important;
}

.avatar_menu .header > div {
    padding: 6px 0;
}

.avatar_menu .section_header {
    font-weight: bold;
    font-size: 14px;
    padding: 15px;
    color: gray;
}

.avatar_menu .content {
    border-top: 0px;
    padding: 0px;
}

.personal_links a,
.avatar_menu .content > a {
    background: #3a3c45;
    color: white;
    text-decoration: none;
    padding: 10px 15px;
}

.personal_links a:hover,
.avatar_menu .content > a:hover {
    background: rgba(66, 69, 79, 1);
    color: white;
    text-decoration: none;
}

span.divider {
    display: none;
}

.header_person_picker a .avatar_border {
    padding:0px;
}




/* Secondary Header */
.bar.bar_header,
.subnav {
    background: rgb(39, 42, 48) !important;
    border-bottom: 0px;
}

.subnav a,
a.button.thin_white {
    font-size: 14px;
    margin-top: 0px;
    margin-right: 1px;
    margin-left: 0px;
    padding: 9px 20px;
    border: 0px;
    border-radius: 0px;
    color: white;
}

.subnav a:hover,
a.button.thin_white {
    text-decoration: none;
    background: rgb(66, 69, 79);
}

.subnav a.selected {
    background: #4182b7;
    color: #ffffff;
}

.subnav a.selected:hover {
    background: rgb(79, 155, 216);
    color: #ffffff;
}





/* Ribbon */
.l_inline > *,
.l_inline_wide > * {
    margin-right: 0px;
}

body#projects.space #main_ribbon,
body.panel.fullscreen #main_ribbon,
body#search #search_ribbon {
    background: #282a30;
    color: white;
}

#projects_ribbon,
#projects_ribbon.with_icon_sidebar {
    padding: 0px 0px;
    height: 49px;
    overflow: hidden;
    background: #282a31;
    border-bottom: 1px solid #282a31;
}

#projects_ribbon .button_bar + .button_bar {
    margin-left: 0px;
}

/* Switch-Mode Button */
#projects_ribbon #switch_mode {
    width: 53px;
    padding: 15px 23px 12px 23px;
    position: relative;
    top: -1px;
    margin-right: -2px;
}

#main_header a .arrow {
    font-size: 1em;
    float: none;
    display: inline-block;
    position: fixed;
    margin-left: 4px;
}

.bar.bar_filter {
    display: inline-block;
    vertical-align: middle;
    padding: 0px 0px;
    border: 0px solid #dedede;
    background: transparent;
    border-radius: 0px;
    white-space: normal;
    overflow: hidden;
}

.pill_filter {
    padding: 0px !important;
    margin: 0px -1px;
    height: auto;
    line-height: 25px;
    border-radius: 0px;
    background: transparent;
    color: white;
    position: relative;
    top: -0px;
}

.pill_label._launch,
a.pill_filter.is_persistent {
    text-decoration: none;
    padding: 12px 20px !important;
    position: relative;
    top: -1px;
    background: rgba(57, 60, 69, 1);
}

.pill_label._launch:hover,
a.pill_filter.is_persistent:hover {
    background: rgba(66, 69, 79, 1);
}

.pill_filter.is_active {
    background: transparent;
}

.pill_filter.is_active > .pill_clear {
    padding-right: 11px;
    background: #4d505c;
    color: white !important;
    opacity: 1;
    padding: 12px 20px;
    position: relative;
    top: -1px;
    margin-left: 1px;
}

a.pill_filter.is_persistent {
    padding-top: 10px !important;
    padding-bottom: 9px !important;
    position: relative;
    top: 0px;
}

a.pill.pill_filter.is_persistent {
    padding: 11px 21px 10px 20px !important;
    top: -1.2px;
    margin-left: -3px;
    border-left: 1px solid #282a31;
    background: rgba(180, 56, 48, 1);
    opacity: 1;
}

.pill_filter.is_active > .pill_clear:hover, a.pill.pill_filter.is_persistent:hover {
    background: rgb(218, 74, 65)
}

#search_ribbon .rounded_button {
    border-radius: 0px;
    padding: 16px;
    position: relative;
    top: 0px;
}

/* Filter Items Icon */

._filter_bar:first-child > a {
    width: 10px;
    height: 28px;
    margin-right: -2px;
    padding-bottom: 10px !important;
    padding-top: 13px !important;
    display: inline-block;
}

._filter_bar:first-child > a img {
    transform: scale(0.7);
    position: relative;
    left: -6px;
}


/* New Task Dropdown Specifically */
#projects > div[style="display: block; position: absolute; top: 0px; left: 42px; margin: 12px; visibility: visible; z-index: 1000;"],
#projects > div[style="display: block; position: absolute; top: 0px; left: 55px; margin: 12px; visibility: visible; z-index: 1000;"],
#projects > div[style="display: block; position: absolute; top: 0px; left: 41.5px; margin: 12px; visibility: visible; z-index: 1000;"] {
    position: relative !important;
    width: 239px;
    top: -1px !important;
    left: 38px !important;
}

.ui-widget-header {
    color: #fff;
}



.popup .ui-dialog-titlebar {
    border-bottom: 0px solid #ddd;
    padding-bottom: 0px;
}

.rounded_gray_border,
.baseline_snapshot_dlg .search_input,
div.lp-tree-picker div.lp-search input[type=text],
div#filter_search input[type=text],
input[type=text] {
    width: 100%;
    border: 0px !important;
    border-radius: 0px !important;
    padding: 8px 8px !important;
    text-indent: 0px;
}

svg.arrow {
    display: none !important;
    visibility: hidden;
}


/* Modal */
.lp-dialog {
    background: #282a30;
    border: 1px solid #51667f !important;
    border-radius: 0px;
    box-shadow: 0 10px 4px #0000006b;
}

.lp-dialog.modal.midnight {
    padding: 2em;
}

.lp-dialog .ui-dialog-titlebar {
    font-size: 26px;
    padding:0em 0px 1em 0em !important;
    text-overflow: ellipsis;
    border-radius: 0px;
    border-bottom: 1px solid #373840 !important;
}

.lp-dialog div.lp-dialog-message {
    margin: 2em 0em;
    font-size:1.2em;
}

.lp-dialog .footer {
    white-space: nowrap;
    padding: 0px;
    border: 0px;
}

/* .lp-dialog.ui-widget.ui-widget-content.ui-corner-all.no_scroll.l_min_s.l_max_s.popup.modal.midnight {
    margin: 0px !important;
    padding: 0px !important;
    border-radius: 0px !important;
} */

.lp-dialog .ui-widget-content {
    border: 0px solid #ddd;
}


/* Spinner */
.wait .spinner {
    background: rgba(32, 33, 39, 0.92);
    border: 1px solid hsla(231, 8%, 55%, 1);
    border-radius: 6px;
    box-shadow: 0px 20px 20px #0000006b;
    padding: 40px 60px 50px;
    text-align:center;
}

.wait>div>img {
    display:none;
    background: transparent !important;
}

.wait>div:before {
    font-family: 'FontAwesome';
    content: "\f1ce";
    font-size: 64px;
    color: #c8def0;
    display: inline-block;
    padding: 35px 35px;
    -webkit-animation: rotation 2s infinite ease-in-out;
}

.wait>div:after {
    content: "Processing";
    font-size: 24px;
    color: #c8def0;
    display: inline-block;
}

@-webkit-keyframes rotation {
    from {
        -webkit-transform: rotate(0deg);
    }
    to {
        -webkit-transform: rotate(359deg);
    }
}


 


/* UI Menu */
.ui-menu {
    height: 100%;
    padding: 0px;
    background: #3a3c45;
}

.ui-menu > .ui-menu-item:not(.ui-state-disabled) a:hover {
    background: rgb(66, 69, 79) !important;
}

ul.ui-menu > li.ui-menu-divider > hr {
    border: 0;
    border-bottom: 1px solid #282a30;
    padding: 0;
    margin: 5px 0;
}

.context-menu > ul.ui-menu li.ui-menu-item a {
    color: hsl(190, 15%, 84%);
}

div.lp-viewport ul li.ui-menu-item a,
li.ui-menu-item a {
    border-bottom: 0px;
}

li.ui-menu-item a {
    background: #3a3c45;
    padding: 3px !important;
}

.ui-menu .ui-menu-item a.ui-state-focus,
.ui-menu .ui-menu-item a.ui-state-active,
.ui-menu .ui-menu-item.ui-state-focus,
.ui-menu .ui-menu-item.ui-state-active {
    background:#4282b7;
}

.ui-menu .ui-menu-item a.ui-state-focus .real_member,
.ui-menu .ui-menu-item a.ui-state-active .real_member,
.ui-menu .ui-menu-item.ui-state-focus .real_member,
.ui-menu .ui-menu-item.ui-state-active .real_member {
    color:white !important;
}



div.lp-viewport ul.ui-menu,
li.ui-menu {
    background: #282a30;
}

div.lp-viewport ul li.ui-menu-item a.checked {
    background-color: rgba(219, 124, 49, 1) !important;
}

div.lp-viewport ul li.ui-menu-item a.checked span span {
    color: rgb(255, 255, 255) !important;
}

._picker,
.lp-viewport.ui-widget.ui-widget-content {
    height: 350px !important;
    width: 470px;
    position: relative;
    left: 0px;
    margin-bottom: 0px;
}

.picker_dialog {
    margin: 0px;
}

.real_member {
    color: #c5cbcf !important;
}

div.lp-viewport ul li span {
    color: #fff;
}

.unassigned {
    color: rgba(218, 74, 65, 1) !important;
}

.virtual_member {
    color: rgba(79, 178, 157, 1) !important;
}

input[type="checkbox"] {
    margin: 0px 5px !important;
    position: relative;
    top: 2px;
}

.avatar_wrapper img.avatar {
    border-radius: 0px;
}

div.lp-viewport ul li a span .avatar_wrapper {
    top: 0px;
}

.avatar_wrapper.small {
    width: 46px;
    height: 46px;
}

.picker_dialog .lp-tree-picker .search_and_save {
    width: 485px;
}

.picker_dialog .lp-tree-picker .selection_bar.lp-search {
    padding: 0px !important;
}

.picker_dialog .lp-tree-picker .search_and_save .save {
    position: relative;
    left: -20px;
}



.picker_dialog .lp-tree-picker .selection_bar.lp-search .saved_filter:not(.l_hide),
.picker_dialog .lp-tree-picker .selection_bar.lp-search .selected .select:not(.exploded) {
    background: rgba(219, 124, 49, 1);
    border-radius: 0px;
    color: white;
    display: inline-block;
    text-indent: 0;
    margin: 0px;
    padding: 6px 8px 8px;
}

input.init_focus {
    max-width: 84px !important;
    padding: 11px 20px !important;
}

/* Add Item Submenu */
.context-menu>ul.ui-menu li.ui-menu-item a,
.command-menu>ul.ui-menu li.ui-menu-item a {
    color:white;
}

.context-menu>ul.ui-menu li.ui-menu-item a img,
.command-menu>ul.ui-menu li.ui-menu-item a img {
    filter: grayscale(1) brightness(2)
}



/* Action Sheet */
.lp-dialog.action_sheet,
.ui-widget-content.action_sheet {
    min-width: 150px;
    background: #3a3c45;
    /*     top: 97.23px !important;
    left: 50px !important; */
    margin: 0px !important;
}

.action_sheet .row {
    cursor: pointer;
    clear: both;
    white-space: nowrap;
    font-weight: normal;
    font-size: 14px;
    color: #ffffff;
}

.action_sheet .content {
    padding: 0px 0;
    overflow-y: auto;
    overflow-x: hidden;
    border-radius: 0px;
}

.action_sheet .row:hover,
.action_sheet .row.selected {
    background-color: #ffffff;
    color: black !important;
}





/* All Projects */

.base_filter_menu .row,
.base_filter_menu .row:hover,
.base_filter_menu .row.selected {
    background-color: transparent;
}

.base_filter_option {
    background-color: rgb(58, 60, 69);
    border-radius: 0px;
    padding: 7px 15px !important;
    margin: 0px;
    border: 0px;
    color: white;
}

.base_filter_option:hover {
    background-color: rgb(66, 69, 79);
}

.base_filter_option.selected {
    background-color: rgba(219, 124, 49, 1);
}

.base_filter_option.selected:hover {
    background-color: rgb(233, 153, 45);
}





/* Tooltip */
body .ui-tooltip {
    padding: 6px 15px;
    position: absolute;
    z-index: 9999;
    max-width: 100%;
    color: #353535;
    background: rgba(237, 239, 245, 1);
    border-width: 1px;
    box-shadow: 0px 4px 4px #00000040;
    pointer-events: none;
    border-radius: 0px;
    border: 0px;
}





/* Sidebar */

.icon_sidebar {
    position: fixed;
    top: 0px;
    bottom: 0;
    left: 0;
    width: 50px;
    color: #d6d6d6;
    background-color: rgba(40, 42, 48, 1);
}

.icon_sidebar ul {
    margin: 0px;
    padding-top: 50px;
}

.icon_sidebar ul li {
    padding: 0px;
    display: inline-grid;
}

.icon_sidebar ul li a {
    width: 50px;
    height: 49px;
    left: -1px;
    display: inline-block;
    background: rgb(57, 60, 69);
    color: white;
    margin-bottom: 1px;
}

.icon_sidebar ul li a:hover {
    background: rgb(66, 69, 79);
}

.icon_sidebar ul li a img {
    margin-top:12px;
}

.icon_sidebar ul li.selected a {
    background: #edeff5;
}

.icon_sidebar ul:first-child {
    padding-top:0px;
}

.icon_sidebar #add_button {
    width:49px;
    border-right:1px solid #282a31;
}

.icon_sidebar #add_button:hover {
    background:#4dac64 !important;
}

.icon_sidebar ul.bottom {
    position: absolute;
    bottom: 0;
    margin-bottom: 0px;
}

.icon_sidebar ul.bottom a:hover {
    animation: warning 1s; /* animation-name followed by duration in seconds*/
         /* you could also use milliseconds (ms) or something like 2.5s */
	-webkit-animation: warning 1s infinite; /* Chrome and Safari */
}

@keyframes warning {
	0%   {background: #b43830;}
	75%  {background: #ff4337;}
	100% {background: #b43830;}
}

@-webkit-keyframes warning { /* Safari and Chrome - necessary duplicate */ 
	0%   {background: #b43830;}
	75%  {background: #ff4337;}
	100% {background: #b43830;}
}

/* Grid */

/* GRID: Headers */
#projects_split_view {
    margin-top: 100px !important;
    background: rgb(66, 69, 79);
}

div.grid .root {
    height: 50px;
}

