@-moz-document domain("nos.nl") {
  body,
  body.homepage #most_viewed_videos,
  #menu-nieuws,
  #menu-sport,
  #menu-uitzendingen,
  #menu-verkeer,
  #menu-weer,
  .section-sub,
  .liveblog-footer {
    background-color: #15232D;
  }

  .subsites__site > div a,
  .subsites__site > div a {
    color: #fff;
  }


  body.homepage #nieuws_in_beeld,
  [class*="textBox_"],
  .preSlider {
    background-color: #0c151c; 
  }

  .nav,
  #footer,
  .announcements__item,
  .block_textbox,
  .subsites__site > div {
    background-color: #193549;
  }

  #footer a,
  #footer a:hover,
  .footer__title,
  .list-roads__title--sub,
  .nav a,
  .nav a:hover,
  .nav [class^='icon-'],
  .nav [class*=' icon-']{
    color: #fff;
  }

  body,
  a,
  a:hover,
  .internallink__link,
  .externallink__link,
  .link-grey,
  .article .article_block a,
  .article .article_block div,
  [class^='icon-'],
  [class*=' icon-'],
  .list-index>.list-item a,
  .list-index>li a,
  [class*="externalContent_"] *,
  [class*="alsoSee_"] *,
  [class*="shareText_"],
  [class*="supplyChannel_"]{
    color: #d8d8d8;
  }

  a:hover .link-hover, a:hover .footer__link, a:hover .list-items__link:hover .list-items__link-hover, .list-items__link:hover a:hover .list-items__link-hover, a:hover .topstorylist__link, a.link-hover:hover, a.footer__link:hover, .list-items__link:hover a.list-items__link-hover:hover, a.topstorylist__link:hover, .link:hover .link-hover, .link-noline:hover .link-hover, .liveblog__elements a:hover .link-hover, .liveblog-header__summary a:hover .link-hover, .link:hover .footer__link, .link-noline:hover .footer__link, .liveblog__elements a:hover .footer__link, .liveblog-header__summary a:hover .footer__link, .link:hover .list-items__link:hover .list-items__link-hover, .list-items__link:hover .link:hover .list-items__link-hover, .link-noline:hover .list-items__link:hover .list-items__link-hover, .list-items__link:hover .link-noline:hover .list-items__link-hover, .liveblog__elements a:hover .list-items__link:hover .list-items__link-hover, .list-items__link:hover .liveblog__elements a:hover .list-items__link-hover, .liveblog-header__summary a:hover .list-items__link:hover .list-items__link-hover, .list-items__link:hover .liveblog-header__summary a:hover .list-items__link-hover, .link:hover .topstorylist__link, .link-noline:hover .topstorylist__link, .liveblog__elements a:hover .topstorylist__link, .liveblog-header__summary a:hover .topstorylist__link, .link.link-hover:hover, .link-hover.link-noline:hover, .link-noline.footer__link:hover, .list-items__link:hover .link-noline.list-items__link-hover:hover, .link-noline.topstorylist__link:hover, .liveblog__elements a.link-hover:hover, .liveblog__elements a.footer__link:hover, .liveblog__elements .list-items__link:hover a.list-items__link-hover:hover, .list-items__link:hover .liveblog__elements a.list-items__link-hover:hover, .liveblog__elements a.topstorylist__link:hover, .liveblog-header__summary a.link-hover:hover, .liveblog-header__summary a.footer__link:hover, .liveblog-header__summary .list-items__link:hover a.list-items__link-hover:hover, .list-items__link:hover .liveblog-header__summary a.list-items__link-hover:hover, .liveblog-header__summary a.topstorylist__link:hover, .link.footer__link:hover, .list-items__link:hover .link.list-items__link-hover:hover, .link.topstorylist__link:hover, .link-red, .football-scoreboard__more, .nav-aside__link:hover, .nav-aside__subitem--active>.nav-aside__link, .nav-aside__item--active>.nav-aside__link {
    color: #FF5465;
  }

  .slider-playlist--light .rsNavSelected,
  .nav__iconlink:hover::after,
  .list-index .list-index-active a {
    color: #FF5465;
    border-color: #FF5465;
  }

  .badge__icon,
  .nav__active::after,
  .nav__iconlink:hover::after,
  .slider-chromeless-arrows .rsArrow,
  .slider-chromeless-arrows-video .rsArrow,
  .img-icon-play {
    background-color: #FF5465;
  }

  .nav__category::after,
  .nav__category:hover::after,
  .search-form__submit,
  .slider-chromeless-arrows .rsArrow:hover,
  .slider-chromeless-arrows-video .rsArrow:hover,
  .nav-aside__link:hover:before,
  .nav-aside__subitem--active>.nav-aside__link:before,
  .nav-aside__item--active>.nav-aside__link:before {
    background-color: #e24a59;
  }

  .link-red:hover {
    color: #e24a59;
  }

  #newsletter-signup {
    display: none;
  }

  .slider-playlist .rsThumbsArrow,
  .slider-playlist--light .rsThumbsArrow:hover {
    background-color: rgba(25, 53, 73, .9);
  }

  .nav,
  #footer,
  #menu-nieuws,
  #menu-sport,
  #menu-uitzendingen,
  #menu-verkeer,
  #menu-weer,
  .football_scoreboard *,
  #latest_broadcasts *,
  .nav-aside__item,
  .announcements__wrapper,
  .bio,
  .article_head_logo-wrap,
  .block_textbox,
  .divide-top,
  .tabs-index,
  .broadcast-programs__title,
  .footer__column,
  .category-list__title,
  .subsites__site > div,
  .slider-playlist--light .rsNavItem,
  .block_embed .embed_title_wrap,
  .block_links,
  .ext-twitter,
  .embed_type-iframe,
  .subnav__head,
  .subnav__aside,
  .media_caption,
  .liveblog__last__content,
  .section-sub,
  .liveblog,
  .block_table {
    border-color: #3B5364;
  }

  .nav__category::before,
  .nav__item--spaced-right::before,
  .nav__item--spaced-left .nav__iconlink::before {
    background-color: #3B5364;
  }

  .slider-playlist--light .rsThumbsArrow:hover .rsThumbsArrowIcn,
  .ext-youtube .embed_title,
  .ext-twitter-caption,
  .ext-twitter-date,
  .ext-twitter-header-author__decorate-name,
  .ext-twitter-header .ext-twitter-header-author__decorate-name {
    color: #d8d8d8; 
  }

  .logo-nieuwsuur {
    background-image: url(http://weblogs.nos.nl/wp-content/themes/nieuwsuur/img/nieuwsuurlogo.png);
  }

  .article table tbody tr:nth-child(2n-1) td,
  .liveblog table tbody tr:nth-child(2n-1) td {
     background-color: #193549;
  }

  .article table thead th, .article table thead td,
  .liveblog table thead th, .liveblog table thead td {
    background-color: #3B5364;
    color: #d8d8d8;
  }

  .article_body aside {
    background-color: #193549;
    border-color: #3B5364;
  }
}
