}
.stats-mainbar .task-stat-table td {
    background-color: var(--widget-bg-color) !important;
    border: 5px solid var(--content-bg-color);
}
.help-center .help-category-box h3 {
    border-bottom: 1px solid var(--border-color) !important;
}
.page-numbers {
