}

/* Questions */
.envelope-on, .envelope-off, .vote-up-off, .vote-up-on, .vote-down-off, .vote-down-on, .star-on, .star-off, .feed-icon, .vote-accepted-off, .vote-accepted-on, .vote-accepted-bounty, .badge-earned-check, .delete-tag, .grippie, .expander-arrow-hide, .expander-arrow-show, .expander-arrow-small-hide, .expander-arrow-small-show, .anonymous-gravatar, .badge1, .badge2, .badge3 {
    background-image: var(--sprites),none;
}
#question-header .question-hyperlink {
    color: var(--h1-h2-color) !important;
}
.subheader h1 {
    color: var(--h1-h2-color) !important;
}
.subheader h2 {
    color: var(--h1-h2-color) !important;
}
.subheader {
    border-bottom: 1px solid var(--border-color) !important;
}
#sidebar h4 {
    color: var(--h4-color) !important;
}
.module h4 {
    color: var(--h4-color) !important;
}
.question-status {
    background-color: var(--bulletin-bg-color) !important;
    border: 1px solid rgba(83,90,96,0.1) !important;
}
.answer {
    background-color: var(--content-bg-color) !important;
}
.question {
