}
.label-key b, .label-key strong {
    color: var(--text-color);
    font-weight: normal;
}
#tabs a, .tabs a {
    background-color: var(--content-bg-color);
    color: var(--h4-color);
}
#tabs a:hover, .tabs a:hover {
    background-color: var(--widget-bg-color);
    color: var(--text-color);
}
#tabs a.youarehere, .tabs a.youarehere {
    background-color: var(--content-bg-color);
    border-color: var(--border-color);
    border-bottom-color: transparent;
    color: var(--text-color);
}
#tabs a.youarehere:hover, .tabs a.youarehere:hover{
    background-color: var(--content-bg-color);
    border-color: var(--border-color);
    border-bottom-color: transparent;
    color: var(--h1-h2-color);
}
.f-label, label.f-label {
    color: var(--h4-color);
}
a.bounty-link {
    color: var(--h4-color);
}
kbd {
    color: #000;
    text-shadow: 0 1px 0 #bbb8b8;
    box-shadow: 0 1px 0 rgba(12,13,14,0.2), 0 0 0 2px #c4c0c0 inset;
    background-color: #b5b9bb;
    border: 1px solid #494c4f;
}
pre, code {
    background-color: #303030;
}
.pln {
    color: #d9d6d2;
}
.pun, .kwd, .dec {
    color: #ffe400;
}
.lit, .tag {
    color: #00bdff;
}
.str {
    color: #43c03e;
}
.com {
    color: #8d8885;
}
.typ {
    color: #00bdff;
}
.close-as-duplicate-pane .original-display .list-originals {
    border: 1px solid var(--border-color);
}
.close-as-duplicate-pane .original-display .navi-container {
    color: var(--text-color);
}
.close-as-duplicate-pane .original-display .list-originals .list .item .body-summary {
    color: var(--text-color);
}
.close-as-duplicate-pane .original-display .list-originals .list .item {
    border-bottom: 1px solid var(--border-color);
}
.close-as-duplicate-pane .original-display .list-originals .list .item:hover {
    background-color: var(--widget-bg-color);
}
.close-as-duplicate-pane .original-display .list-originals .list .item .stats {
    background: var(--widget-bg-color) !important;
}
.close-as-duplicate-pane .original-display .preview {
    border: 1px solid var(--border-color);
}
blockquote {
    background-color: #3c3726;
    border-left: 2px solid #938339;
}
.question-page #answers .answer {
    border-bottom: 1px solid var(--border-color);
}
.comments {
    border-top: 1px solid  var(--border-color);
}
ul.comments-list .comment>* {
    border-bottom: 1px solid  var(--border-color);
}
.comment-up-off, .comment-flag, .comment-flag-off {
    color: rgba(223, 239, 255, .15);
}
.popup {
    background-color: var(--content-bg-color) !important;
    border: solid 1px var(--border-color) !important;
}
h1 {
    color: var(--h1-h2-color);
}
.deleted-answer {
    background-color: #332b2b !important;
}
.revision td, .owner-revision td, .vote-revision td {
    background-color: #303030;
}
.revision-comment {
    color: #c8d1d9;
}
.diff-choices a {
    border: 1px solid #202020;
}
.diff-choices a.selected {
    border: 1px solid var(--border-color);
    background-color: #303030;
}
span.diff-add {
    color: #D1E1AD;
    background: #2b3c03;
}
span.diff-delete {
    background-color: #6f220d;
    color: #E5BDB2;
}
#revisions .markdown-diff .full-diff .inserted>div {
    background-color: #2a3111;
}
#revisions .markdown-diff .full-diff .deleted>div {
    background-color: #382828;
}
#revisions .markdown-diff .full-diff .skip {
    background-color: #353535;
    border-top: 4px solid var(--content-bg-color);
    border-bottom: 4px solid var(--content-bg-color);
}
#revisions .markdown-diff .full-diff td {
    border-left: 4px solid var(--content-bg-color);
    border-right: 4px solid var(--content-bg-color);
}
.tagged-interesting {
    background-color: #2d2b26;
}
.comment__highlight:not([style*="background-color"]) {
	background-color:#443910;
}
ul.comments-list .comment__highlight .comment-text {
	background-color:#443910;
}
body:not(.no-grid-comments) ul.comments-list .comment__highlight .comment-actions {
	background-color:#443910;
}
.action-list:not(.popup-condensed) .action-selected {
    background-color: var(--highlighted-bg-color) ;
}

/* Input */
textarea, textarea:not(.s-input) {
    color: var(--text-color) !important;
    background-color: var(--content-bg-color);
}
.grippie {
    border: 1px solid var(--border-color);
    border-top-width: 0px;
    background-color: var(--widget-bg-color);
}
.s-input, .s-textarea {
    border: 1px solid var(--border-color);
}
.wmd-button>span {
    background-image: url(https://svgshare.com/i/7uH.svg?v=50d4554b2dd3),none;
}
.wmd-preview {
    border-top: 1px dotted var(--border-color);
    border-bottom: 1px dotted var(--border-color);
}
.wmd-button-row {
    border-bottom: 1px solid var(--border-color);
}
.wmd-button-bar {
    border: 1px solid var(--border-color);
    border-bottom: 0 !important;
}
.wmd-button__active {
    border-left: 1px solid var(--border-color);
    border-right: 1px solid var(--border-color);
    border-bottom: 1px solid var(--content-bg-color);
    background-color: var(--content-bg-color) !important;
}
.s-input[disabled]{
    background-color: #191919 !important;
    color:  var(--border-color) !important;
    border-color: var(--border-color);
}
.wmd-inline-dialog {
    border: 1px solid var(--border-color);
    border-top: 0 !important;
    background-color: var(--content-bg-color) !important;
}
.bt {
    border-top: 1px solid var(--border-color) !important;
}
.s-label {
    color: var(--text-color) !important;
}
.s-btn__danger:hover, .s-btn__danger:focus {
    color: rgb(255, 35, 57) !important;
}
.s-btn:hover, .s-btn:focus {
    color: rgb(8, 159, 251);
}
input[type="text"]:not(.s-input), input[type="password"]:not(.s-input), input[type="number"]:not(.s-input), input[type="email"]:not(.s-input), input[type="url"]:not(.s-input), input[type="search"]:not(.s-input), input[type="tel"]:not(.s-input), input[type="datetime"]:not(.s-input), input[type="datetime-local"]:not(.s-input), input[type="date"]:not(.s-input), textarea:not(.s-input) {
    color: var(--text-color) ;
    background: var(--content-bg-color);
    border: 1px solid var(--border-color);
}

/* User page */
.user-show-new #user-tab-answers .answer-votes, .user-show-new .post-container .vote, .user-show-new .user-panel .mini-counts, .user-show-new .user-rep .rep-amount .rep-down, .user-show-new .user-rep .rep-amount .rep-up {
    border: 1px solid var(--border-color);
}
.user-show-new .user-rep .rep-amount .rep-up {
    border-color: var(--answered-bg-color);
}
.user-show-new #user-tab-answers .answer-votes.accepted, .user-show-new .post-container .vote.accepted, .user-show-new .user-panel .mini-counts.accepted, .user-show-new .user-rep .rep-amount .rep-down.accepted, .user-show-new .user-rep .rep-amount .rep-up.accepted, .user-show-new #user-tab-answers .answer-votes.answered-accepted, .user-show-new .post-container .vote.answered-accepted, .user-show-new .user-panel .mini-counts.answered-accepted, .user-show-new .user-rep .rep-amount .rep-down.answered-accepted, .user-show-new .user-rep .rep-amount .rep-up.answered-accepted, .user-show-new #user-tab-answers .answer-votes.special-rep, .user-show-new .post-container .vote.special-rep, .user-show-new .user-panel .mini-counts.special-rep, .user-show-new .user-rep .rep-amount .rep-down.special-rep, .user-show-new .user-rep .rep-amount .rep-up.special-rep {
    border-color: var(--answered-bg-color);
    background: var(--answered-bg-color);
    color: var(--answered-color);
}
.users-page .user-info:hover {
    background-color: var(--widget-bg-color);
}
.popup._badge-selection .popup-close .-link {
    background-color: var(--content-bg-color);
    color:  var(--h4-color) !important;
}
.popup._badge-selection .popup-close .-link:hover {
    background-color: var(--widget-bg-color);
    color:  var(--h1-h2-color) !important;
}
.popup._badge-selection .-list-item {
    background-color: var(--content-bg-color);
    border: 1px solid var(--border-color);
}
