.s-progress__silver .s-progress--label {
    border-color: #454d55;
    color: var(--text-color) !important;
}
.s-progress__silver .s-progress--bar {
    background-color: #464d55;
}
.s-progress__gold .s-progress--label {
    border-color: #483a00;
    color: var(--text-color) !important;
}
.s-progress__gold .s-progress--bar {
    background-color: #595338;
}
.popup._badge-selection .-list-item .-flag-recommend {
    background-color: var(--answered-bg-color) !important;
    color: var(--answered-color) !important;
}
div.meter {
    background-color: var(--bg-color) !important;
    border: 1px solid var(--border-color) !important;
}
div.meter div {
