style.css:
@-moz-document domain("www.albert.io") {
body {
	background: url(/*[[bg]]*/) fixed no-repeat;
	background-size: cover;
	color: #fff;
}
.global-nav,
.subheader,
.questions-list__questions,
.active-question-wrapper__question,
.active-question-header__nav-button,
.question-footer__label-item,
.tag-item,
.home-page-content__wrapper,
.home-page-hero,
.home-page-nav__wrapper,
.card,
.user-dashboard-item,
.user-dashboard-totals__flag,
.secondary-header,
.tertiary-header.subject-menu-tertiary-navigation-component.color-test-prep,
.tertiary-header.subject-menu-tertiary-navigation-component.color-foundations,
.tertiary-header.subject-menu-tertiary-navigation-component.color-humanities,
.tertiary-header.subject-menu-tertiary-navigation-component.color-math,
.tertiary-header.subject-menu-tertiary-navigation-component.color-professional,
.tertiary-header.subject-menu-tertiary-navigation-component.color-science,
.tertiary-header.subject-menu-tertiary-navigation-component.color-social-sciences,
.simple-dropdown__input-wrapper .simple-dropdown__input,
.subject-menu-grid__hero.color-test-prep,
.subject-menu-grid__hero.color-foundations,
.subject-menu-grid__hero.color-humanities,
.subject-menu-grid__hero.color-math,
.subject-menu-grid__hero.color-professional,
.subject-menu-grid__hero.color-science,
.subject-menu-grid__hero.color-social-sciences,
.subject-grid__card,
.questions-list-settings__toggle,
.questions-list-paginator,
.ie-breadcrumbs,
.subject-menu-search-view__list-item,
.subject-view-top-header,
.a-form-input__element,
.study-guide-cards-cta,
.study-guide__card,
.sg-tabs-nested-content,
.sg-table__tbody .sg-table__tr,
.sg-tab, .sg-tab.unbutton,
.free-response-question-guide__themes,
.free-response-question-sub-type-badge--free_response_question,
.subject-faq-list__wrapped-item,
.questions-list-settings__settings-form,
.sg-checkbox-toggle__toggle::before,
.user-dashboard-stats__stats-item-pie::after,
.student-topics-guides,
.card-list__item,
.tooltip-content,
.active-question-button, a.active-question-button, button.active-question-button,
.passage-supplement,
.passage-supplement .passage-toggle,
.active-question-wrapper__question pre,
.user-dashboard__item {
	background: rgba(0,0,0,/*[[opacity]]*/) !important;
	box-shadow: none;
	border: none;
}
.multiple-choice-question-options-option {
	background: rgba(0,0,0,.5) !important;
}
.question-list-item,
.multiple-question-set,
.active-question-wrapper__question tr:nth-child(n),
.active-question-wrapper__question th,
.homepage-links__wrapper,
.global-nav__flag,
.subject-menu-search-bar__search-bar,
.access-label,
.sg-table__thead,
.question-difficulty-label--easy,
.question-difficulty-label--moderate,
.question-difficulty-label--difficult,
.user-dashboard-stats__difficulty.easy,
.user-dashboard-stats__difficulty.moderate,
.user-dashboard-stats__difficulty.difficult,
.guide-table-row--subject,
.tooltip-wrapper, .tooltip-wrapper::before,
.question-response-stats__item-label,
.timer,
.audio-player__content-wrapper,
.free-entry-question-inputs {
	background: transparent;
	border: none;
}
.question-list-item__content,
.active-question-wrapper__question .MathJax,
.active-question-wrapper__question strong,
.footer-links__single-item,
.footer-links__list-item a,
.my-classroom__subjects,
.active-question-wrapper__question table,
.a-form-input__element,
.active-question-button, a.active-question-button, button.active-question-button,
.passage-content > p,
.passage-supplement .passage-toggle,
.active-question-wrapper__question pre {
	color: #fff;
}
.my-classroom__label,
.global-nav__flag,
.subject-menu__content-wrapper a,
.access-label__subscription,
.study-guide-theme__meta-description,
.study-guide-theme-topics-table__meta-description,
.study-guide-content,
.study-guide__card a,
.blocky-date-picker__input,
.free-response-question-guide,
.free-response-question-guide-item__title,
.subject-faq-list__wrapper,
.user-dashboard-stats__stats-item,
.user-dashboard-stats__stats-item-value,
.topic-list-heading,
.topic-list-row__accuracy,
.question-response-stats__item-percentage,
.question-response-stats__header {
	color: rgba(255,255,255,.75);
}
.interaction-engine-v2__button,
.question-list-item__difficulty,
.question-difficulty-label,
.progress-meter {
	opacity: .75;
}
.home-page-content__content-header,
.my-classroom__name, .view-archived-classrooms-card__text,
.user-dashboard-item__title,
.question-difficulty-label--easy,
.user-dashboard-stats__difficulty.easy {
	color: rgb(80, 171, 238);
}
.question-difficulty-label--difficult,
.user-dashboard-stats__difficulty.difficult {
	color: #ff4e8c;
}
.sg-table__thead--fixed-headings {
	color: transparent;
}
.button--green,
.button--blue, 
.flag--get-started, 
.button--blue-medium, 
.blocky-date-picker__input,
.access-label__subscription,
.global-nav__upgrade-subject-wrapper .upgrade-subject-button, .global-nav__upgrade-subject-wrapper .upgrade-subject-button__button,
.interaction-engine-v2__button,
.multiple-choice-question-options-option {
	background: transparent;
	border: rgba(255,255,255,.5) 1px solid !important;
}
a.global-nav__main-link--active {
	background: none;
	border-bottom:  rgb(80, 171, 238) 2px solid !important;
}
.global-nav__main a:hover,
.global-nav__dropdown-controller:hover {
	background:  rgb(80, 171, 238) !important;
	margin: 10px 0 10px 0;
	border-radius: 15px;
}
.multiple-choice-question-options-option-letter {
	background:  rgba(80, 171, 238,.3);
	color: #fff
}

/* hide  */
.interaction-engine__footer,
.footer {
	display: none;
}

/* loading */
.loading-indicator__circle {
	-webkit-animation-duration: .24s;
	animation-duration: .24s;
	-webkit-animation-iteration-count: infinite;
	animation-iteration-count: infinite;
	background-color: #fff;
	border-radius: .5em;
	height: 50px;
	margin: .25em;
	width: .5em;
}
.loading-indicator {
	font-size: 2rem;
}

/*[[albert]]*/
}

