style.css:
@-moz-document domain("goodgame.ru") {
.chat-container .chat-control-block .control-panel .donate-block:not(.icon-plus) {
	font-family: icomoon!important;
	vertical-align: baseline !important;
	font-size: 0 !important;
	font-weight: normal !important;
	background-color: transparent !important;
}
.chat-container .chat-control-block .control-panel .donate-block.icon-plus {
	background-color: transparent !important;
}
.chat-container .chat-control-block .control-panel .donate-block:hover {
	background-color: #222e46 !important;
}
.chat-container .chat-control-block .control-panel .donate-block:not(.icon-plus)::before {
	content: "\e60c";
	font-size: 12px;
}
}
