style.css:
@-moz-document url-prefix("https://twitter.com/") {
div.module.Trends.trends, div.Footer.module.roaming-module{
    visibility: hidden;
}

a.DashboardProfileCard-bg.u-bgUserColor.u-block{
    border-radius: 8px;
}

div.DashboardProfileCard.module, .module .flex-module, div.ProfileHeading-content, a.DashboardProfileCard-avatarLink.u-inlineBlock{
    background-color: transparent;
}

div.DashboardProfileCard.module, .module .flex-module, div.ProfileHeading-content, li#stream-item-tweet-1037445975680446474.js-stream-item.stream-item.stream-item.js-pinned, li#stream-item-tweet-1037445975680446474.js-stream-item.stream-item.stream-item, li#stream-item-tweet-1037468036817666048.js-stream-item.stream-item.stream-item{
    background-color: transparent;
}

.account-summary .dismiss, .account-group .username, .time, .time a, .created-via{
    color: rgb(155, 155, 155);
}

a.DashboardProfileCard-avatarLink.u-inlineBlock{
    padding-bottom: 0;
}

.u-boxShadow{
    box-shadow: none;
}

div.home-tweet-box.tweet-box.component.tweet-user, div.ProfileCanopy-header.u-bgUserColor, div.ProfileCanopy-headerBg{
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
}

.global-nav-inner, div.ProfileCanopy-navBar.u-boxShadow, div.global-nav-inner{
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
    border-bottom: 1px solid rgb(155, 155, 155);
}

#global-actions>li>a{
    border-bottom: 1px solid #1c94e0;
}

input.text-input, div.DMComposer-container.u-borderUserColorLighter{
    border-radius: 70px;
}

/*Below codes for the borders & shadows of elements, remove it if you want borders on the elements.*/
div.DashboardProfileCard.module, .module .flex-module, div.stream-item.js-new-items-bar-container.new-tweets-bar-visible, .top-timeline-tweetbox .timeline-tweet-box, .global-nav, .stream-item+.stream-item.separated-module:not(.no-header-background-module), .stream-item.separated-module+.stream-item:not(.no-header-background-module), .PromptbirdPrompt-streamItem.separated-module+.stream-item:not(.no-header-background-module), input#search-query.search-input, button.UserActions-editButton.edit-button.EdgeButton.EdgeButton--tertiary, button.EdgeButton.EdgeButton--secondary.EdgeButton--small.button-text.follow-text, div.RichEditor.RichEditor--emojiPicker.is-fakeFocus, button.js-add-tweet.EdgeButton.EdgeButton--secondary.EdgeButton--icon.EdgeButton--medium.js-tooltip, button.EdgeButton.EdgeButton--secondary.EdgeButton--medium.button-text.follow-text, button.EdgeButton.EdgeButton--tertiary.cancel-action.js-close, div.Tombstone, button#js-userColorButton.EdgeButton.EdgeButton--secondary.ProfileHeaderCardEditing-userColorButton.js-current-color.js-dropdown-toggle, button.ProfilePage-saveButton.EdgeButton.EdgeButton--secondary, button.ProfilePage-cancelButton.EdgeButton.EdgeButton--tertiary, button#confirm_dialog_cancel_button.EdgeButton.EdgeButton--tertiary.js-close, button.MomentGuideNavigation-createButton.js-createMoment.EdgeButton.EdgeButton--secondary, button.DMInbox-toolbar.EdgeButton.EdgeButton--small.EdgeButton--secondary.EdgeButton--icon.mark-all-read.js-tooltip, div.global-nav, div.topbar.js-topbar, input.EdgeButton.EdgeButton--secondary.EdgeButton--medium.submit.js-submit, a.js-nav.EdgeButton.EdgeButton--medium.EdgeButton--secondary.StaticLoggedOutHomePage-buttonLogin, div.DMComposer-container.u-borderUserColorLighter{
    border: 0;
}

.RichEditor div[contenteditable], .RichEditor div[contenteditable]:focus, .RichEditor div[contenteditable].fake-focus, .RichEditor{
    border-radius: 20px;
    border: 0;
    padding-left: 7px;
}
}
