style.css:
@-moz-document url-prefix(https://kanobu.ru)
{

@media all and (min-width: 1200px)
{

.c-content_right, .c-branding_fon, .c-likes, .c-share 
{ display: none; }

.c-content_left 
{ width: 100%; }

.c-newsList .c-item a 
{ line-height: 1.3; }

p 
{ line-height: 1.4; margin: 0 0 25px 0; }

.c-branding 
{ padding: 0 !important; }

.c-detail .c-head 
{ margin-bottom: 15px; } 

.c-detail_item.intro-node .intro-node_text, .c-detail_item.text-node, .c-detail_item.image-node 
{ max-width: 100%; padding-left: 30px; padding-right: 30px; }

.c-detail_item.text-node.with-inline-js 
{ max-width: 700px; }

.c-ficher_left .c-item.large-mod .c-item_body, .c-ficher_left .c-item_body, .c-ficher_left .c-item_body:before 
{ text-shadow: rgba(0,0,0,0.80) 0 1px 2px; }

}
}
