style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://lkong.cn/")
{
  :root
  {
    --global-degree: /*[[yj]]*/px;
  }

  body
  {
    background: url(/*[[bg_url]]*/)no-repeat fixed center;
  }

/*顶栏*/
/*[[ycdl]]*/

.header-user{
    /*用户信息栏*/
    width:200px;
}
.header-nav ul li a {
    /*优书网字体颜色*/
	color:/*[[dl_color]]*/;
}
.header .header-user a {
    /*用户信息字体颜色*/
	color: /*[[dl_color]]*/;
}
.header .noticelogo {
    /*消息栏按钮*/
	color: /*[[dl_color]]*/;
}
  .search-form #search-query
  {
    /*搜索栏*/
    border-radius: var(--global-degree);
    background-color: #f9f9f900;
  }

  #search-query:focus
  {
    /*搜索栏焦点*/
    background: #ffffffb5;
  }

  .search-form-glass
  {
	/*搜索按钮*/
    right: 5px;
  }

  .search-form
  {
    /*搜索栏*/
    margin: 5px 0 3px 0;
  }

  .sideCustom .detail .punch
  {
	/*签到按钮*/
    width: 90px;
    height: 50px;
    border-radius: var(--global-degree);
  }

  .lkongcontent
  {
    /*主面板-底色*/
    background: transparent;
  }

  .lkongfeed
  {
    /*左面板，帖子列表*/
    background: rgba(255, 255, 255, /*[[tz]]*/);
    border-right: 0px solid #EEE;
    border-radius: var(--global-degree) var(--global-degree) 0 0!important;
    /*[[glass]]*/
  }

  .th_rp_num
  {
    /*帖子回帖数量图标*/
    background: transparent;
  }

  .new-feed-bar
  {
    /*新信息提醒字体*/
    text-shadow: -0 -0 0.2em #ff887721;
  }
.onefeed {
	/*列表分隔线*/
	border-bottom: 1px solid rgba(204, 204, 204,/*[[fgx]]*/);
}
  
  .nav-tabs > .active > a,
.nav-tabs > .active > a:hover,
.nav-tabs > .active > a:focus
  {
    /*激活的标签卡*/
    background-color: #ffffff08;
    border: 1px solid #dddddd05;
    border-bottom-color: transparent;
  }

  #lkong_pane
  {
    /*详细内容*/
    border-radius: var(--global-degree) var(--global-degree) 0 0!important;
  }

  .pane .pane_title
  {
    /*详细内容标题*/
    border-radius: var(--global-degree) var(--global-degree) 0 0!important;
    background: #d1d1d1;
  }

  .pane_content
  {
    /*详细内容内页*/
    background: #fff;
  }

  .new-feed-bar
  {
    /*新消息提示框*/
    border-radius: var(--global-degree);
    background-color: rgba(115, 162, 255, .35);
  }

  .newfeed
  {
    /*新帖子背景*/
    background-color: #D9EDF799;
  }

  .lkong_right
  {
	/*右框架*/
    padding: 0 12px;
  }

  .module .modulecon
  {
    /*导航栏文字背景*/
    /*border-radius: 1px;*/
    background: #EEE0!important;
  }

  .forum_blackboard
  {
    /*公告栏*/
    background: #EEE0;
  }

  .post
  {
    /*版面说明*/
    background: #F5F5F500;
  }

  .postlist .user
  {
    /*全屏页面头像框*/
    background-color: #f9f9f900;
  }

  .postlist .message
  {
    /*全屏页面背景*/
    border-radius: var(--global-degree);
    background: #FFFFFFB5;
  }

  .forum_blackboard .title
  {
    /*公告分隔条*/
    border-bottom: 3px solid #0070EE;
  }

.module {
    /*导航栏*/
    text-shadow: 0 0 0.2em #c2c2c2;
    border: 1px solid #EEE;
    border: 1px solid rgba(0, 0, 0, 0);
    background-color: rgba(238, 238, 238, /*[[dhl]]*/);
    border-radius: var(--global-degree);
    margin-bottom: 15px;
  	box-shadow: none;
  	/*[[glass]]*/
}

  .tl .users .name
  {
    /*个人主页标题栏*/
    background-color: #f5f5f56b;
  }


  .sidenav li
  {
    /*设置面板导航*/
    background-color: #fff0;
  }

  .sidenav li.select
  {
    /*设置面板高亮*/
    background-color: transparent;
    text-shadow: 0 0 0.2em #f87, -0 -0 0.2em #f87;
  }

  .newbg
  {
    /*设置面板背景*/
    border: 0px solid #EEE;
  }

  .addfollow
  {
	/*添加关注、发表帖子按钮*/
    border-radius: var(--global-degree);
    /*background-color: rgba(217, 240, 251, .75);*/;
  }

  .forumrightbtn a
  {
    /*添加关注、发表帖子字体*/
    font-size: 13px;
  }

  .isfollow
  {
	/*取消关注按钮*/
    border-radius: var(--global-degree);
    background-color: #DDD;
  }

  div[class^="onefeed forummod"]:hover
  {
    /*鼠标经过标题列表*/
    background-color: #FAFFAE4A;
  }

  .userIntro
  {
    color: #3E3E3E;
    font-size: 14px;
    text-shadow: -0 -0 0.2em #558AFF;
  }

/*[[forumlinks]]*/

/*[[hotthread]]*/

/*[[blackboard]]*/

div,p,li{font-family: "Microsoft Yahei";}
}

options.value:
/*隐藏推荐板块*/
div[id$="forumlinks"]{
    display: none
}

/*隐藏今日热门*/
div[id^="hotthread"]{
    display: none
}

/*隐藏版面公告*/
div[class^="forum_blackboard"]{
    display: none
}

.lkongheader {
    /*顶部横条*/
	background: rgba(/*[[head_bar]]*/) !important;
	border-radius: var(--global-degree);
	border-radius: 0 0 var(--global-degree) var(--global-degree);
    /*[[glass]]*/
    /*[[dljz]]*/
	opacity: 0!important;
	transform: translateY(-15%);
	transition-property: opacity, transform;
	transition-duration: .5s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}

.lkongheader:hover {
	opacity: 1!important;
	transform: translateY(0%);
	transition-property: opacity, transform;
	transition-duration: .5s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}

.lkongheader {
    /*顶部横条*/
	background: rgba(/*[[head_bar]]*/) !important;
	border-radius: var(--global-degree);
  	/*[[glass]]*/
  	/*[[dljz]]*/
}
