style.css:
/**
 * Google+ Theme for Mastodon v1.1
 * Copyright (C) 2018-2019 Genbu Project
 */

@-moz-document regexp("https:///*[[instance]]*//.*") {

body,
body .ui {
  color: rgba(0, 0, 0, 0.87);
  background: #f1f1f1;
}

.focusable:focus {
  background: inherit;
}
.focusable:focus .status {
  box-shadow: 0 0 20px 0 rgba(0, 0, 0, 0.3);
}
.focusable:focus .status.status-direct {
  background: #f2f2f2;
}
.focusable:focus .detailed-status {
  background: #ffffff;
}
.focusable:focus .detailed-status__action-bar {
  background: #fafafa;
}

::-webkit-scrollbar-thumb {
  background: #e0e0e0;
}
::-webkit-scrollbar-thumb:hover {
  background: #d6d6d6;
}
::-webkit-scrollbar-thumb:active {
  background: #e0e0e0;
}

::-webkit-scrollbar-track {
  background: #f1f1f1;
}
::-webkit-scrollbar-track:hover, ::-webkit-scrollbar-track:active {
  background: #ebebeb;
}

.floating-action-button {
  font-size: 24px;
  background: #db4437;
}

.card {
  border: 1px solid rgba(0, 0, 0, 0.14);
}
.card > a {
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
}
.card > a:hover .card__bar, .card > a:focus .card__bar, .card > a:active .card__bar {
  background: initial;
}
.card > a:hover .card__bar .display-name strong, .card > a:focus .card__bar .display-name strong, .card > a:active .card__bar .display-name strong {
  color: #2962ff;
}
.card > a .card__img {
  background: #f1f1f1;
}
.card > a .card__bar {
  background: #ffffff;
}
.card > a .card__bar .avatar img {
  background: transparent;
}
.card > a .card__bar .display-name strong {
  color: rgba(0, 0, 0, 0.87);
}
.card > a .card__bar .display-name span {
  color: #616161;
}

.dropdown-menu,
.actions-modal > ul {
  border-radius: 4px;
  background: #ffffff;
}
.dropdown-menu .dropdown-menu__arrow.top,
.actions-modal > ul .dropdown-menu__arrow.top {
  border-top-color: #ffffff;
}
.dropdown-menu .dropdown-menu__arrow.bottom,
.actions-modal > ul .dropdown-menu__arrow.bottom {
  border-bottom-color: #ffffff;
}
.dropdown-menu .dropdown-menu__item a, .dropdown-menu > li:not(:empty) a,
.actions-modal > ul .dropdown-menu__item a,
.actions-modal > ul > li:not(:empty) a {
  color: #616161;
  padding: 8px 16px;
  background: #ffffff;
}
.dropdown-menu .dropdown-menu__item a:active, .dropdown-menu > li:not(:empty) a:active, .dropdown-menu .dropdown-menu__item a:focus, .dropdown-menu > li:not(:empty) a:focus, .dropdown-menu .dropdown-menu__item a:hover, .dropdown-menu > li:not(:empty) a:hover,
.actions-modal > ul .dropdown-menu__item a:active,
.actions-modal > ul > li:not(:empty) a:active,
.actions-modal > ul .dropdown-menu__item a:focus,
.actions-modal > ul > li:not(:empty) a:focus,
.actions-modal > ul .dropdown-menu__item a:hover,
.actions-modal > ul > li:not(:empty) a:hover {
  background: #e6e6e6;
}
.dropdown-menu .dropdown-menu__separator,
.actions-modal > ul .dropdown-menu__separator {
  border-bottom-color: rgba(0, 0, 0, 0.14);
}
.dropdown-menu > li:not(:empty) a,
.actions-modal > ul > li:not(:empty) a {
  padding: 12px 16px;
}

.table thead th {
  border-bottom-color: rgba(0, 0, 0, 0.14);
}
.table th,
.table td {
  color: rgba(0, 0, 0, 0.87);
  background-color: #f1f1f1;
  border-top: 0;
}
.table > tbody > tr:nth-child(odd) > td, .table > tbody > tr:nth-child(odd) > th {
  background: #e7e7e7;
}
.table a {
  color: #2962ff;
}

a.table-action-link,
button.table-action-link {
  color: #2962ff;
}
a.table-action-link:hover,
button.table-action-link:hover {
  color: #0044ff;
}

.tabs-bar {
  margin: 0;
  background: #ffffff;
}
.tabs-bar .tabs-bar__link {
  color: #757575;
}
.tabs-bar .tabs-bar__link .fa {
  vertical-align: top;
}

.drawer .drawer__header {
  box-shadow: 0 1px 8px 0 rgba(0, 0, 0, 0.3);
  background: #ffffff;
}
.drawer .drawer__header .drawer__tab {
  color: #757575;
}
.drawer .drawer__header a:hover {
  background: #e6e6e6;
}
.drawer .drawer__pager {
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
}
.drawer .drawer__pager .drawer__inner {
  background: #ffffff;
}
.drawer .drawer__pager .drawer__inner__mastodon {
  display: none;
}

.navigation-bar {
  color: #2962ff;
  background: #ffffff;
}
.navigation-bar .navigation-bar__profile strong {
  color: rgba(0, 0, 0, 0.87);
}
.navigation-bar .navigation-bar__profile-edit {
  color: #2962ff;
}

.compose-form .compose-form__buttons-wrapper {
  background: #ffffff;
}
.compose-form .compose-form__publish {
  border-top: 1px solid rgba(0, 0, 0, 0.14);
}

.column .column-header {
  box-shadow: 0 1px 8px 0 rgba(0, 0, 0, 0.3);
  background: #ffffff;
}
.column .column-header.active .column-header__icon {
  color: #db4437;
  text-shadow: 0 0 10px rgba(219, 68, 55, 0.4);
}
.column .column-header * {
  background: inherit;
}
.column .column-header__icon {
  color: #757575;
  vertical-align: top;
}
.column .column-header__buttons .column-header__back-button .column-back-button__icon {
  vertical-align: unset;
}
.column .column-header__buttons .column-header__button.active {
  color: #db4437;
  background: inherit;
}
.column .column-header__buttons .column-header__button .fa {
  vertical-align: unset;
}
.column .column-header__collapsible {
  color: #2962ff;
  border-bottom: 1px solid rgba(0, 0, 0, 0.14);
}
.column .column-header__collapsible-inner {
  background: #ffffff;
}
.column .column-back-button__icon {
  vertical-align: unset;
}
.column .column-subheading {
  background: #d8d8d8;
}
.column .column-link {
  color: rgba(0, 0, 0, 0.87);
  background: #f1f1f1;
}
.column .column-link__icon {
  color: #757575;
  vertical-align: top;
}
.column .scrollable {
  background: transparent;
}
.column .scrollable *[role=feed] article {
  margin: 1em 0;
}
.column .scrollable *[role=feed] article:first-of-type {
  margin: 0;
}
.column .scrollable div[tabindex="-1"] {
  margin-bottom: 1em;
}
.column .notification__filter-bar {
  background: #ffffff;
  border-bottom-color: rgba(0, 0, 0, 0.14);
}
.column .notification__filter-bar button {
  color: #757575;
  background: #ffffff;
}
.column .notification__filter-bar button.active {
  color: #db4437;
}
.column .notification__filter-bar button.active::before {
  border-color: transparent transparent rgba(0, 0, 0, 0.14);
}
.column .notification__filter-bar button.active::after {
  border-color: transparent transparent #ffffff;
}
.column .empty-column-indicator {
  flex-direction: column;
  background: #f1f1f1;
}
.column .empty-column-indicator::before {
  content: "";
  display: block;
  width: 100px;
  height: 100px;
  margin-bottom: 1em;
  background: none center/contain no-repeat;
  background-image: url("https://raw.githubusercontent.com/GenbuProject/GPlusTheme-for-Mastodon/asset/notification-jingle_2x.gif");
}

.getting-started__wrapper, .getting-started {
  background: #f1f1f1;
}
.getting-started {
  border-top: 1px solid rgba(0, 0, 0, 0.14);
}
.getting-started a {
  color: rgba(0, 0, 0, 0.87);
}

.load-more, .load-gap {
  background: transparent;
}
.load-more:hover, .load-gap:hover {
  background: #e6e6e6;
}
.load-gap {
  border: 0;
}

.column .column-back-button {
  background: inherit;
}
.column .column-inline-form {
  background: #ffffff;
}
.column .column-inline-form label input {
  color: #2962ff;
}
.column .column-inline-form label input:focus {
  color: rgba(0, 0, 0, 0.87);
}
.column .column-inline-form ~ .scrollable article {
  margin: 0;
}

.modal-root .list-editor {
  background: #f1f1f1;
}
.modal-root .list-editor h4 {
  background: #ffffff;
}
.modal-root .list-editor .drawer__pager .drawer__inner {
  background: #ffffff;
}

.account {
  background: #ffffff;
  border-color: rgba(0, 0, 0, 0.14);
}
.account .account__display-name {
  color: #2962ff;
}
.account .account__display-name strong {
  color: rgba(0, 0, 0, 0.87);
}

.column > .column-back-button {
  box-shadow: 0 1px 8px 0 rgba(0, 0, 0, 0.3);
  background: #ffffff;
}

.account-timeline__header .account__header {
  background-color: rgba(0, 0, 0, 0.4);
}
.account-timeline__header .account__header > div {
  background-color: inherit;
}
.account-timeline__header .account__header .account__header__username {
  color: #00bfff;
}
.account-timeline__header .account__header .account--action-button .icon-button:not(.active) {
  color: #ededed;
}
.account-timeline__header .account__header .account--action-button .icon-button:not(.active):active, .account-timeline__header .account__header .account--action-button .icon-button:not(.active):focus, .account-timeline__header .account__header .account--action-button .icon-button:not(.active):hover {
  color: #ffffff;
}
.account-timeline__header .account__disclaimer, .account-timeline__header .account__action-bar, .account-timeline__header .account__section-headline {
  background: #ffffff;
  border-color: rgba(0, 0, 0, 0.14);
}
.account-timeline__header .account__action-bar__tab {
  border-color: rgba(0, 0, 0, 0.14);
}
.account-timeline__header .account__action-bar__tab.active {
  border-bottom-color: #db4437;
}
.account-timeline__header .account__action-bar__tab > span {
  color: #9e9e9e;
}
.account-timeline__header .account__section-headline a {
  color: rgba(0, 0, 0, 0.87);
}
.account-timeline__header .account__section-headline a.active {
  color: #9e9e9e;
}
.account-timeline__header .account__section-headline a.active::before {
  border-color: transparent transparent rgba(0, 0, 0, 0.14);
}
.account-timeline__header .account__section-headline a.active::after {
  border-color: transparent transparent #ffffff;
}

.account__header__fields dl, .account-timeline__header .account__header .account__header__fields dl {
  border-color: rgba(0, 0, 0, 0.14);
}
.account__header__fields dl dt, .account-timeline__header .account__header .account__header__fields dl dt,
.account__header__fields dl dd,
.account-timeline__header .account__header .account__header__fields dl dd {
  color: rgba(0, 0, 0, 0.87);
  background: #ffffff;
}
.account__header__fields dl dd.verified, .account-timeline__header .account__header .account__header__fields dl dd.verified {
  border-color: rgba(66, 133, 244, 0.5);
  background: rgba(66, 133, 244, 0.25);
}
.account__header__fields dl dd.verified a, .account-timeline__header .account__header .account__header__fields dl dd.verified a {
  color: #92cf94;
}
.account__header__fields a, .account-timeline__header .account__header .account__header__fields a {
  color: #2962ff;
}

.account__action-bar__tab strong {
  color: rgba(0, 0, 0, 0.87);
}

.status {
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
  border-bottom: 0;
  transition: box-shadow 0.1s 0s ease-out;
}
.status.light .status__relative-time {
  color: #9e9e9e;
}
.status.light .status__display-name {
  color: rgba(0, 0, 0, 0.87);
}
.status.light .status__display-name .display-name__account {
  color: #9e9e9e;
}
.status.light .status__content {
  color: rgba(0, 0, 0, 0.87);
}
.status.status-direct {
  background: #f2f2f2;
}
.status.status-direct:not(.read) {
  background: inherit;
  border-bottom-color: initial;
}
.status.muted .status__content p {
  color: rgba(51, 51, 51, 0.87);
}
.status .status__info .status__display-name {
  color: #9e9e9e;
}
.status .status__info .status__display-name strong {
  color: rgba(0, 0, 0, 0.87);
}
.status .status__info .status__relative-time {
  color: #9e9e9e;
}

.detailed-status .detailed-status__display-name {
  color: #2962ff;
}
.detailed-status .detailed-status__display-name strong {
  color: rgba(0, 0, 0, 0.87);
}
.detailed-status .detailed-status__meta .detailed-status__datetime {
  color: #9e9e9e;
}
.detailed-status .detailed-status__meta .detailed-status__link .fa.fa-star {
  vertical-align: middle;
}
.detailed-status__wrapper {
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
}
.detailed-status__wrapper .detailed-status__action-bar {
  background: #fafafa;
  border-color: rgba(0, 0, 0, 0.14);
}

.status,
.detailed-status,
.detailed-status__wrapper .detailed-status {
  background: #ffffff;
}
.status .status__content,
.detailed-status .status__content {
  color: rgba(0, 0, 0, 0.87);
}
.status .status__content a,
.detailed-status .status__content a {
  color: #2962ff;
}
.status .status__content .status__content__spoiler-link,
.detailed-status .status__content .status__content__spoiler-link {
  color: rgba(0, 0, 0, 0.87);
  background: #f1f1f1;
}

a.status-card:hover,
a.status-card.compact:hover {
  background: initial;
}

.name-tag,
a.name-tag {
  color: #2962ff;
}

.notification.notification-favourite .status.status-direct {
  background: transparent;
}
.notification > .notification__message {
  color: #616161;
  font-size: 13px;
  padding: 8px 0;
}
.notification > .notification__message .notification__display-name:hover {
  color: inherit;
}
.notification > .notification__message .notification__favourite-icon-wrapper .star-icon {
  color: #db4437;
}

.search .search__input {
  border-radius: 4px;
  color: rgba(0, 0, 0, 0.87);
  background: rgba(0, 0, 0, 0.1);
}
.search .search__input:focus {
  background: rgba(51, 51, 51, 0.1);
}
.search .search__icon .fa {
  color: #757575;
}
.search-results > .search-results__header {
  background: #f1f1f1;
}
.search-results > .search-results__section > h5 {
  background: #d8d8d8;
}

.trends__item {
  background: #ffffff;
}
.trends__item .trends__item__name a {
  color: #2962ff;
}
.trends__item .trends__item__current {
  color: #5c87ff;
}

@media screen and (min-width: 415px) {
  .edit_account .card > a {
    position: relative;
  }
  .edit_account .card > a .card__img {
    border-bottom: 1px solid rgba(0, 0, 0, 0.14);
  }
  .edit_account .card > a .card__bar {
    position: static;
    padding-top: 30%;
  }
  .edit_account .card > a .card__bar .avatar {
    position: absolute;
    left: 0;
    bottom: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
  }
  .edit_account .card > a .card__bar .avatar img {
    width: 128px;
    height: 128px;
  }
  .edit_account .card > a .card__bar .display-name {
    flex: auto;
    margin-left: 0;
    text-align: center;
  }
}

.report-card {
  background: #ffffff;
}
.report-card__profile__stats {
  color: #616161;
}
.report-card__profile__stats a:focus, .report-card__profile__stats a:hover, .report-card__profile__stats a:active {
  color: #757575;
}
.report-card__summary__item {
  border-top-color: rgba(0, 0, 0, 0.14);
}
.report-card__summary__item:hover {
  background: #e6e6e6;
}
.report-card__summary__item__reported-by, .report-card__summary__item__assigned {
  color: #616161;
}
.report-card__summary__item__content__icon {
  color: #757575;
}
.report-card__summary__item__content a {
  color: #616161;
}

.batch-table__toolbar {
  background: #ffffff;
  border-color: rgba(0, 0, 0, 0.14);
}
.batch-table__row {
  background: #ffffff;
  border-color: rgba(0, 0, 0, 0.14);
}
.batch-table__row:hover {
  background: #e6e6e6;
}
.batch-table__row__content .status__content {
  color: rgba(0, 0, 0, 0.87);
}
.batch-table__row__content .detailed-status__meta {
  color: #9e9e9e;
}

.speech-bubble__bubble {
  color: rgba(0, 0, 0, 0.87);
}
.speech-bubble a {
  color: #2962ff;
}

.dashboard__counters > div > div, .dashboard__counters > div > a {
  background: #f1f1f1;
}
.dashboard__counters > div > a:hover, .dashboard__counters > div > a:focus, .dashboard__counters > div > a:active {
  background: #f6f6f6;
}
.dashboard__counters__num {
  color: rgba(0, 0, 0, 0.87);
}
.dashboard__counters__label {
  color: #9e9e9e;
}
.dashboard__widgets a:not(.name-tag) {
  color: #616161;
}

body.admin {
  color: rgba(0, 0, 0, 0.87);
  background: #f1f1f1;
}
body.admin .sidebar-wrapper {
  background: inherit;
}
body.admin .sidebar ul {
  border-radius: 0;
}
body.admin .sidebar ul a {
  color: #616161;
  border-radius: 0;
}
body.admin .sidebar ul a:hover {
  color: rgba(0, 0, 0, 0.87);
  background: #e4e4e4;
}
body.admin .sidebar ul a.selected {
  color: rgba(0, 0, 0, 0.87);
  background: #ececec;
}
body.admin .sidebar ul ul {
  background: #e7e7e7;
}
body.admin .sidebar ul .simple-navigation-active-leaf a {
  color: #db4437;
  background: initial;
}
body.admin .sidebar ul .simple-navigation-active-leaf a:hover {
  background: #e4e4e4;
}
body.admin .content {
  box-shadow: 0 1px 8px 0 rgba(0, 0, 0, 0.3);
  background: #ffffff;
}
body.admin .content h2, body.admin .content h3, body.admin .content h4, body.admin .content h6 {
  color: rgba(0, 0, 0, 0.87);
}
body.admin .content p {
  color: rgba(0, 0, 0, 0.87);
}
body.admin .content p strong {
  color: rgba(0, 0, 0, 0.87);
}
body.admin .content h2, body.admin .content h4, body.admin .content hr {
  border-bottom-color: rgba(0, 0, 0, 0.14);
}
body.admin .content .muted-hint {
  color: #9e9e9e;
}

.simple_form .input.boolean .label_input > label, .simple_form .input.with_label .label_input > label, .simple_form .input.with_floating_label .label_input > label {
  color: rgba(0, 0, 0, 0.87);
}
.simple_form .input.with_block_label > label {
  color: rgba(0, 0, 0, 0.87);
}
.simple_form .input.radio_buttons .radio label {
  color: rgba(0, 0, 0, 0.87);
}
.simple_form .input-copy {
  background: #f1f1f1;
}
.simple_form .check_boxes .checkbox label {
  color: rgba(0, 0, 0, 0.87);
}
.simple_form input[type=text],
.simple_form input[type=number],
.simple_form input[type=email],
.simple_form input[type=password],
.simple_form textarea {
  color: rgba(0, 0, 0, 0.87);
  background: #ffffff;
  border-color: rgba(0, 0, 0, 0.14);
}
.simple_form input[type=text]:hover,
.simple_form input[type=number]:hover,
.simple_form input[type=email]:hover,
.simple_form input[type=password]:hover,
.simple_form textarea:hover {
  border-color: rgba(0, 0, 0, 0.14);
}
.simple_form input[type=text]:active, .simple_form input[type=text]:focus,
.simple_form input[type=number]:active,
.simple_form input[type=number]:focus,
.simple_form input[type=email]:active,
.simple_form input[type=email]:focus,
.simple_form input[type=password]:active,
.simple_form input[type=password]:focus,
.simple_form textarea:active,
.simple_form textarea:focus {
  background: #ebebeb;
}
.simple_form select {
  color: rgba(0, 0, 0, 0.87);
  background-color: #ffffff;
  border-color: rgba(0, 0, 0, 0.14);
}
.simple_form .input.field_with_errors label,
.simple_form .input.field_with_errors .error {
  color: #d50000;
}
.simple_form .hint, .simple_form p.hint {
  color: #9e9e9e !important;
}
.simple_form .label_input__append {
  color: #9e9e9e;
}

.simple_form .warning,
.table-form .warning {
  background: rgba(213, 0, 0, 0.8);
}

.quick-nav a {
  color: #2962ff;
}
.quick-nav a:hover, .quick-nav a:focus, .quick-nav a:active {
  color: #5280ff;
}

.pagination .page,
.pagination a {
  color: rgba(0, 0, 0, 0.87);
}
.pagination .page.current {
  background: #db4437;
  color: #ffffff;
}

.filters .filter-subset strong {
  color: rgba(0, 0, 0, 0.87);
}
.filters .filter-subset a {
  color: #616161;
}
.filters .filter-subset a:hover {
  color: rgba(0, 0, 0, 0.87);
}
.filters .filter-subset a.selected {
  color: #db4437;
  border-bottom-color: #db4437;
}

label[for=user_setting_default_privacy_public], label[for=user_setting_default_privacy_unlisted] {
  color: #db4437 !important;
}

.column-header__collapsible__extra .column-settings__section {
  color: rgba(0, 0, 0, 0.87);
}
.column-header__collapsible__extra .setting-toggle .react-toggle.react-toggle--checked > .react-toggle-track {
  background: rgba(66, 133, 244, 0.5);
