@-moz-document domain("ibb.co"), domain("imgbb.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.content-tabs li:hover,
.content-tabs li.current,
.tone-light .top-bar #menu-fullscreen > ul > li .arrow-down, .tone-light .top-bar .arrow-down,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.list-item-image .image-container img {
    object-fit: scale-down!important;
}

.list-item-thumbs li a {
    background-size: contain;
}

.image-container,
.selected .image-container,
.ui-selecting .image-container
{
    transform: scale(0.8)!important;
}

.js .list-item {
	display: inline-block!important;
}

x.js .list-item,
x.visible {
	position: relative!important;
}

.content-listing {
    clear: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.number-figures,
	
a.number-figures
{
    font-size: 2em;
}

[data-icon-selected="icon-square-check"]
{
	font-size: 81px!important;
}

.btn-icon.icon-arrow-down-a-z,
.header .header-link
{
    font-size: 31px!important;
}

.btn-icon {
    font-size: 21px!important;
}

[class*=" icon-"], [class^="icon-"] {
    line-height: 1.5;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#background-cover,

.abnr
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.list-item-thumbs {
    margin-top: 2px;
    margin-bottom: 12px;
    margin-left: 8px;
}

.tabbed-listing .list-item-image .image-container img {
    margin-top: 3rem!important;
}

#content-listing-tabs #tabbed-content-group {
    margin-top: 0!important;
}

.btn-text {
    margin-left: 13px;
}

#content-listing-tabs {
    margin-left: 1%!important;
}

[data-type="album"] .list-item-desc, [data-type="album"] .list-item-image-tools {
    padding-bottom: 99px;
}

.pop-box-menu li.with-icon a {
    padding-left: 47px;
}

.list-item-thumbs {
    margin-top: -1px;
    margin-bottom: 0px;
    margin-left: 5px;
}

.content-tabs a {
    padding-left: 26px;
    padding-right: 26px;
}

.list-item-image-tools {
    right: 33px!important;
}

.list-item-image-tools > div {
    right: -20px!important;
}

.list-item-desc {
    bottom: -72px!important;
}

.list-item-image-tools--left {
    left: -31px!important;
}

.list-item-image-tools {
    right: 16px!important;
}

.list-item-image-tools > div {
    float: left!important;
}

.list-item-desc-title > * {
    padding-bottom: 40px!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

.about,
.content-width,
.js .list-item
{
	margin: 1.2rem!important;
}

.header.follow-scroll {
    margin: 13px -10px 0 -10px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.content-width,
.js .list-item,
.top-user
{
	padding: 1.2rem!important;
}

.content-tabs li
{
	padding: 3px 16px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.list-item-image .image-container img {
    height: 82%!important;
}

.tabbed-listing .list-item-image .image-container img {
    height: 63%!important;
}

.list-item-image {
    height: 14rem!important;
}

.top-bar, .top-bar ul li.pop-btn, .top-bar ul li.top-btn-el, .top-bar-placeholder {
    height: 61px!important;
}

.js .list-item {
    height: 19.5rem!important;
}

.list-item-desc-title-link {
    height: 9rem!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.content-width
{
	max-width: none!important;
}

.list-item-image .image-container img
{
    width: 100%!important;
}

.list-item-thumbs {
    width: 99%!important;
}

#content-listing-tabs,

.about,
.tabbed-content,
.content-width
{
	width: 98%!important;
}

.js .list-item
{
    width: 12.5%!important;
}

.btn-icon {
    width: 44px!important;
}

.list-item-buttons > div,
.list-item-image-tools > div,
.list-item-image-tools > div .btn-icon
{
    width: 3.2em!important;
