style.css:
@-moz-document url-prefix("https://flexnow2.uni-giessen.de"),url-prefix("https://flexnow.uni-giessen.de") {
a {
    color: #9bb2df;
}

/*Backgrounds*/

body {background: #181818}                          /*overall-bg*/
.header {background: #2d2d2d;}                      /*header-bg*/
.header .information .inner {background: #565656}   /*Aktuelles-title-bg*/

/*Navigation Menu*/

#leftColumn .navigation-main a,  #leftColumn .navigation-main a.last {
    color: #7f7f7f;
    background: #181818;
    border-top: 0px;
    border-bottom: 0px;
}
#leftColumn .navigation-main a.act {    /*active link*/
    color: white;
    background: #565656
}

/*Main Area*/
#workColumn .inner {background: #2d2d2d; color: white}
.btn, .btn2 {background-color: #565656; border: 0px}
.fristBis {font-weight: normal}
.willkommen {color: #3c6089}
.contentContainer, .keineErg, .prfstdErg{background: #2d2d2d}
li.abmeldenBlock {color: white}
#listeTeilprfsPrfangs .PrfContainerAktuelles {border: 0px}
.info-inner, ul.class-list li, #listeTeilprfsPrfangs .PrfContainerAktuelles{background: #2d2d2d}
ul.class-list li ul li .class-list-head.signed {background-color: #565656; font-weight: normal}
.prffachBez {font-weight: normal}

/*anderes*/

#bitteWartenDiv {background-color: rgba(0,0,0,0.9)}

.lobibox-notify.lobibox-notify-error {
    background: #f748ff; 
    border-color: #0091ff;
    color: #0f0; 
    font-family: "Comic Sans MS";}
/*Studentendaten*/
td {background: #2d2d2d}    /*Allgemeine Informationen*/
h3, h4 {color: white; font-weight: normal}

/*Prüfungen*/
.lvAnmeldung, #leereLvListe, #leererWarenkorbPrf, .keineTeilprf, .wwwMeldung, .keineErg, .keineAb, .containerTAN, .tanDownloadLink, .auslandContainer, .auslandFormular, #ListeWarenkorbPrf .warenkorbEintrag, .terminListe, #listeStudentfach, .conteinerEinstellungen, .containerPWAendern, .containerDatenblatt, #leererWarenkorb, .listeLvVerfuegbar, .listeLvAbmeldbar, #ListeWarenkorb .warenkorbEintrag, #persTitel #prfContainer, #containerWorkDatenPrfAktuell #prfContainer, #containerWorkDatenPrfAbgelegt #prfContainer, #containerWorkDatenLvAngemeldet #lvContainer, #zeugnisContainer, #semContainer, #mapContent {background-color: #2d2d2d}
div.hitarea.collapsable-hitarea::before{background: #2d2d2d; color: #7f7f7f}

div.hitarea.expandable-hitarea::before{background: #2d2d2d; color: white}

.header .information .inner, #leftColumn .navigation-main a.act, .treeview li.collapsable span.studfach_blau, .treeview li.expandable span.studfach_blau, #containerWorkTeilprfs .teilprfBezeichner, #containerWorkWarenkorbPrf .teilprfBezeichner, #containerWorkWarenkorbPrfSolo .teilprfBezeichner, #containerWorkTeilprfs #containerListeTeilprf #listeTeilprfsPrfangs .teilprfBezeichner {
    background-color: #3c6089;}

.treeview li.collapsable span.studfach_blau, .treeview li.expandable span.studfach_blau, #containerWorkTeilprfs .teilprfBezeichner, #containerWorkWarenkorbPrf .teilprfBezeichner, #containerWorkWarenkorbPrfSolo .teilprfBezeichner, #containerWorkTeilprfs #containerListeTeilprf #listeTeilprfsPrfangs .teilprfBezeichner {
    border: 0px}

/*----------------------------------------------------------*/
/*An-/Abmeldung*/
.suche {
    position: relative;
    height: auto;
    background-color: #2d2d2d;
    margin: 5px 5px 5px 5px;
    padding: 10px;
    box-shadow: 0 0 0px;
}
.prfangBlock p.Anmerkung, .prfangBlock p.Anmerkung::before, .studiumli a.studiumAnchor {
    color: #9bb2df;
    font-weight: normal
}
#containerWorkTeilprfs #containerListeTeilprf #listeTeilprfsPrfangs .prfangBlock.selected, #containerWorkWarenkorbPrf #warenkorbPrf #ListeWarenkorbPrf .prfangBlock, .treeview a.selected {
    background-color: #2d2d2d !important;
    font-weight: bolder;
    font-style: italic;
}
div#TeilPrfPrfangs_prfangs4.PrfContainer, div#TeilPrfPrfangs_prfangs1.PrfContainer {
    background-color: #2d2d2d
}
div.primaryData {
    background-color: #2d2d2d;
    font-style: normal;
}

}
/*ANMELDESEITE*/
@-moz-document url-prefix("https://www.uni-giessen.de/studium/waehrend/ecampus/flexnow"),url-prefix("http://www.uni-giessen.de/studium/waehrend/ecampus/flexnow") {
body {
    background-color: #181818;                      /*hintergrund außenrum*/
}
#header-wrapper {                                       /*hide header images*/
    display: none;
}
#parent-fieldname-title {                               /*Überschrift*/
    color:#d7d7d7;
}
/*----------------------------------------------------------------------------------------------------------------*/
/*------------------------------------NAVIGATIONBAR---------------------------------------------------------------*/
#section-name {                                                      /*active*/
    background: #181818;
}
.searchbox-wrapper, #portal-globalnav, .plain {                   /*inactive*/
    background: #4d5b86;
}

/*----------------------------------------------------------------------------------------------------------------*/
/*------------------------------------ANMELDE BOX-----------------------------------------------------------------*/
#fn2container {
    background-color: #2d2d2d;                      /*hintergrund anmeldebox*/
}
#fn2container h2 {      /*Mein Flexnow*/
    color: #d7d7d7;
}
#fn2container label {   /*Benutzername und PW*/
    color: #9e9e9e;
}
/*----------------------------------------------------------------------------------------------------------------*/
/*------------------------------------ANMELDE BUTTON--------------------------------------------------------------*/
#fn2container input[type=submit] {
    background-image: linear-gradient(#181818, #2d2d2d);    /* Hintergrund*/
    color: white;}
#fn2container input[type=submit]:hover {                        /*hover*/
    background-image: linear-gradient(#2d2d2d, #181818);    /*Hintergrund (inverted)*/
    color: #d7d7d7;}
/*----------------------------------------------------------------------------------------------------------------*/
/*----crap----------------*/
.portletItem{
    background-color: #181818;
    color: #d7d7d7;
}
.portletHeader{
    background-color: #181818;
}
/*----------------------------------------------------------------------------------------------------------------*/
dl.portlet dt.portletHeader, dl.portlet .portletHeaderHtml{
    color: white;
    background: #181818}
.bluePortletWrapper dl, dd.portletItem.even, .grayPortletWrapper dl, .uniblauPortletWrapper dl, #fn2container #lower{background: #181818}
/*background-image: url(https://i.imgur.com/VlP1i3w.jpg)*/
aside.searchbox-wrapper{background: #181818;}
#globalnav-wrapper {
    border-color: #181818;
    border-radius: 0px 0px 0px 0px;
    border-width: 0px;
    border-bottom: 0.22em solid #181818;
    background-color: #181818;}
.searchbox-wrapper, #portal-globalnav, .plain {
    background: #181818;}
#globalnav-wrapper nav#portal-globalnav ul li a, p{color: #d7d7d7;}
span#breadcrumbs-you-are-here{color: #d7d7d7;}

}

/*HELP*/
@-moz-document url-prefix("https://flexnow2.uni-giessen.de/FN2Hochschule/hilfe/FN2SSS/") {
*{
    color: white;
    font-family: arial;
    background: #181818;
}
}
