@-moz-document domain("gbs.nesa-sg.ch") {
/*Material Button*/
#loginSubmit,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input {
    position: relative;
    display: inline-block;
    box-sizing: border-box;
    margin: 0 8px;
    border: none;
    border-radius: 2px;
    padding: 0 16px;
    min-width: 64px;
    height: 36px;
    vertical-align: middle;
    text-align: center;
    text-overflow: ellipsis;
    text-transform: uppercase;
    color: #fff;
    background-color: #008334;
    box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
    font-family: "Roboto", "Segoe UI", BlinkMacSystemFont, system-ui, -apple-system;
    font-size: 14px;
    font-weight: 500;
    line-height: 36px;
    overflow: hidden;
    outline: none;
    cursor: pointer;
    transition: box-shadow 0.2s;
}

#loginSubmit:hover,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input:hover,
#loginSubmit:focus,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input:focus {
    box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.2), 0px 4px 5px 0px rgba(0, 0, 0, 0.14), 0px 1px 10px 0px rgba(0, 0, 0, 0.12);
}

#loginSubmit:active,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input:active {
    box-shadow: 0px 5px 5px -3px rgba(0, 0, 0, 0.2), 0px 8px 10px 1px rgba(0, 0, 0, 0.14), 0px 3px 14px 2px rgba(0, 0, 0, 0.12);
}

#loginSubmit:disabled,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input:disabled {
    color: rgba(0, 0, 0, 0.38);
    background-color: rgba(0, 0, 0, 0.12);
    box-shadow: none;
    cursor: initial;
}

#loginSubmit::before,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input::before {
    content: "";
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    background-color: currentColor;
    opacity: 0;
    transition: opacity 0.2s;
}

#loginSubmit:hover::before,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input:hover::before {
    opacity: 0.12;
}

#loginSubmit:focus::before,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input:focus::before {
    opacity: 0.2;
}

#loginSubmit:active::before,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input:active::before {
    opacity: 0.32;
}

#loginSubmit:disabled::before,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input:disabled::before {
    opacity: 0;
}

#loginSubmit::after,
#standardformular > table > tbody > tr:nth-of-type(3) > td > input::after {
    content: "";
    position: absolute;
    left: 50%;
    top: 18px;
    border-radius: 50%;
    padding: 50%;
    width: 35px;
    height: 32px;
    background-color: currentColor;
    opacity: 0;
    transform: translate(-50%, -50%) scale(1);
    transition: opacity 1s, transform 0.5s;
}

#loginSubmit:active::after {
    opacity: 0.4;
    transform: translate(-50%, -50%) scale(0);
    transition: transform 0s;
}

#loginSubmit:disabled::after {
    opacity: 0;
}
}
