/* ==UserStyle==
@name           Campus Student Dark Theme
@namespace      Colin's Infinite Campus Dark
@version        1.0.1
@description    A simple dark theme for the new infinite campus student interface
@author         Colin Lewis (http://colincon.ga)
==/UserStyle== */

@-moz-document domain("campus.ccsd.net") {
    html.login,
    .divider__header:hover,
    .dropdown,
    .collapsible-card__header:hover {
        background-color: #2a2e30;
    }

    html.login .login-card, html.login .login-card .login-content,
    body.workspace,
    .assignment__row.clickable:hover,
    li.ng-star-inserted:hover,
    .divider__header,
    .grades__row.clickable:hover,
    thead[_ngcontent-c19] th,
    tr[_ngcontent-c19]:hover,
    .table__caption, .table__header-cell,
    .static-table--striped tr:nth-child(even), .table--striped tr,
    .table__row.clickable:hover,
    .collapsible-card__header,
    .grading-task-list__header[_ngcontent-rme-c16],
    .row__header {
        background-color: #383e40;
    }


    .card__header,
    .vertical-nav__item:hover,
    .user-dropdown__item:hover,
    .k-item:hover:not(.k-state-active) {
        background-color: #383e40 !important;
    }

    div.toolbar,
    .card,
    .card__header-button:hover,
    .assignment__row,
    .assignment__empty,
    tr[_ngcontent-c19],
    table.table,
    .selcat-schedule-periodRow,
    .fade-out__button[_ngcontent-c27],
    .k-tabstrip > .k-content {
        background-color: #4d565a;
    }

    html.login .login-card h1,
    a,
    .k-tabstrip-items .k-item,
    .k-item:hover:not(.k-state-active) {
        color: #92c83e;
    }

    .k-tabstrip-items .k-item.k-state-active {
        background-color: #92c83e;
    }

    .k-tabstrip > .k-tabstrip-items, .k-tabstrip .k-item,
    .k-tabstrip .k-item hover {
        border-color: #92c83e;
    }

    .k-state-disabled {
        border: none !important;
    }

    .login-content h5,
    span.divider,
    .help-container a,
    .header-text,
    .card__header,
    .assignment__row,
    .card,
    .toolbar__header-link,
    .fa:not(.notification__circle), .fa-circle:not(.notification__circle), .far,
    li.ng-star-inserted:hover a,
    .toolbar__header,
    .row__header,
    .title, #programSelection,
    .fade-out__button[_ngcontent-c27]:hover,
    .vertical-nav__item,
    div.user-dropdown__item,
    .notifications-dropdown__header,
    .notification__text,
    .button__text--hide-small,
    .grades__flex-row__item,
    .divider__header,
    body,
    .k-tabstrip > .k-content {
        color: white !important;
    }

    .login-content p,
    .hide-tiny,
    .text-secondary,
    .assignment__empty,
    li.ng-star-inserted a,
    .card__sub-header,
    .divider__subtext,
    .fade-out__button[_ngcontent-c27],
    .notification__date,
    .assignment__not-graded{
        color: #ccc !important;
    }

    .toolbar,
    .card__header, .card,
    .divider__item[_ngcontent-c6], .divider__content,
    .row__header,
    .card__header-button,
    .assignment__row[_ngcontent-c2] + .assignment__row[_ngcontent-c2],
    .assignment-list__card__header[_ngcontent-c12], .assignment-list__card__content[_ngcontent-c12],
    .task__row[_ngcontent-c13] + .task__row[_ngcontent-c13],
    .divider__item+.divider__item,
    .static-table th, .table__header-cell,
    .well,
    tr[_ngcontent-c19],
    .table__caption,
    .static-table tr, .table__row,
    .announcement__container[_ngcontent-c27], hr,
    .announcement[_ngcontent-c27] + .announcement[_ngcontent-c27],
    .vertical-nav__item[_ngcontent-c28],
    .vertical-nav__item[_ngcontent-c0],
    .dropdown,
    .assignment-list__card__header[_ngcontent-c1],
    .assignment-list__card__content[_ngcontent-c1],
    .divider__item[_ngcontent-c5],
    .collapsible-card__header,
    .collapsible-card__content {
        border-color: #2a2e30;
    }

    .user-dropdown__item[_ngcontent-c3],
    .notification[_ngcontent-c4],
    .notifications-dropdown__header[_ngcontent-c4] {
        border-color: #4d565a;
    }

    .fade-out__overlay--fade[_ngcontent-c27] {
        background-image: linear-gradient(rgba(255,255,255,0) 0,#4d565a 100%);
    }
}
