style.css:
@-moz-document domain("rgl.gg") {
body {
    font-family: 'Raleway', sans-serif !important;
    background-color: #23272a;
    color: #fff;
}
strong {
    color: #fff;
}
.navbar-nav li.active a {
    color: #1f271b !important;
    background-color: #999 !important;
}
.nav-tabs {
    padding-bottom: 1px;
    border-bottom: 1px solid #e29455 !important;
}
.nav-tabs > li > a {
    background-color: #2c2f33 !important;
    border-radius: 0px;
    margin: 0px;
}
.nav-tabs > li > a:hover {
    border: none;
}
.nav-tabs > li.active > a {
    background-color: #e29455 !important;
    border: 1px solid #e29455 !important;
}
.nav-tabs > li.active > a:hover {
    color: #fff;
}
.dropdown-menu {
    background: #2c2f33;
    box-shadow: none;
    margin-left: 1px;
    border: none;
}
.dropdown-menu > li > a {
    color: #fff;
}
.dropdown-toggle[aria-expanded="true"] {
    background-color: #2c2f33 !important;
}
.list-group {
    border: 1px solid #e29455 !important;
    border-radius: 6px;
}
.list-group-item {
    background-color: #2c2f33;
    border: none;
    color: #fff;
}
a.list-group-item {
    color: #fff;
    border: none;
}
a.list-group-item:hover {
    background-color: #999;
}
.panel-default > .panel-heading a[aria-expanded="true"] {
    background-color: #EE964B;
    color: white;
    font-weight: 500;
}
.panel-body {
    background-color: #2c2f33;
    border: none;
}
.collapsed {
    color: #fff;
    background-color: #2c2f33;
}
.panel-title {
    color: #fff;
}
a.deco-none-excepthover {
    color: #999 !important;
}
.navbar {
    background-color: #2c2f33 !important;
}
table {
    border: 1px solid #e29455 !important
}
th,
td {
    border-top: none !important;
}
.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #2c2f33;
}
.navbardropdown {
    background-color: #2c2f33 !important;
}
.Well-yellowRimmed {
    background-color: #2c2f33;
    border: 1px solid #e29455 !important;
    border-radius: 0;
}
a.deco-none {
    color: #999 !important;
}
.alert-event-notification {
    background-color: #EE964B;
    border: none;
}
.alert > a {
    color: #2c2f33;
}
.well {
    background-color: #2c2f33;
}
.PlayoffSafe {
    background-color: #4b3b1c !important;
}
.bhoechie-tab,
.bhoechie-tab-content {
    color: #fff !important;
    background-color: #2c2f33 !important;
}
div.bhoechie-tab-container {
    color: #fff !important;
    background-color: #2c2f33 !important;
    border: none;
}
.panel {
    background-color: #2c2f33 !important;
    border-radius: 0px;
    box-shadow: none;
    margin-bottom: 0px;
}
.page-header {
    border-bottom: none;
}
.jumbotron {
    background-color: #2c2f33 !important;
    color: #fff !important;
}
.blockquote {
    background-color: #2c2f33 !important;
    border-left: none;
}
tr[style="background-color: #B9DFCD"] {
    background-color: #7dc6a4 !important;
}
tr[style="background-color: #FEF0C7;"] {
    background-color: #fef0b3 !important;
    color: #2c2f33 !important;
}
tr[style="color: gray;background-color: #f9f9f9"] {
    background-color: #4f1313 !important;
    color: #fff !important;
}
#ContentPlaceHolder1_Main_lblPlayerBanHistory {
    color: #fff !important;
}
hr {
    display: none !important;
}
.divider {
    background-color: #e29455 !important;
    margin: 5px 0 !important;
}
}

@-moz-document domain("forums.highlander.tf") {
html {
    background-color: #23272a !important;
    color: #aaa;
}
#content {
    background-color: #23272a !important;
}
.upper {
    background-color: #2c2f33 !important;
}
.trow1,
td {
    background-color: #2c2f33 !important;
    color: #ffffff !important;
    border: none !important;
}
.trow2 {
    background-color: #23272a !important;
    color: #ffffff !important;
    border-bottom: 1px solid #333 !important;
}
.post.classic,
.post_controls {
    background-color: #2c2f33 !important;
    color: #ffffff !important;
    border: none;
}
.post.classic .post_author,
.post.classic .post_content {
    border: none !important;
}
.post_head {
    border: none !important;
}
.post_body {
    color: #eee;
}
.mycode_quote {
    background-color: #23272a !important;
    border: none !important;
}
.mycode_b {
    color: #fff;
}
.mycode_font {
    color: #666;
}
.lower {
    background-color: #23272a !important;
    border: none !important;
}
textarea,
fieldset {
    border: 1px solid #333 !important;
    color: #666 !important;
    background-color: #23272a !important;
}
cite {
    border-bottom: 1px solid #333 !important;
}
}
