style.css:
@-moz-document domain("www.zive.cz"), domain("www.mobilmania.cz"), domain("connect.zive.cz"), domain("jnp.zive.cz"), domain("vtm.zive.cz"), domain("doupe.zive.cz") {
body {
    color: #fff;
}
html, body, .common-menu, .tm-r1, .cs-cnt-wrapper {
    background: none;
    background-color: #151515 !important
}
.tm-r1 .cs-cnt, .cs-cnt-cl, .cs-cnt-main, .box-cex, .ar-detail .ar-society-networks .ar-society-networks-wrapper, .box, .fs-os {
    background: none;
    background-color: #333 !important;
}
.cs-cnt {
    background: #424242;
}
.forum-row-wrapper .forum-head, .forum-text {
    border-top: 4px solid rgba(255, 255, 255, 0.3);
    background-color: #454545 !important;
}
.forum-row-wrapper .forum-head, .forum-row-wrapper .forum-text {
    border-left: 1px solid rgba(255, 255, 255, 0.3);
    border-right: 1px solid rgba(255, 255, 255, 0.3);
}
.ar-detail .ar-annotation, .ar-detail .ar-content, .ar-detail .ar-society-networks .ar-info .ar-date, a, .ar-chapters a.ar-btn, .ar-chapters .ar-btn-comments, .ar-chapters .ar-btn-comments a, .ar-content .ar-tags, .ar-content .ar-tags a, .ar-detail .ar-content .ar-tags a {
    color: #999 !important;
}
#Billboard-bottom-1 {
    background: none !important;
}
a {
    color: #ccc;
}
.bx-head a, .bx-cnt {
    color: #fff !important;
}
.forum-main div[id^='collapseChilds'] {
    display: inline !important;
}

div[id^='bodyPart'] {
    display: inline !important;
}

.ar-detail .ar-content .article-template-spliter {
    display: none;
}
.consult-results tr.odd td {
    background: #2d2d2d;
}
.ar-author > a > img { 
    filter: invert(0.8) hue-rotate(170deg);
}
.cs-cnt-cl-r {
    display: none;
}
.branding-wrapper {
    background: #151515 !important
}
.cs-cnt-wrapper, .christbar ul {
    background: inherit !important 
} 
#footer-global {
	display: none
}
.cs-cnt.fs-os {
    display: none;
}
}

@-moz-document domain("www.mobilmania.cz") {
#search-text::placeholder {
    color: #1781f2;
}
.ar-detail h1 {
    color: #69aef8;
}
.ar-detail .ar-content h2, .ar-detail .ar-content h3, .ar-detail .ar-content h4 {
    color: #4186cf;
}

.ar-list .ar-row .ar-txt span.ar-comments a::before {
    filter: brightness(1.7);
}
}

@-moz-document domain("connect.zive.cz") {
.ar-detail .ar-content a, .forum-social-agree {
    color: #23a3bf !important;
}
.tm-r1 .logo {
    filter: invert(0.8) hue-rotate(170deg);
}
}
