style.css:
@-moz-document domain("greasyfork.org"), domain("sleazyfork.org") {
/* Site Settings */

* {
	text-decoration: none!important;
}

selector
{
	text-transform: capitalize!important;
}

.external-login,
.list-option-button,
.list-option-group a:focus, .list-option-group a:hover,
.post-install,
.rating-icon-bad,
.rating-icon-good,
.rating-icon-ok,
.tabs .current,
.user-content,

:focus,
code,
form.external-login-form
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

#additional-info table {
    border-collapse: separate;
    text-indent: initial;
    border-spacing: 5rem 0;
}

/*___ Fonts ________________________________________________________________________*/

.discussion-meta,

h2 a
{
	font-size: 31px!important;
}

.installation-instructions-modal-content-bypass,
.list-option-groups,
.script-list .description,

body,
pre
{
	font-size: 25px!important;
}

.rating-icon
{
	font-size: 17px!important;
}

selector
{
	font-size: 15px!important;
}

selector
{
	line-height: 25px!important;
}

/*___ Remove Items _________________________________________________________________*/

.adsbygoogle.adsbygoogle-noablate,
.ad,
.ea-callout, .ea-content
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.external-login {
    padding-left: 54px!important;
}

/*___ Margins ______________________________________________________________________*/

#script-info,

.discussion-list,
.script-list,
.sidebar,
.sidebarred-main-content,
.text-content,
.user-list
{
	margin: 12px!important;
}

selector
{
	margin: 3px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

#main-header,

.sidebar
{
	padding: 12px!important;
}

.rating-icon-bad,
.rating-icon-ok,
.rating-icon-good,
.external-login-container,

[type="submit"]
{
    padding: 12px 8px;
}

/*___ Heights ______________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

#script-meta,

.sidebarred-main-content,
.width-constraint
{
	max-width: none!important;
}

.external-login
{
	width: auto!important;
}

.script-list
{
	width: 97%!important;
}

.inline-script-stats dd, .inline-script-stats dt {
    width: 25%;
}

form.new_user {
    width: 21%!important;
}

.sidebar {
    width: 20%!important;
}

.external-login-container {
    width: 12rem;
}

}

