@-moz-document domain("greasyfork.org"), domain("sleazyfork.org") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.external-login-container .google_oauth2-login
{
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.discussion-list,
.script-list,
.text-content,
.user-content,
.user-list
{
	background: #060505!important;
	background-color: #060505!important;
}

selector
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

code,
pre,
li.L1, li.L3, li.L5, li.L7, li.L9,
.list-option-button,
.pagination>*,
.script-list+.pagination>*,
.user-list+.pagination>*,

form.external-login-form
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

[type="submit"]
{	
	background: radial-gradient(circle, #0f79af 0%, #031017 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

[type="submit"]::before
{
	background: radial-gradient(circle, #031017 0%, #0f79af 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

[type="submit"]:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
{	
	background: radial-gradient(circle, #380002 0%, #870101 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

selector::before
{
	background: radial-gradient(circle, #870101 0%, #380002 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

selector:hover::before {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

selector
{	
	background: radial-gradient(circle, #651c03 0%, #e1410b 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

selector::before
{
	background: radial-gradient(circle, #e1410b 0%, #651c03 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

selector:hover::before {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.install-help-link,
.install-link
{	
	background: radial-gradient(circle, #032802 0%, #299607 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.install-help-link::before,
.install-link::before
{
	background: radial-gradient(circle, #3de10b 0%, #066503 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.install-help-link:hover::before,
.install-link:hover::before {
  opacity: 1!important;
}

/*___ GitLab ________________________________________________________________________*/

.external-login-container:nth-child(2)
{
	background-image: linear-gradient(to bottom right, #626161, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
}

.external-login-container:nth-child(2):hover
{
	background-image: linear-gradient(to bottom right, #737272, #000)!important;
	color: #ff0!important;
}

.external-login-container:nth-child(2):active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
}

/*___ GitHub ________________________________________________________________________*/

.external-login-container:nth-child(3)
{
	background-image: linear-gradient(to bottom right, #626161, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
}

.external-login-container:nth-child(3):hover
{
	background-image: linear-gradient(to bottom right, #737272, #000)!important;
	color: #ff0!important;
}

.external-login-container:nth-child(3):active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
}

/*___ Google ________________________________________________________________________*/

.external-login-container:first-child
{
	background-image: linear-gradient(to bottom right, #db4437, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
}

.external-login-container:first-child:hover
{
	background-image: linear-gradient(to bottom right, #ec5548, #000)!important;
	color: #ff0!important;
}

.external-login-container:first-child:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#script-info,

.discussion-list,
.script-list,
.sidebar,
.text-content,
.user-list
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.list-option-group ul,

form.new_user
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#script-info:hover,

.discussion-list:hover,
.list-option-group ul:hover,
.script-list:hover,
.sidebar:hover,
.text-content:hover,
.user-list:hover,

form.new_user:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.alert
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

.post-install
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

/* Green Gradient 
_____________________________________________________________________________________________________*/

.notice
{
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
}

/* Yellow Gradient 
_____________________________________________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
}

/* Orange Gradient 
_____________________________________________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

.post-install
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.list-option-group .list-current,
.list-option a:hover,
tr:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.pagination>*:hover,
.script-list+.pagination>*:hover,
.user-list+.pagination>*:hover
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#0000 70%)!important;
}

.tabs .current
{
	background: radial-gradient(ellipse at center, #9a9d00b0 0%,#0e0e0e 70%)!important;
}

selector
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
}

.rating-icon-good
{
	background: radial-gradient(ellipse at center, #4da64d 0%,#0e0e0e00 70%)!important;
}

.rating-icon-ok
{
	background: radial-gradient(ellipse at center, #d3d34d 0%,#0e0e0e00 70%)!important;
}

.rating-icon-bad
{
	background: radial-gradient(ellipse at center, #ff4d4d 0%,#0e0e0e00 70%)!important;
}



}

