style.css:
@-moz-document url-prefix("https://www.novilar.com/") {
/* Whitespace reduction */
main {
    padding: 0px !important;
    line-height: 1.32 !important
}
.body-main-content {
    min-height: auto
}
.body-content {
    position: relative;
    top: -3px
}
.body-main-content .main-h2 {
    margin: 0
}
div.col-md-3:nth-child(1) {
    max-width: 25%;
    min-width: 280px;
    width: auto
}

/* Something smaller than it needs to be? Re-enable this
div.col-md-9:nth-child(2){width:auto min-width:80%; max-width: 100%}*/
.col-md-4 {
    width: 28%
}
.col-md-8 {
    width: 72%
}
.user-avatar-box {
    padding: unset !important;
    padding-left: 130px !important;
    width: 280px;
    height: 60px;
    box-shadow: none;
    -webkit-box-shadow: none
}
.user-hud {
    padding: unset !important;
    z-index: 10
}
.user-bar .d-caret {
    display: none
}
.user-avatar-box .dropdown {
    height: 6px;
    line-height: 1.0
}
.user-bar {
    padding: 0px
}
.navbar-form {
    margin-top: 3px;
    margin-bottom: 3px
}
.main-top a.logo {
    margin-top: 0;
    height: 50px;
    width: 300px
}
.info-bar {
    padding: 0.3rem 1rem;
    z-index: 3;
    height: 30px
}
.info-bar ul.list-inline {
    position: relative;
    top: -6px
}
.side-nav-info {
    margin-bottom: 0px;
    box-shadow: none;
    -webkit-box-shadow: none;
    border: none
}
.side-nav-info-header {
    border-radius: 30px 30px 0px 0px
}
.logo > img:nth-child(1) {
    position: relative;
    top: -15px
}
.panel-body {
    padding: 9px
}

/* Forums */
.profilelink {
    position: relative;
    top: -12px
}
.forum-avatar {
    margin: 0px;
    position: relative;
    top: -9px
}
.miniprofile-details {
    line-height: inherit;
    position: relative;
    top: -9px
}
.thread-miniprofile {
    padding: 0px
}
.sidebar-view {
    padding: 0.3rem 1rem
}
h1,
.h1,
h2,
.h2,
h3,
.h3 {
    margin-top: 0px;
    margin-bottom: 0px
}
h4,
.h4,
h5,
.h5,
h6,
.h6 {
    margin-top: 0px;
    margin-bottom: 0px
}
.content-inner {
    padding: 0 0.3rem 0.3rem
}
hr {
    height: 3px;
    margin-top: 3px
}
.btn-group {
    margin-bottom: 0px
}
.what-to-do {
    margin-bottom: 0.3rem
}
.main-navigation h4 {
    margin-top: 0.3rem
}
.main-navigation ul li a {
    font-size: 12px !important
}
.alert {
    margin-bottom: 3px;
    padding: 3px
}
.forum-container {
    min-height: 90px;
    margin: 0.5rem 0 0.5rem 0;
    width: 33%
}
.forum-container p {
    font-size: 1.3rem;
    margin: 0 0 3px
}

/*Removes lab art */
img[src="/img/lab/lab.png"],
#labmap,
.labtool,
#shops {
    display: none
}
/*Adventurelog*/
.alert-event {
    width: 100%
}
.inblock {
    min-height: auto;
    margin-bottom: 0
}
#world-map {
    height: 480px;
    width: 750px
}
.half2 {
    width: 33%
}
.form-group {
    margin-bottom: 3px
}
.timeline-centered .timeline-entry .timeline-entry-inner .timeline-label[data-v-4d1a5f6f] {
    padding: 0
}
.timeline-centered .timeline-entry[data-v-4d1a5f6f] {
    padding: 0.3rem
}
.timeline-centered[data-v-4d1a5f6f] {
    margin-bottom: 0px
}

/* Inventory */
.item-block {
    padding: 1px;
    border: none;
    width: auto
}
.actionbar.form-inline {
    position: relative !important
}
.highlighted {
    background-color: #005baa
}
div [style="margin: 2rem 0px; padding-bottom: 1rem; border-bottom: 1px solid rgb(51, 51, 51);"] {
    margin: 0rem 0px !important
}
.panel {
    margin-bottom: 0px !important
}
.panel-heading {
    padding: 6px 15px
}

/* Profile */
.userProfile {
    background-size: auto !important;
    min-height: 500px
}
.userProfile .userAttribute .userLinks {
    margin-top: 0px;
    text-align: right
}
.userProfile .userAttribute .userLinks ul li {
    width: 50%
}
.userProfile .userAttribute dd {
    margin-left: 200px
}
.userProfile .userAttribute {
    margin-left: 33%
}
.fa-3x {
    font-size: 2em
}

/* Classified */
div[class="left alert-event forty"] {
    width: 40%
}
}

@-moz-document url-prefix("https://www.novilar.com/achievements") {
.achievement {
    padding: 0.3rem 0.6rem !important
}

/*This makes the star bigger, if you want that
    img[class="animated pulse infinite"]{width:90px}*/
img[class="animated pulse infinite"] {
    animation-name: none
}
.col-md-12 .col-md-6 {
    width: 33.3%;
    min-width: 300px;
    padding-left: 9px;
    padding-right: 9px
}
.achievement .row .col-md-3,
.achievement .row .col-md-9 {
    padding-left: 3px !important;
    padding-right: 3px !important
}

/* Achievement hotfix */
div.col-md-3:nth-child(1) {
    min-width: 20% !important
}
div.col-xs-12:nth-child(1) {
    max-width: 25% !important;
    min-width: 280px !important;
    width: auto
}
}

@-moz-document url-prefix("https://www.novilar.com/territory/") {
/* Territory - slightly smaller pets 
img[src*="/img/assets/pets"]{width:200px} */
}

@-moz-document url-prefix("https://www.novilar.com/mypets") {
/* Mypets */
.total-body .body-content .col-md-9 {
    width: auto !important
}
.panel-body {
    padding-bottom: 0px !important
}
#\34 7715 {
    margin-bottom: 0px !important
}

/* Extra compact pets for viewing your own territory */
.row.pets .col-md-3 {
    max-width: 200px !important;
    padding: 5px !important
}
div[style="border: 1px solid rgb(204, 204, 204); border-radius: 5px; background: rgb(254, 254, 254) none repeat scroll 0% 0%;"] {
    min-height: 244px
}
.pet-header,
div[style="background: rgb(238, 238, 238) none repeat scroll 0% 0%; padding: 1rem;"] {
    padding: 0.6rem !important;
}
div[style="background: rgb(238, 238, 238) none repeat scroll 0% 0%; padding: 1rem;"] {
    position: absolute;
    bottom: 6px;
    width: 188px
}
div.col-md-3:nth-child(1) {
    min-width: unset !important
}
.btn-group-xs .btn-sm {
    padding: 1px 1px
}
}

@-moz-document url-prefix("https://www.novilar.com/breeding") {
form .row .col-md-6 {
    max-width: 400px;
    margin-left: auto
}
form .row .col-md-6 .panel .panel-body {
    padding: 0px
}
form .row .col-md-6 .panel .panel-body br {
    display: none !important
}
.panel-body {
    padding-bottom: 0px
}
}

@-moz-document url-prefix("https://www.novilar.com/recipebook") {
/*Recipe list*/
.body-main-content .main-h2 {
    margin: 0px;
    font-size: 14px
}
.pagination {
    margin: 3px;
    position: relative;
    top: -30px
}
.recipe-list {
    position: relative;
    top: -80px
}

/*.content-inner > div:nth-child(2){position: relative; top: -30px}*/
.body-main-content .main-h1 {
    font-size: 14px
}
.table > tbody > tr > td {
    padding: 3px
}
.item-display-block {
    height: 60px
}
}

@-moz-document url-prefix("https://www.novilar.com/lab") {
/*Alchemy page*/
.panel {
    margin-bottom: 0px
}
.panel-heading {
    padding: 6px 15px
}
.panel-body {
    padding: 3px
}
img[src="/img/lab/craftingtable_icon.png"],
img[src="/img/lab/mortarandpestle_icon.png"],
img[src="/img/lab/crucible_icon.png"],
img[src="/img/lab/centrifuge_icon.png"],
img[src="/img/lab/distiller_icon.png"],
img[src="/img/lab/extractor_icon.png"],
img[src="/img/lab/condenser_icon.png"],
img[src="/img/lab/dessicator_icon.png"],
img[src="/img/lab/furnace_icon.png"],
img[src="/img/lab/cutter_icon.png"] {
    height: 50px;
    width: 50px
}
td {
    padding: 3px
}
.content-inner {
    position: relative;
    top: -90
}
h1 {
    font-size: 14
}
.form-control {
    padding: 0px;
    height: 24px
}
}

@-moz-document url-prefix("https://www.novilar.com/shops/") {
/*Kills the image at the top of a shop*/
div[style='background: rgb(238, 238, 238) url("/img/shops/1.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/shops/2.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/shops/3.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/shops/4.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
