div[style='background: rgb(238, 238, 238) url("/img/shops/9.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/shops/10.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/shops/11.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/shops/12.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/shops/13.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/shops/14.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/9.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/3.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/1.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/2.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/4.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/5.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/6.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/7.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/8.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
div[style='background: rgb(238, 238, 238) url("/img/events/festivalstallnpc/10.png") no-repeat scroll 250px -20px; min-height: 350px; border-radius: 5px; margin: 0px 1rem 3rem;'],
#app > main > div > div > div.col-md-9.body-main-content > div > div.row {
    background: none !important;
    min-height: auto !important;
    margin: 0px !important;
    max-width: 600px
}

/* Allows items to fit more snugly */
.col-md-2 {
    width: auto;
    padding-left: 4px;
    padding-right: 4px;
    min-height: 180px
}
}

@-moz-document url-prefix("https://www.novilar.com/inventory") {
/* Makes the inventory appear higher on the page*/
.content-inner {
    position: relative;
    top: -60px
}
#pagination {
    position: relative;
    top: -30px
}
}

@-moz-document url("https://www.novilar.com/bank") {
/* No Lysander */
img[src="/img/shops/bank.png"] {
    display: none
}
}

@-moz-document url-prefix("https://www.novilar.com/forum/thread") {
.btn-sm {
    padding: 1px 10px
}
}

@-moz-document url-prefix("https://www.novilar.com/pet/") {
div[class="right swatches"] {
    float: left
}
div.center:nth-child(4) {
    position: relative;
    bottom: 30px
}
div.col-md-6:nth-child(1) > div:nth-child(1) > div:nth-child(2) {
    height: 260px
}
.col-md-6 {
    width: auto
}
tr td:nth-child(1),
tr td:nth-child(2) {
    background: #88411526
}
tr td:nth-child(3),
tr td:nth-child(4) {
    background: #17881526
}
div.col-md-6:nth-child(2) > div:nth-child(1) > div:nth-child(2) > div:nth-child(2) {
    height: auto !important
}
.col-md-4 {
    min-width: 150px;
    max-width: 200px;
    width: auto;
    padding-left: 6px;
    padding-right: 6px
}
.col-md-4 .panel {
    height: auto;
    min-height: 100px
}
.col-md-4 .panel .panel-body {
    padding-top: 3px;
    padding-bottom: 3px;
    min-height: 100px !important
}
div.col-md-6:nth-child(1) > div:nth-child(1) > div:nth-child(2) > br:nth-child(2) {
    display: none
}
}

@-moz-document url-prefix("https://www.novilar.com/profile/") {
.userProfile .userAttribute dd {
    margin-left: 67%
}
}
