style.css:
@-moz-document domain("novilar.com") {
body {
    background-color: #272528;
    color: #bdbdbd;
    text-shadow: 0 0 3px #444
}
.body-content {
    background-color: #211e1e80;
    color: #bdbdbd;
    box-shadow: none;
    text-shadow: 0px 0px 3px #444, 0px 1px 3px #444, 1px 0px 3px #444, 0px 0px 1px #444
}
.body-main-content {
    background: none;
    color: #bdbdbd;
    text-shadow: 0px 0px 3px #444, 0px 1px 3px #444, 1px 0px 3px #444, 0px 0px 1px #444
}
.round-box {
    background: #616161
}
.alert-event {
    background-color: #bdcebd
}
.panel {
    background-color: #2e2e2e7a;
    box-shadow: none
}
.panel-footer {
    background-color: transparent
}
.btn-sm {
    background-color: #404e42
}
.glyphicon {
    color: #bdbdbd
}
.main-h1 {
    color: saddlebrown !important
}
.info-bar,
.panel .panel-heading {
    background-color: #6D3515
}
div[style="color: rgb(51, 51, 51);"] {
    color: #bdbdbd !important
}
.well,
.well-sm {
    background-color: #1e776c80 !important
}
.list-group-item {
    background-color: #4444
}
.text-muted {
    color: #b4b4b4
}
.table .table {
    background-color: transparent
}
.achievement {
    background-color: rgba(165, 145, 90, 0.50) !important
}
.achievement .el-progress__text {
    color: #b4b4b4
}
.cke_dialog_background_cover,
.cke_dialog_body,
.cke_dialog,
.cke_dialog_contents {
    background-color: #444 !important;
    color: #b4b4b4
}
.cke_reset_all,
.cke_reset_all * {
    color: #b4b4b4
}
.cke_dialog_title,
.cke_dialog_footer {
    background-color: #703e21ab
}
input.cke_dialog_ui_input_password,
input.cke_dialog_ui_input_text,
textarea.cke_dialog_ui_input_textarea,
select.cke_dialog_ui_input_select {
    background-color: #2e2e2e7a
}
.thread-wrapper {
    background-color: #2f3c3580
}
.thread-body {
    background-color: transparent
}
.btn-group-justified > .btn,
.btn-group-justified > .btn-group {
    background-color: #2f3c3580
}
.profilelink {
    background-color: #2f3c3580
}
.forum-avatar img {
    background-color: transparent;
    border-radius: 30px
}
.thread-miniprofile {
    background-color: #3b3b3b80
}
.content-inner,
.forum-container {
    background-color: #2e2e2e7a
}
.forum-container p {
    color: #9ec3aa
}
.input-group .form-control:first-child,
.input-group-addon:first-child,
.input-group-btn:first-child > .btn,
.input-group-btn:first-child > .btn-group > .btn,
.input-group-btn:first-child > .dropdown-toggle,
.input-group-btn:last-child > .btn:not(:last-child):not(.dropdown-toggle),
.input-group-btn:last-child > .btn-group:not(:last-child) > .btn {
    background-color: #404e42cc
}
.form-control {
    color: #b3afaf;
    background-color: #4d4d4d
}
input,
button,
select,
textarea {
    background-color: #383838
}
#breed,
#reload {
    background-color: #703E21
}
a,
a *,
.total-body a,
.btn-link {
    color: sandybrown !important
}
a:hover,
a:hover *,
.link:hover,
.link:hover * {
    color: #a5e9e9 !important;
    text-shadow: 0 0 2px rgba(105, 105, 50, 0.1) !important
}
.pagination > li > a,
.pagination > li > span,
.pagination > li:first-child > a,
.pagination > li:first-child > span {
    background-color: #404e42
}
.pagination > .active > a,
.pagination > .active > a:hover,
.pagination > .active > a:focus,
.pagination > .active > span,
.pagination > .active > span:hover,
.pagination > .active > span:focus {
    border-color: #ddd
}
.tooltipster-default,
.tooltipster-content {
    background-color: #383838 !important;
    color: #eee !important
}
.item-tooltip-heading {
    background-color: #4d423a!important
}
.pagination > .disabled > span,
.pagination > .disabled > span:hover,
.pagination > .disabled > span:focus,
.pagination > .disabled > a,
.pagination > .disabled > a:hover,
.pagination > .disabled > a:focus {
    background-color: #404e42
}
.pagination > li > a:hover,
.pagination > li > a:focus,
.pagination > li > span:hover,
.pagination > li > span:focus {
    background-color: #0d4215
}
.user-avatar-box {
    background-color: rgba(73, 73, 73, .5)
}
.user-avatar-box a {
    color: #c9fc8b !important
}
.user-avatar-box a:hover {
    color: lime !important
}
.dropdown-menu {
    background-color: #404e42
}
.dropdown-menu > li > a:hover {
    background-color: #322d2a
}
.user-bar .d-caret {
    background-color: rgba(73, 73, 73, .5)
}

/* Spin to Win */
div.inline:nth-child(1) {
    background-color: #2e2e2eb8 !important
}
.swal2-popup {
    background-color: #444;
    font-size: 1.3rem
}
.swal2-popup .swal2-content {
    color: #bdbdbd;
    text-shadow: 0 0 3px #444
}
#game .canvas {
    opacity: 0.85
}
.messages li:hover {
    background-color: #404e4278
}
.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #31463480
}
.table-bordered {
    background-color: #494e4080
}
.table-hover > tbody > tr:hover {
    background-color: #404e4280
}
.sidebar-view {
    background-color: transparent;
    box-shadow: none;
    -webkit-box-shadow: none
}
.main-navigation ul li a {
    font-size: 14px;
    color: #f2d1d7 !important;
    text-shadow: 0 0 3px #444, 1px 0 0 #444, 0 1px 0 #444, 0 0 1px #444, 1px 0 1px #444, 1px 1px 0 #444, 0 1px 1px #444, 0px 0px 3px #444, 0px 1px 3px #444, 1px 0px 3px #444, 0px 0px 1px #444
}
.main-navigation ul li a:hover {
    color: #bddfdf !important
}
#navigation {
    background-color: #1d1f27;
    border: 3px double #9e5f2fb3;
    border-radius: 30px;
    background-image: url(https://cdn.discordapp.com/attachments/458103023869231116/491620275075678233/gator_transp.png);
    background-size: cover;
}
.side-nav-info .side-nav-info-body {
    background-color: #776a485c
}
.item-categories > li > a:hover {
    background-color: #97661f78
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus {
    background-color: #776a485c
}
.nav-tabs > li > a:hover {
    background-color: #97661f78
}
.main-navigation h4,
.side-nav-info .side-nav-info-header {
    background-color: #6d3515ad
}

/* Bootstrap stuff */
.btn-default {
    background-color: #8b811380;
    color: #bdbdbd
}
.btn-info,
.badge-info,
.card-info,
.bg-info,
.alert-info,
a.cke_dialog_ui_button_ok {
    background-color: #103a50;
    color: #519abf !important
}
.btn-success,
.badge-success,
.card-success,
.bg-success,
.alert-success {
    background-color: #315a20;
    color: #9fe7a0 !important
}
.btn-success:hover {
    background-color: #315a2080 !important
}
.alert-event {
    background-color: #59665980
}
.btn-warning,
.badge-warning,
.card-warning,
.bg-warning,
.alert-warning {
    background-color: #624d29;
    color: #fcf8e3 !important
}
.btn-danger,
.badge-danger,
.card-danger,
.bg-danger,
.alert-danger,
a.cke_dialog_ui_button_cancel {
    background-color: #78302e;
    color: #f2dede !important
}
.modal-content {
    background-color: #293f37
}
.actionbar.form-inline {
    background: #293f37bd !important
}
#action {
    color: white
}
.cke_top,
.cke_bottom {
    background-color: #703E21
}
.cke_chrome {
    background: grey;
    border: 1px solid grey
}
.cke_contents {
    border: 1px solid grey
}
.cke_toolgroup {
    background-color: #2f3c35c2
}
.cke_button:hover {
    background-color: #0e5310 !important
}
.cke_button__image_icon,
.cke_button__youtube_icon,
.cke_button__link_icon,
.cke_button__unlink_icon,
.cke_ltr .cke_button__anchor_icon,
.cke_button__justifyblock_icon,
.cke_button__justifyright_icon,
.cke_button__justifycenter_icon,
.cke_button__justifyleft_icon,
.cke_ltr .cke_button__indent_icon,
.cke_ltr .cke_button__outdent_icon,
.cke_ltr .cke_button__bulletedlist_icon,
.cke_ltr .cke_button__numberedlist_icon,
.cke_button__strike_icon,
.cke_button__underline_icon,
.cke_button__italic_icon,
.cke_button__bold_icon {
    filter: drop-shadow(1px 0px 0 #d1f5b04a) drop-shadow(-1px 0px 0 #d1f5b040) drop-shadow(0px 1px 0 #d1f5b045) drop-shadow(0px -1px 0 #d1f5b045) drop-shadow(1px 1px 0 #d1f5b03d) drop-shadow(-1px 1px 0 #d1f5b036) drop-shadow(1px -1px 0 #d1f5b038) drop-shadow(-1px -1px 0 #d1f5b03b)
