.cke_button_icon {
    opacity: 1 !important
}
blockquote {
    background-color: #6c4f3b
}
div [style="background: rgb(240, 240, 240) none repeat scroll 0% 0%; border-radius: 5px; padding: 1rem;"] {
    background-color: #6c4f3b !important
}
.faq-section label {
    background-color: #6c4f3b;
    text-shadow: inherit
}
.faq-section input[type="checkbox"]:checked ~ p {
    color: inherit
}
.box {
    background-color: #2e2e2e7a
}
div#calendar ul.dates li {
    background-color: #2e2e2e7a;
    color: #e7e2e2
}
.subtopic {
    background-color: #6c4f3b75
}
fieldset {
    background-color: #4e594080
}

/* Pets */
.pet-header {
    background-color: #404e42
}
div [class="row pets"] {
    background-color: #253b2f80
}
.swatch {
    border: 1px solid white
}
.panel-body {
    background: none !important
}

/* Explore */
#results {
    background-color: #2a2a2acc;
    border-radius: 18px
}
.option-dialogue span {
    background-color: #2a2a2acc
}
#encounter h1 {
    color: inherit
}
.exploration .exploration-header {
    text-shadow: none
}

