/* END FONTS */

body {
    color: #959595;
}

div#recentNews,
.widget #logged,
.widget #convivio {
    background: #272B2C;
}

#sidebar .widget,
#content .widget {
    border: none;
    background: linear-gradient(to right, #C1202F, #EF582B) !important;
    padding: 2px;
}

#sidebar h4,
#content h4 {
    background: linear-gradient(to right, #C1202F, #EF582B) !important;
}

.breadBoxTop .breadcrumb {
    background: none;
    border: none;
}

.over_header {
    background: none;
    height: 180px;
}

body > br {
    display: none;
}

#header {
    width: 980px;
    height: 180px;
}

#footer {
    display: none;
}

::selection {
    background-color: #844000;
}

.logo {
    width: 980px;
    height: 180px;
    margin: 16px 0px 0px 0px;
    border: none;
}

.mainContent p:nth-child(6),
.mainContent p:nth-child(7),
.mainContent p:nth-child(8),
.mainContent p:nth-child(9),
.mainContent p:nth-child(10),
.mainContent p:nth-child(11),
.mainContent div.widget:nth-child(1),
.mainContent div.widget:nth-child(2),
#recentThreadsMain {
    display: none;
}


/* Torrent Search Bar */
#search_torrent {
    margin-bottom: 50px;
}

#search_torrent input {
    margin: 0px 5px;
    outline: none;
}


select#cat_content_tiny {
    border: none;
    height: 27px;
    position: relative;
    bottom: 2px;
    color: #757575;
    background: #0f0f0f;
    border-radius: 0;
}

body,
html {
    background: #181818 !important;
}
body#tinymce {
    background-color: white !important;
}
#menu {
    background: #0f0f0f !important;
    border: none;
    width: 980px;
    display: flex;
    height: 35px;
}
#menu li:not(.menu_right) {
    flex:5;
    float: none;
    text-align:center;    
}
#menu li:nth-child(11),#menu li:nth-child(12) {
    display:none;
}
#menu li.menu_right {
    flex:1;
    float: none;
    text-align:center;    
}
#menu li a.link {
    font-size: 12px;
    text-transform: uppercase;
    font-weight:bold;
    letter-spacing: 1px;
    z-index: 1000;
    height: 100%;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;

}
#menu li:hover {
    background: #0f0f0f;
    border-color: #292929;
    border-radius: 0;
    -webkit-border-radius: 0;
}
#menu li:hover a.link {
    color: white;
    
}
#menu li:hover .dropdown_column {
    top: 24px;
}
#menu .dropdown_column {
    background: #0f0f0f;
    border-color: #292929;
    box-shadow: none;
    -webkit-box-shadow: none;
    border-radius: 0;
    -webkit-border-radius: 0;
}
#menu .column a:hover {
    text-shadow: none;
    color: #FEFCF9;
    background: #1C1A18;
}
#menu .column a {
    color: #FEFCF9;
}


/* Member Info Bar */
#member_info_bar .showStats {
    padding: 0;
    font-size: 11px;
    text-align: left;
}
#member_info_bar span.ratioGood {
    padding-right: 5px;
    margin: 2px;
}
#member_info_bar .showStats a,
#member_info_bar .showStats .clickable {
    margin: 2px;
    color: white;
    padding-right: 5px;
}
#member_info_bar {
    background: #0c0c0c;
    color: #FEFCF9;
    border: 0;
    padding: 5px 0px;
    width: 980px;
    margin-bottom: 10px;
    border-bottom: 1px solid #212121;
}
#cssmenu {
    line-height: 20px;
}
#cssmenu ul {
    background: linear-gradient(to right, #C1202F, #EF582B) !important;
    border: none;
    text-shadow: none;
    color: #FEFCF9;
    border-radius: 0;
    box-shadow: 0px 1px 5px rgb(0 0 0 / 16%);
    font-family: 'Source Sans Pro';
    font-weight:bold;
    letter-spacing: 1px;
}
#cssmenu ul li a {
    text-shadow: none;
    color: #FDFDFE;
    padding: 5px 20px 5px;
}
#cssmenu > ul li a:hover {
    background: #1F384B;
    color: #fff;
    text-shadow: none;
}
#cssmenu > ul li.has-sub ul {
    background: #161618;
    border: none;
}

#inner {
    box-shadow: none;
    -webkit-box-shadow: none;
    border: 1px solid #161617;
    background: transparent;
}
.widget {
    background: #161618 !important;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
    border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
}
.widget:before,
.widget:after {
    display: none !important;
}
.widget h4 {
    background: #1F384B !important;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
    color: #FEFCF9 !important;
    text-shadow: none !important;
    border-radius: 0 !important;
    -webkit-border-radius: 0 !important;
    text-transform: uppercase;
    font-weight: bold;
    letter-spacing: 1px;
}
.widget h4 a {
    color: #FEFCF9 !important;
}

#sidebar h4,
#content h4 {
    text-shadow: none;
    border-radius: 0;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    box-shadow: none;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    margin: 0 !important;
    border: none;
    font-family: 'Source Sans Pro'
}

h4::before {
    border-width: 0;
}

#torrents_table_classic {
    border-spacing: 0;
}
#torrents_table_classic tr {
    content-visibility: auto;
}
#torrents_table_classic td {
    background: #1C1A18;
}
#torrents_table_classic td.row2 {
    background: #161618;
}
#torrents_table_classic td a {
    color: white;
}

td.torrent_image span a {
    display: none;
}
td.torrent_image {
    width: auto !important;
    height: auto !important;
}
td.torrent_image .category_image {
    border: none !important;
    background: transparent !important;
    position: inherit !important;
    top: 10px;
    left: 0px;
    width: 48px !important;
    height: 48px !important;
}
td.torrent_image .category_image img {
    width: 32px !important;
    height: 32px !important;
}

.pagination li a {
    text-shadow: none;
    color: white;
    border: none;
    background: #1a262f;
}
.pagination li.active a,
.pagination li:hover a {
    border: none;
}

.paginationNoMySQL li a,
.paginationNoMySQL li.disabled {
    border: none;
    background: #1a262f;
    color: white !important;
    text-shadow: none;
    padding: 4px 8px;
}

.paginationNoMySQL li a:hover,
.paginationNoMySQL li.disabled:hover {
    border: none;
    background: #941D24;
}


.paginationNoMySQL li:first-child,
.paginationNoMySQL li.disabled:first-child {
    margin-left: 10px;
}

#torrents_table_classic th.seeders,
#torrents_table_classic th.leechers,
#torrents_table_classic th.completed {
    width: 4%;
}
#torrents_table_classic th.torrent_name {
    width: 75%;
}

img[src="http://tracker.btnext.com/styles/Invisible/torrents/torrent_s.png"] {
    display: none;
}

th.leechers img,
th.seeders img {
    width: 16px;
    height: 16px;
}

#Alfabe {
    display: none;
}

.information {
    padding: 4px 0px 4px 40px;
    border-radius: 0px;
    background-image: url("data:image/svg+xml;utf8;base64,");
    background-size: 16px;
    background-color: #7b1420;
    color: white;
    margin-bottom: 0;
}

.widget div {
    border: none !important;
}


.widget,
