/* Tabs for Torrent Details */
div#similarTorrents {
    flex: 1;
    margin: 10px;
}

div#similar_torrents {
    display: flex;
    justify-content: space-between;
}

ul.tabs {
    border-bottom: none;
}

ul.tabs a.current,
ul.tabs a.current:hover,
ul.tabs li.current a {
    background: #000000;
    cursor: default !important;
    color: #fff !important;
    border-bottom: none;
}

ul.tabs a {
    float: left;
    display: block;
    padding: 5px 15px;
    text-decoration: none;
    border-right: 1px solid #4a4a4a;
    border-top: none;
    border-bottom: none;
    height: 18px;
    background-color: #373737;
    color: #bbb;
    -moz-border-radius: 0;
}


/* Info boxes */
.error {
    border: 1px solid #92181c;
    color: #fff;
    font-size: 11px;
    background-color: #0f0f0f;
    border-radius: 0;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    -khtml-border-radius: none;
}


/* Torrent Info Page */
.torrent-box,
.box-hover {
    background: #0d0d0d;
    box-shadow: none;
    -webkit-box-shadow: none;
    border: 1px solid #474747;
    border-radius: 0;
}
.torrent-box .categoryImage {
    position: absolute;
    right: 0px;
    top: 0px;
}

.newIndicator {
    box-shadow: none;
    text-shadow: none;
    background: none;
    border: 0;
}
.newIndicator a {
    color: #ffffff;
    text-shadow: none;
    text-transform: uppercase;
    font-size: 15px;
}
.newIndicator a:hover {
    color: #92181c;
    text-decoration: none;
    text-transform: uppercase;
}
.newIndicator span {
    display: none;
}
.whiteBox {
    background: #181818;
    border: none;
    margin: 10px 0px;
}

.box-hover {
    background: transparent;
}


/* Series Gallery */
.imdb-small-icon {
    height: 24px;
    position: relative;
    vertical-align: middle;
    top: -1px;
}
.grow {
    transition: all 0.2s ease;
}
.grow:hover input[type="image"] {
    transform: scale(1.05) !important;
    border: 1px solid black;
}

#request_form {
    border-radius: 0px;
    border: 0;
    background: #1f1f1f;
    color: white;
}

#request_form input {
    margin: 0px 10px;
}

#request {
    border-radius: 0;
    border: 0;
    background-color: #101010;
}

#request .votes {
    background-color: #1f1f1f;
    border-radius: 0;
}

.eLinks a {
    background: #901a22;
    padding: 5px 10px;
    color: white;
    text-decoration: none;
    text-transform: uppercase;
}

#menu li.nodrop:hover {
    border-radius: 0;
}

#menu li:hover {
    border: 0px;
    padding: 4px 10px 4px 10px;
}


#menu li:hover:before {
    content: '';
    /* background: red; */
    border-bottom: 3px solid;
    border-image-source: linear-gradient(45deg, rgb(193 32 47), rgb(239 88 43));
    border-image-slice: 1;
    width: 50%;
    margin: 0 auto;
    position: absolute;
    bottom: 0;
    left: 0;
    height: 100%;
    z-index: 100;
    margin-left: 25%;
}

